css:
@-moz-document url-prefix("https://www.youtube.com") {
/*Main stuff and guff*/

body {
    background: #e2e2e2;
}
body #footer-container {
    margin-top: -141px;
    background-color: #efefef;
    border-top: 1px solid #e2e2e2;
}
.watch-stage-mode #theater-background {
    background-color: #e2e2e2;
}
#yt-masthead-container {
    background: #efefef;
}
.yt-card,
.all-comments,
.all-comments a {
    background: #efefef;
}
.yt-ui-ellipsis {
    background-color: #efefef;
    display: block;
    line-height: 1.3em;
    overflow: hidden;
    position: relative;
    text-overflow: ellipsis;
    white-space: normal;
    word-wrap: break-word;
}
#guide-container {
    background: #efefef;
}
.yt-subscription-button-subscriber-count-branded-horizontal,
.yt-subscription-button-subscriber-count-unbranded-horizontal {
    color: #6f6360;
}
#watch7-sidebar-modules .watch-sidebar-head,
.autoplay-bar .checkbox-on-off,
.yt-default h1,
.yt-default h2,
.yt-default h3,
.yt-default h4,
.yt-default h5,
.yt-default h6,
h1.yt,
h2.yt,
h3.yt,
h4.yt,
h5.yt,
h6.yt,
.guide-item,
#watch-header .yt-user-info a {
    color: #6f6360;
}
.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
    background-color: #9b59b6;
}
.video-extras-sparkbar-likes {
    background: #9b59b6;
}
li.guide-section h3 a {
    color: #9b59b6;
}
.video-extras-sparkbars {
    height: 2.5px;
}
.video-extras-sparkbar-dislikes {
    height: 2.5px;
    color: #d6d6d6;
}
.comments .like-count {
    color: #9b59b6;
}
.comments .comment-header .user-name {
    color: #9b59b6
}
.comments .comment-replies-header .load-comments a,
.comments .comment-replies-header .hide-comments a {
    color: #9b59b6
}
#action-panel-details:hover a {
    color: #91c7a9;
}
a.yt-uix-button-epic-nav-item.partially-selected:hover,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading {
    height: 29px;
    line-height: 29px;
    border-bottom: 3px solid;
    border-color: #9b59b6;
    padding-bottom: 0;
    display: inline-block;
}
.yt-lockup-title a,
.yt-lockup:hover a,
.yt-lockup:hover .yt-lockup-meta a,
.yt-lockup:hover .yt-lockup-description a {
    color: #9b59b6;
    background: #efefef;
}
.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover,
.guide-item.guide-item-selected .yt-deemphasized-text,
.guide-item.guide-item-selected:hover .yt-deemphasized-text {
    background: #9b59b6;
    color: #fff;
}
a.yt-uix-button-epic-nav-item:hover,
a.yt-uix-button-epic-nav-item.selected,
a.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
    height: 29px;
    line-height: 29px;
    border-bottom: 3px solid;
    border-color: #9b59b6;
    padding-bottom: 0;
    display: inline-block;
}
.multirow-shelf .yt-uix-expander-head {
    position: relative;
    z-index: 1;
    margin-right: 10px;
    text-align: right;
    color: #9b59b6;
    font-size: 12px;
    font-weight: 500;
}
#masthead-appbar {
    position: relative;
    background-color: #efefef;
    border-bottom: 1px solid #e8e8e8;
    height: 40px;
    line-height: 40px;
}
.channel-header .secondary-header-contents {
    position: relative;
    padding: 0 15px;
    height: 38px;
    font-size: 0;
    background-color: #efefef;
    border-bottom: 1px solid #e2e2e2;
}
.yt-uix-button-subscribe-branded,
.yt-uix-button-subscribe-branded[disabled],
.yt-uix-button-subscribe-branded[disabled]:hover,
.yt-uix-button-subscribe-branded[disabled]:active,
.yt-uix-button-subscribe-branded[disabled]:focus {
    color: #fefefe;
    background-color: #9b59b6;
}
/* Youtube player seeker thing */

.html5-play-progress,
.ytp-play-progress {
    background: #9b59b6 !important;
}
.html5-scrubber-button:hover,
.ytp-scrubber-button:hover,
.ytp-swatch-background-color,
.ytp-swatch-background-color-secondary {
    background: #9b59b6 !important;
}
.ytp-volume-slider-handle:before {
    background: #9b59b6;
}
.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after,
.ytp-settings-button.ytp-3d-badge::after {
    background-color: #9b59b6 !important;
}
.ytp-swatch-color {
    color: #9b59b6 !important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: #9b59b6 !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
    fill: #9b59b6 !important;
}
}
