@charset "UTF-8";

@-moz-document domain(dealabs.com){

/*============================================*/
/*     DarkLabs - MonsieurCro - V20200123     */
/*============================================*/

/* MODS (merci à leipo159, nberdah & Trideal) */
/*============================================*/

/*
[[ mod ]] --> 1px (yes) / 9999px (no)
*/

@media screen and (min-width: /*[[mod]]*/px){

    /*Espacement entre prix barré et prix*/
    .text--lineThrough {
        padding: 1.57143em;
    }
    /*Légère réduction de la taille des images sur l'accueil*/
    .imgFrame-img,
    .ratioBox-child {
        max-width: 90%;
        max-height: 90%;
        margin: auto;
    }
    /*Réduction des espaces des box-deals de l'accueil*/
    .comments-list--insideBox .comments-item-inner,
    .page2-space--h-p, .space--h-3, .space--r-3 {
        padding-right: 1em;
    }
    .space--h-2, .space--r-2 {
        padding-right: 1.57143em;
    }
    .space--fromW3-t-3 {
        padding-top: 1em;
    }
    /*Réduction de la colonne de droite sur l'accueil*/
    .thread-list--type-list {
        max-width: 90%;
        margin: auto;
    }
    /*Décallage du post par apport a l'avatar*/
    .comments-avatarColumn {
        width: 6em;
        box-sizing: content-box;
    }
    /*Quote message*/
    .userHtml-quote {
        padding: .5em 1.57em;
        border: 1px dashed #CCC;
        cursor: default;
        border-radius: 30px;
    }
    /*Grossisement nom user*/
    .user {
        font-weight: 700;
        font-size: larger;
        color: #35373B;
    }
    /*Espacement avatar/message dans les notifications*/
    .notification-item-image {
        width: 5.64286em;
    }
    /*Espacement avatar/message dans la messagerie (partie gauche)*/
    .space--h-1, .space--l-1 {
        padding-left: .28571em;
    }
    /*Encadré deal*/
    .threadGrid.space--t-2.space--b-2.space--h-2.space--fromW3-h-3.space--fromW3-t-3.space--fromW3-b-3 {
        position: relative;
        margin-bottom: 5px;
        border: 1px solid #D1D5DB;
    }
    /*Décalage du quote forum*/
    .userHtml-quote, .userHtml-video, .userHtml-videoDummy, .userHtml[data-animated-gif] {
        display: block;
        margin-top: .28571em;
        margin-bottom: .28571em;
        margin-left: 20px;
    }
    /*Prix barré et remise*/
    .mute--text.size--all-s {
        font-weight: normal;
        font-size: 10pt;
    }
    /*Marchand*/
    .cept-merchant-name {
        font-weight: bold;
        font-size: 10pt;
        font-variant: small-caps;
    }
    /*Description du deal*/
    .cept-description-container {
        color: #DDD;
        font-weight: normal;
        font-size: 10pt;
    }
    /*Barre de navigation - réduit légèrement la barre du haut*/
    .subNav-item {
        height: 3.1em;
    }
    .subNavMenu .subNavMenu-item {
        height: 3.1em; line-height: 3.1em;
    }
    /*Tuiles Deals*/
    .thread-list--type-list .js-threadList .thread--type-list .threadGrid-image .thread-listImgCell {
        width: 10em;
    }
    /*Header onglet "description"*/
    .profileHeader .profileHeader-content,
    .profileHeader > .page-content {
        padding-bottom: .6em;
        padding-top: .6em;
    }
    .grpAvatar {
        width: 4em; min-width: 4em;
    }
    /*Pagination*/
    .ico--type-left-blue:before {
        content: "\2B9C";
        background-image: none;
        color: /*[[blue]]*/;
    }
    .ico--type-right-grey:before {
        content: "\2B9E";
        background-image: none;
        color: #CCC;
    }

}

/*-----#COULEURS-----*/
/*============================================*/

/*
[[ blue ]] --> #1DA1F2
[[ bg ]] --> #141D26
[[ content ]] --> #1B2836
*/

/*Bouton "Back to top"*/
.btn--move-to-top.zIndex--above {
    display: block !important;
    opacity: 1 !important;
    font-size: .8em;
}
.btn--mode-white--dark,
.btn--mode-white--dark:focus,
.btn--mode-white--dark:hover {
    background-color: #121212;
    color: #EEE;
}
/*Bouton sur l'accueil*/
.ico.ico--type-up-brandPrimary.ico--pos-l.hide--toW3:active {
    background-color: /*[[content]]*/;
    color: #EEE !important;
    border: 1px solid #D1D5DB;
}
.ico.ico--type-up-brandPrimary.ico--pos-l.hide--toW3:hover {
    border: 1px solid /*[[blue]]*/;
}
.subNavMenu--menu .subNavMenu-roundCounter-text,
nav.comments-pagination.width--all-12.space--v-2.bg--color-greyPanel {
    background-color: /*[[blue]]*/;
}
.bg--off,
.nav,
.nav-banner,
.footer,
.bg--greyPanel,
.popover--mainNav .popover-content,
.popover--brandAccent .popover-content,
.vote-box,
.subNavMenu--menu .subNavMenu-list,
.space--v-3.space--h-3.border.border--color-brandPrimary.text--color-brandPrimary.bRad--a.bg--color-brandPrimaryPale,
.bg--color-greyPanel {
    background: /*[[bg]]*/;
}
.popover--layout-s > .popover-arrow::after {
    border-bottom-color: /*[[bg]]*/;
}
html,
.subNav,
.subNav--light,
.bg--inverted,
.listingProfile,
.card,
.text--overlay,
.bg--white,
.popover--brandAccent .popover-content,
.popover--brandPrimary .popover-content,
.popover--default .popover-content,
.popover--menu .popover-content,
.popover--red .popover-content,
.plainSelect-ctrl,
.softMessages-item,
.bg--color-white,
.listingProfile {
    background-color: /*[[content]]*/;
}
.listingProfile-footer.space--h-3,
svg.icon.icon--mail.text--color-white,
.space--l-2,
.icon.icon--bell,
.icon.icon--bell-off,
.page2-center.space--h-3.space--t-3.hide--toW3,
.tGrid-cell.tGrid-cell--shrink.vAlign--all-m.space--h-4.space--v-1.pagination-page.text--color-,
.conversation-content.iGrid-item.width--all-12.mute--text2.userHtml,
.lbox--v-4.lbox--f.boxAlign-jc--all-c.bRad--t-a.border.border--b-r {
    color: white;
}
.subNav-item {
    border-right: none;
}
/*Un peu plus de texte dans le caroussel*/
.tGrid-cell.space--r-2 {
    padding-right: 1em;
}
.bg--main,
.thread--type-card,
.thread--type-list,
.widget,
.popover--modal,
.popover-content,
.popover--menu,
.popover--menu .popover-content,
.popover--default .popover-content,
/*Pop-up de connexion et les onglets notifs, etc.*/
.popover--modal .popover-content,
.autoSuggest-suggestionsBox,
.threadWidget-header,
.threadWidget--type-linear,
/*Suggestions de recherche + onglet "les + hot" qd fenêtré*/
.threadWidget-options--carousel-previous-button {
    color: #EEE;
    background-color: /*[[content]]*/;
}
/*Titres des onglets de notifs*/
.navDropDown-head-text {
    opacity: 1;
    color: white;
}
/*Bouton "poster un deal"*/
.btn--mode-special {
    background: transparent;
    border: 1px solid #D1D5DB;
}
/*Retouches liées à l'affichage du prix*/
.overflow--wrap-off flex--auto-0 {
    font-size: 1.5em;
    font-weight: bold;
}
.thread--expired.thread--type-list .thread-price,
.userHtml a.linkPlain,
/*"Afficher plus"*/
.userHtml a.thread-link {
    color: /*[[bg]]*/ !important;
}
.userHtml a.linkPlain:hover,
.userHtml a.thread-link:hover,
.text--color-subNavActive,
.text--color-subNavActiveLight,
.text--color-subNavLayerActive,
.nav-link--selected,
.subNavMenu-link.subNavMenu-link--selected {
    color: /*[[blue]]*/ !important;
}
.thread--expired.thread--type-card,
.thread--expired.thread--type-list {
    opacity: .6;
}
.user,
.mute--text2,
.text--color-brandPrimary,
.subNavMenu-link:hover,
.subNavMenu-link:focus,
.subNavMenu-link:active,
.subNavMenu-link.subNavMenu-link--selected,
.lbox--v-2.text--color-blue,
.text--color-blue,
.link,
.linkPrimary,
.staticPageHtml.a,
.userHtml.a,
.linkBlue,
.thread-userOptionTeaser,
.userHtml a,
.text--color-navDropDownPrimary,
.thread-username,
.subNavMenu-item.subNavMenu-link.flex--align-c:hover,
.search,
.text--color-brandPrimary.text--b.btn--mini,
.icon.icon--bell.space--mr-2,
.text--color-charcoal {
    color: /*[[blue]]*/;
}
.subNavMenu--selected .subNavMenu-btn,
.subNavMenu--selected .subNavMenu-btn:focus,
.subNavMenu--selected .subNavMenu-btn:hover,
.subNavMenu--light.subNavMenu-btn:focus,
.subNavMenu--light.subNavMenu-btn:hover
.btn--mode-special:focus,
.btn--mode-special:hover,
button:focus .btn--mode-special,
button:hover .btn--mode-special,
.checkbox-input:checked ~ .tGrid-cell > .checkbox-box,
.toolbar-btn.toolbar-btn--link,
.checkbox--brand .checkbox-input:checked ~ .tGrid-cell > .checkbox-box,
.btn.btn--mini.btn--toW5-square,
.bg--color-brandPrimary {
    border-color: /*[[blue]]*/;
    background-color: /*[[blue]]*/;
}
.subNavMenu-btn:active,
.subNavMenu-btn:hover,
.border--color-blue,
.border--color-brandPrimary,
.multiSelect-input.tGrid-cell.tGrid-cell--preventOverflow.bRad--r-r.input.width--all-12.tree-select:hover {
    border-color: /*[[blue]]*/;
}
.comments--top-header,
.subNavMenu-link-badge,
.subNavMenu-link.subNavMenu-link--selected::after {
    background-color: /*[[blue]]*/ !important;
}
.comments-list .comments-item {
    border-bottom: none;
}
/*Survol des votes et des menus moins agressifs*/
.vote-btn:hover,
.navDropDown-link:hover,
.btn--mode-boxSec:focus,
.btn--mode-boxSec:hover,
.btn--mode-primary-inverted,
.navDropDown-link:focus,
.navDropDown-link:hover,
button:hover .btn--mode-boxSec,
.tabbedInterface-tab--primary:not(.tabbedInterface-tab--selected):hover {
    background: transparent !important;
    color: /*[[blue]]*/ !important;
    border-color: /*[[blue]]*/;
}
.btn--mode-primary, .btn.width--all-12 {
    background: /*[[blue]]*/;
    color: white;
    border: none;
}
.btn--mode-primary:hover {
    background: transparent;
    border: 1px solid #D1D5DB;
}
.thread-title,
.subNavMenu-btn,
p,
a {
    color: #EEE;
}
/*Header dans la consultation d'un deal*/
.voteBar {
    background-color: #222;
    border-bottom: 1px solid #D1D5DB;
}
.conversationList-msg,
.notification-item,
.conversationList-msgPreview,
.conversationList-senderLine {
    background-color: /*[[content]]*/ !important;
    color: #EEE !important;
}
.redactor-editor,
.comments-item--active,
.comments-item--in-moderation,
.comments-item-inner--edit,
.bg--em,
.tabbedInterface-tab--horizontal {
    background-color: transparent;
    color: #EEE;
}
/*On vide les boutons*/
.input,
.inputBox,
.toolbar,
.voucher-code,
.voucher-teaser,
.btn--mode-boxSec,
.checkbox-box,
.tag--deselected,
.btn:disabled,
.btn--disabled,
.subNavMenu-btn,
.imgFrame--squircle,
#notifications,
.navMenu-trigger,
.navMenu-trigger:focus,
.no-touch .autoSuggest-suggestion.selected,
.subNavMenu--light .subNavMenu-btn {
    background-color: transparent;
}
.speechBubble {
    background: black;
}
.speechBubble:after,
.speechBubble:before {
    color: black;
}
.popover--arrow-navDropDownPrimary .popover-arrow::after,
.checkbox-input:checked ~ .tGrid-cell > .checkbox-box {
    border-bottom-color: /*[[blue]]*/;
}
.popover--layout-e > .popover-arrow::after {
    border-right-color: #03A5C1;
}
/*Catégories des paramètres*/
.tabbedInterface-tab--selected {
    background-color: /*[[content]]*/ !important;
    color: #CCC;
}
.tabbedInterface-tab {
    background-color: /*[[bg]]*/;
    color: #CCC;
}
.userProfile-title--sub {
    color: #CCC;
}
.select-ctrl option {
    background: black;
}
/*Espace entre commentaires*/
.comments-item-inner,
.comments-list article:target .page2-space--h-p {
    border-top: 5px solid /*[[bg]]*/;
}
/*Espace plus grand après le descriptif du topic*/
nav.space--h-3.space--fromW3-h-4.space--v-2.bg--em.boxSec-div {
    border-bottom: 10px solid /*[[bg]]*/;
}
.textBadge--blue {
    background-color: transparent;
    border: .5px solid #D1D5DB;
}
.overflow--fade::after {
    background: transparent;
}
.badge {
    background-image: none !important;
}
.vote-box,
.threadGrid-title {
    box-shadow: none !important;
}
.text--overlay::before {
    background-image: -webkit-linear-gradient(left, hsla(0, 0%, 0%, 0), /*[[content]]*/ 90%);
    background-image: linear-gradient(90deg, hsla(0, 0%, 100%, 0), /*[[content]]*/ 90%);
}
.text--color-white {
    color: /*[[content]]*/;
}
.thread-title--list::after {
    background: none !important;
}
.comments-list--top .comments-item:target .comments-item-inner,
.comments-list .comments-list-item:target .comments-item-inner {
    animation: none !important;
    border-bottom: none !important;
}
.multiSelect-list {
    background-color: /*[[content]]*/;
    color: white;
}
.navDropDown-head,
.btn--mode-navDropDownPrimary,
.navDropDown-trigger--selected,
.comments-marker-down,
.comments-marker-up {
    background-color: /*[[blue]]*/ !important;
}
.comments-marker-down:after {
    border-top: 1em solid /*[[blue]]*/;
}
.navDropDown-footer {
    border: .8px solid /*[[blue]]*/;
}
.text--color-greyShade,
.cept-nav-subheadline.box--all-i.space--l-3.vAlign--all-m,
p.size--all-l.size--fromW3-xl.text--b,
.text--lh-1, .text--lh-1 span, .text--lh-1 a {
    color: #AAA;
}
.userHtml a.linkPlain, .userHtml a.thread-link {
    color: #AAA !important;
}
.notification-item {
    background-color: #121212 !important;
}
.notification-item--read {
    background-color: /*[[content]]*/ !important;
}
.tGrid-cell.mute--text.hAlign--all-r.vAlign--all-m.space--l-4,
.tGrid-cell.mute--text.hAlign--all-l.vAlign--all-m.space--r-4,
.cept-previous-page.pagination-previous.lbox--v-4,
.cept-next-page.pagination-next.lbox--v-4,
.cept-first-page.tGrid-cell.tGrid-cell--shrink.vAlign--all-m.space--r-1.pagination-page,
.tGrid-cell.tGrid-cell--shrink.vAlign--all-m.space--l-1.cept-last-page.pagination-page {
    color: white !important;
}
/*Rennes*/
.mc-bg--forest,
.mc-background--page {
    background-color: transparent !important;
}
.mc-festiveFont,
.mc-festiveFont--giant,
.mc-card-counter-child {
    color: white;
}
.mc-bg--forest .mc-content {
    background-color: /*[[content]]*/;
    border-radius: 10px;
}
.mc-card.mc-card--border.space--v-3.space--h-3.mc-card--trade.mc-card--shadow {
    background-color: #333;
}
input.mc-form-search.autoSuggest-input.input.input--icon-l.width--all-12.cept-trading-partner-user-search-suggestions {
    background-color: white;
}
a.btn.btn--mode-boxSec.mc-notification-shadow.space--h-4.width--all-12 {
    background-color: #333;
    color: #CCC;
}
a.btn.btn--mode-boxSec.mc-notification-shadow.space--h-4.width--all-12:hover {
    background-color: #555;
    color: #EEE;
}
.imgFrame-img {
    border-radius: 5px;
}

}
