css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("utkonos.ru") {

:root{  
/*  var(--red)     */  --red: #CC2700 ;       /* AE0C00 Ca2700  CC2700   CC0000 */
/*  var(--red-f)   */  --red-f: #F12000 ;       /*  FF0000    */
/*  var(--red-d)   */  --red-d: #A00000 ;    

/*  var(--or1)     */  --or1: #F06000  ;      /* F06000   D75600  */   
  
/*  var(--gold)    */  --gold: #FDB528 ;      /* FFCC66 FDB528 */  
/*  var(--Y)       */  --Y: #FFFF00 ;    
  
/*  var(--gr2)     */  --gr2: #00792C  ;      /* новый родной 00792C  */ 
/*  var(--gr1)     */  --gr1: #009900  ;      /* родной 009900  */   
/*  var(--gr-lt)   */  --gr-lt: #F7FBF5  ;      /* F7FBF5  */  
/*  var(--gr-lt2)  */  --gr-lt2: #D5FFCF ;  
/*  var(--gr-rod)  */  --gr-rod: #33935B  ;      /* 33935B  */  

/*  var(--blue1)   */  --blue1: #226CB8  ;    /* 226CB8  */   
  
/*  var(--lab1)    */  --lab1: #0DC2E2  ;   /*  0DC2E2   10C8E1  var(--or1)  */
  
/*  var(--dark1)    */  --dark1: #393939  ;  /*  393939  454545 */
}  
  
  
.ToolTip.fade-in   /*  Рейтинг товара на основе голосов от 19 человек */ ,
[class="OffsetWrap fade-in"]  /* Посмотрите наш специальный раздел с товарами для здоровья: */,
body > .Tooltip-wrapper  div[class="Tooltip right"]  /* Прямо сейчас x  человек смотрит  этот товар */  
{
    display: none !important;
}  
  
div.fixpanel_basket_content * , 
.foreign_font ,
.price.order_sum.has_discount,
span.discounted , 
[class="price delivery_sum "] ,
span.value ,
div[class*="order_status"]  ,
div[class*="state-payment"] ,
div[class="order_status_state order_status_state-interval"]  ,
.cost span  , 
/* Ваша экономия:  */
div[class*="order_check_info"] [class*="price"] *  ,
.omega H3[class*="price"] SPAN  ,  
[class="price discount"] > SPAN  ,
div.total_sum_container H3 SPAN
{
  font-family: "Utkonos"  !important;  
}

*:not([class="fixpanel_upper"]):not([class*="foreign_font"]):not([class*="sum"]):not([class="color_action"]):not([class*="price"]):not([class*="balance"]):not([class*="rub"]):not([class*="discounted"]):not([class*="value"]):not([class*="state-payment"]):not([class*="cost"]):not([class*="price discount"]):not([class^="module_floating_header-tabs_item"]):not([class^="module_buyer_nav-item module_buyer_nav"])    {
  font-family:   Helvetica , Arial !important;         
}  
  
  
  
/* верхняя зеленая полоса */  
.module_floating_header-contact  {
  background-color: var(--gr2)   !important;
}
/* Условия доставки и оплаты и пр. */  
.module_floating_header-contact > div > div > a {
  text-decoration:  none !important;  
}    
.module_floating_header-contact > div > div > a:hover  {
  color: var(--Y)  !important;
}  
/* Выбрать время */  
.module_floating_header-contact a.module_floating_header-step:hover {
  color: var(--Y)   !important;
  border-color: var(--Y)  !important;
}
  
  
  
/* Гипермаркет Красота и Здоровье Детство Зоомагазин Оптом Сервис   */  
.module_floating_header-shop  {
   height: 68px !important;
}  
/*   лого */
.module_floating_header-shop .module_floating_header-shop_logo a {
  margin-top: -7px !important;
  transition: all 500ms ease  !important;  
}  
.module_floating_header-shop .module_floating_header-shop_logo a:hover {
  transform:  scale(1.10)  ; 
  transition: all 450ms ease  !important;
}    
/* сами линки Гипермаркет Красота и Здоровье Детство Зоомагазин Оптом Сервис   */   
.module_floating_header .module_floating_header-shop_tabs > a {
     font-size: 17px;
     letter-spacing: 0px   !important; 
     font-weight: bold !important;
     border-width: 0px  !important;
/*   border: 6px solid transparent !important;  */
}
.module_floating_header .module_floating_header-shop_tabs > a:hover {
   cursor: pointer  !important;  
   color: var(--Y) !important; 
}  
.module_floating_header .module_floating_header-shop_tabs > a:not([class*="active"]):hover {
/*    color: var(--red)  !important; 
   border-bottom: 4px  var(--red)   solid !important;   */ 
   background-color: var(--red) !important;
   background-image: -moz-linear-gradient(to bottom, 
                  var(--or1)  /* #31C9FC */ , var(--dark1) 98%  ) !important;    
}  
/* active */
.module_floating_header .module_floating_header-shop_tabs > a[class*="active"] {
   text-decoration:  none !important; 
/*    color: var(--gr1)  !important; 
   border-bottom: 4px  var(--gr1)  solid !important;   */ 
   color: #FFF ; 
   background-color: var(--gr2)   !important;
   background-image: -moz-linear-gradient(to bottom, 
                      var(--gr2) , var(--dark1) 98%  ) !important;  
}  
  
  
  
  
  
  
/* черный header */
.module_floating_header-nav  {
  background-color: var(--dark1)   !important;
}
/*   левая иконка */
[class="module_floating_header-equal_float module_floating_header-equal_logo"]:hover  {
  transform:  scale(1.30)  ; 
  transition: all 150ms ease  !important;
}  
/* Каталог товаров */  
[class^="module_floating_header-wrap"]  a[class="module_floating_header-equal_link module_floating_header-equal_link-arrow module_floating_header-megamenu_control"]:hover ,  
[class^="module_floating_header-wrap"]  [class^="module_floating_header-equal_float module_floating_header-equal_cat"]:hover a ,
[class^="module_floating_header-wrap"]  a[class="module_floating_header-equal_link module_floating_header-equal_link-arrow module_floating_header-megamenu_control active"] ,
/*   сумма + ниже Выгода */  
[class^="module_floating_header-wrap"]  .module_basket_view:hover > a   
{
  color: var(--Y) !important;
  text-decoration:  none !important; 
}
/* кружочки */  
[class^="module_floating_header-wrap"]  a[class^="module_floating_header-equal_icon"]:hover {
  transform:  scale(1.30)  ; 
  transition: all 150ms ease  !important;
}
/* вып. меню под кружочками */   
 [class^="module_floating_header-wrap"] .module_floating_header-tooltip  {
  z-index: 666 !important;
} 
  
/* поле поиска */  
[class^="module_search_field_white-wrap"]   {
  background-color: #f6f6f6  !important;
}
[class^="module_search_field_white-wrap"] > INPUT  {
  color: var(--gr1) !important;
  font-weight: bold !important;
  font-size: 19px !important; 
  padding-left: 0px !important;
  margin-left: 10px !important;
}  
[class^="module_search_field_white-wrap"] > BUTTON  {
  margin-left: 0px !important;
  margin-right: -11px !important;
}    
[class^="module_search_field_white-wrap"] > BUTTON:hover  {
  transform:  scale(1.30)  ; 
  transition: all 150ms ease  !important;
}  
  
/* вып. блок под строкой поиска   */  
.search_suggest  {
  z-index: 500 !important;  
  opacity: 1;  
  margin-top: 12px !important;
  margin-left: -2px !important;
}
.search_suggest .container .requests,
.search_suggest .container SECTION  {
  border: 1px  solid var(--gr1) !important;
  box-shadow:   none !important;  
  padding-top: 12px !important;
  padding-bottom: 12px !important;
}
.search_suggest .container SECTION:not([class="requests"])  {
  margin-top: -1px !important;
}
/*   зеленый блок */
.search_suggest .container SECTION.requests {
  background: var(--gr-lt)  !important;
}
.search_suggest .container SECTION.requests a  {
  text-decoration:  none  !important;
  color: var(--gr1)  !important;
  font-weight: bold ; 
  padding-left: 6px !important;
  padding-right: 6px !important;    
} 
.search_suggest .container SECTION.requests a > div {
  color: #000000  !important;
}  
.search_suggest .container .requests a:hover {
  background: #fff !important;  
  outline: 1px solid var(--gr1) !important; 
} 
.search_suggest .container .requests a:hover SPAN ,
.search_suggest .container SECTION  a:hover  {
  color: var(--red)   !important;
} 
/*   белый блок */  
.search_suggest .container SECTION:not([class="requests"])  {
  background: #fff !important;     /*  F4F9FD */
/*   border-top-color: transparent !important; */
}  
.search_suggest .container SECTION:not([class="requests"]) a {
  text-decoration:  none  !important;
  color: var(--blue1) !important;
  font-weight: bold ; 
}    
 .search_suggest .container SECTION:not([class="requests"]) a:hover {
  text-decoration:   underline  !important;
  color: var(--red) !important;
}   
   
  
/*   цена товара */
div[class*="goods_price"]  {
  font-weight: bold  !important;
}    
  
  
/*   сумма + ниже Выгода */
[class^="module_floating_header-wrap"]  .module_basket_view    {
  top: 1px !important;
/*    background-color: #677  !important;  */
}  
[class^="module_floating_header-wrap"]  .module_basket_view ,  
[class^="module_floating_header-wrap"]  .module_basket_view > * {
  min-width:  120px   !important;  
  width:      120px   !important;
}    
[class^="module_floating_header-wrap"]  .module_basket_view > a * {
  font-weight: bold !important; 
  font-size: 14px !important; 
}    
#basket_widget-main /*   сумма корзины */ {
  font-size: 17px !important; 
}     
  
/*  открывашка */  
[class^="module_floating_header-wrap"] .module_floating_header-equal_float.module_floating_header-equal_basket.active > a[class="module_floating_header-equal_link module_floating_header-equal_link-arrow"] {
/* background-color: #aaa  !important;
 left: 20px !important; 
  transform-origin:  center !important;/
   height: 12px !important; 
  padding: 6px 9px 6px 0px !important;
  margin:  0px -0px 0px 14px !important; */
}    
[class^="module_floating_header-wrap"]  a[class="module_floating_header-equal_link module_floating_header-equal_link-arrow"]:hover {
/*   transform:  scale(1.60)  ;
  color: #FFF4CC  !important;  */
}  
/*  вып.список - корзина */  
.module_floating_header-tooltip.module_floating_header-cart   {
    min-width:  252px   !important;
/*   margin-left: 10px !important; */
}    
/*   список */
.module_floating_header-tooltip.module_floating_header-cart .module_basket_floating-goods  {
    min-height: 300px !important;
}     
/*   рамка товара в вып.списке */
.module_basket_floating-goods > div {
  margin-left: 1px !important;
  margin-right: 3px !important;
  padding-left: 2px !important;
  padding-top: 2px !important;
  padding-bottom: 2px !important;
}   
.module_basket_floating-goods > div:hover  {
  outline:  1px solid var(--lab1) !important; 
  background-color:  #f9FFFF  !important;
}    
/*  вып. блок со списком товаров -   если кнопки + и -недоступны */   
.module_basket_floating-goods > div > FORM > BUTTON[disabled=""] > i {
  background-color: #C0C0C0 !important;
} 
  
  
  
  
/* список - Заказ от 6 шт. */  
.goods_view_box-min_sale ,  
/* страница товара - Заказ от 6 шт. */
.goods_item_control .goods_view_item-min_sale {
  color: var(--red)  !important;
  font-weight: bold !important;
/*   font-size: 1.1em !important;  */
/*   min-width: 180px !important; */
}
.goods_view_box-min_sale {
  margin-bottom: 6px !important;      
}  
.goods_view_item-limit_min ,  
.goods_view_small-min_sale {
  color: var(--red)  !important;
  font-weight: bold !important;
  font-size: 1.0em !important; 
} 
  
  
/*  "Макс 30 шт. в корзине" */
.goods_view_box-max_sale ,
.goods_view_small-max_sale  ,
/* на странице товара  */  
.goods_view_item-limit_max,   
.goods_view_item-max_sale  
{
    font-weight: bold !important;
    color: var(--red)  !important;
}  
    

  
  
/* Избранное / Прошлые покупки -
   ТОВАР ОТСУТСТВУЕТ или   СНЯТ С ПРОДАЖ */
div[class="goods_view goods_view_box-view goods_view_missing"] {
    box-shadow: 0px 0px 0px 2px  #c00  inset  , 
                0px 0px 0px 0px  #c00  !important;    
/*      background-color: #FFF4F4 !important;  */
}  
.goods_view_missing-default_pic > div[class^="goods_view_missing"] {
  top: -16px !important;
  padding: 1px !important;
  margin: 0px 0px 0px 28px !important;
  width:  140px   !important;
  height: 20px !important;
  line-height: 18px !important;
  background-color: #c00  !important;
  color: #fff  !important;
}
.goods_view_missing-default_pic * {
  opacity: 1 !important;
}  
  

  
  
  
/* на странице товара  - 1 шт. в корзине */    
.goods_view_item-in_basket {
    font-weight: bold !important;
    font-size: 1.25em !important;   
}    
  
  
  
/* Выберите вес: */  
[class="goods_variants_property-content goods_variants_property-lot"] .goods_variants_property-item {
    outline: 1px solid   !important; 
    box-shadow:   none !important;  
    margin: 0px -1px 0px -1px !important;
    min-width:  57px   !important;  
    padding-left: 0px !important;
    padding-right: 3px !important;  
}  
/* Выберите размер: */  
[class="goods_variants_property-content"]  {
/*   position:  relative !important; */
/*    margin-left: 20px  !important;  */
/*     padding-left: 0px !important;
    padding-right: 0px !important;  */ 
}    
[class="goods_variants_property-content"] .goods_variants_property-item {
    outline: 1px solid   !important; 
    box-shadow:   none !important;  
    margin: 1px -1px 1px -1px !important; 
/*     min-width:  45px   !important;   */
    width:  47px   !important;  
/*     padding-left: 0px !important;
    padding-right: 3px !important;  */ 
}    
.goods_view_item-action .goods_variants_property-item {   /* на странице товара  */  
    outline: 1px solid   !important; 
    box-shadow:   none !important;  
    margin: 1px -1px 1px -1px !important;  
    min-width:  74px   !important;
    padding-left: 4px !important;
    padding-right: 6px !important;
}
/* тек. */
.goods_variants_property-item.active {
  background-color: var(--gr1) !important;  
  outline-color: var(--gr1) !important;  
  color:  #FFF  !important;
}  
/*  варианты   */ 
.goods_variants_property-item:not(.active) {
  background-color: #FFF ;
  outline-color: var(--gr1) !important;
  color:  #000  ;  
}    
/*  варианты hover   */ 
.goods_variants_property-item:not(.active):hover {
  background-color: var(--gr-lt2)  ;   /* var(--gr-lt)  var(--gold) */
}  
  
  
  
/* Продажа алкогольной продукции не осуществляется лицам в возрасте до 18 лет */  
[class="goods_view_item-side_control"] .goods_view_item-alcohol_message  ,
.catalogue-warnings .warning  {
  background-color: #Fdd  ;  
  border: 1px solid var(--red-f) !important; 
}  
  
  
/* Описание / Отзывы   */
.goods_view_item-side_data {
}  
/* Описание и характеристики / Отзывы   */
[id^="goods_view_item-tabs="] > div {
  padding-bottom: 30px;
}
/* отзывы покупателей */  
[class="module_comment_template comment_template_list"] ,
[class*="comment_template_list"] ,  
[class*="comment"][class*="list"] ,       
[class*="module_comment"] ,
[id*="comment_wrapper_"] ,  
[id*="comment_text_"]  {
  visibility:  visible !important;
  display:   block  !important;
} 
[id*="comment_text_"] {
  font-size: 1.2em !important; 
}  
/* читать полностью */  
[id*="comment_text_"] .action_read_full ,
/* Посмотреть еще отзывы */  
[class*="hide_control"] a[class="hide_control-more"]  {
   color: var(--blue1) !important;
   font-weight: bold !important;  
} 
[id*="comment_text_"] .action_read_full:hover {
  color: var(--red)   !important;
}   
  
  
  
  
  
/* лейбл размер скидки / Суперцена / Новинка /  Хит продаж  
Скидка 5% от 6шт, 10% от 10шт / 
Хит продаж  */ 
.goods_promoaction_icon {
    background-color:  var(--lab1)  !important;  /* 0DC2E2   10C8E1  var(--or1)  */
    font-weight: bold !important;  
    font-size: 16px  !important;  
    font-family: Arial  !important; 
}  
  
  
  
  
  
  
/* РАМКА ТОВАРА  */  
/*   большая рамка типа как в поиске */
[class="goods_view_box-view goods_view goods_view-item"]:hover  ,  
/* Информация о заказе */  
.goods_view.goods_view-item.goods_view_history_line:not([data-not_stock=""]):not([data-in_basket=""]) .goods_pic_form:hover , 
/* товары внутри заказа  */  
.history_order .goods_view_updatable > .goods_view:hover  , 
/*   список прошлых заказов */
.order_view_board > .order_view:hover  ,
/*   на стр. корзины */
#basket_all_goods > .goods_view_basket[data-type_error=""]:hover  ,
#basket_content .basket_goods_line:hover , 
/*   блок внизу страницы */
.module_slider_goods-content .goods_view_small > div:hover
{
    box-shadow: 0px 0px 0px 2px var(--lab1)  inset  , 
                0px 0px 0px 0px var(--lab1)  !important; 
}   
/* если Нет в наличии */  
[class="goods_view_basket error"]  ,
.goods_view_basket[data-type_error="empty_stock"] ,  
[class="goods_view_box-view goods_view goods_view-item"][data-not_stock=""]  ,
.goods_view.goods_view-item.goods_view_history_line[data-not_stock=""] .goods_pic_form ,
#basket_content .basket_goods_line.basket_goods_line-error:hover   
{
    box-shadow: 0px 0px 0px 2px #BB0000 inset  , 
                0px 0px 0px 0px var(--red)  !important; 
}   
[class="goods_view_basket error"]:hover ,    
.goods_view_basket[data-type_error="empty_stock"]:hover ,    
[class="goods_view_box-view goods_view goods_view-item"][data-not_stock=""]:hover ,  
.goods_view.goods_view-item.goods_view_history_line[data-not_stock=""] .goods_pic_form:hover      
{
    box-shadow: 0px 0px 0px 2px #FF280C inset  , 
                0px 0px 0px 0px var(--red-f)  !important; 
}   
/* если уже в корзине */  
[class="goods_view_box-view goods_view goods_view-item"][data-status_in_cart=""]  ,
[class="goods_view_box-view goods_view goods_view-item"][data-in_basket=""]  ,  
[class="goods_view_box-view goods_view goods_view-item in_basket"] ,   
[class^="goods_view goods_view-item goods_view_history_line"][class*="in_basket"] .goods_pic_form  ,
.goods_view.goods_view-item.goods_view_history_line[data-status_in_cart=""] .goods_pic_form ,  
.goods_view.goods_view-item.goods_view_history_line[data-in_basket=""] .goods_pic_form 
{
    box-shadow: 0px 0px 0px 2px var(--gr1)   inset  , 
                0px 0px 0px 0px var(--gr1)   !important; 
}   
[class="goods_view_box-view goods_view goods_view-item"][data-status_in_cart=""]:hover  ,
[class="goods_view_box-view goods_view goods_view-item"][data-in_basket=""]:hover  ,  
[class="goods_view_box-view goods_view goods_view-item in_basket"]:hover  ,
[class^="goods_view goods_view-item goods_view_history_line"][class*="in_basket"] .goods_pic_form:hover  ,
.goods_view.goods_view-item.goods_view_history_line[data-status_in_cart=""] .goods_pic_form:hover ,  
.goods_view.goods_view-item.goods_view_history_line[data-in_basket=""] .goods_pic_form:hover 
{
    box-shadow: 0px 0px 0px 2px #33CC00  inset  , 
                0px 0px 0px 0px #33CC00  !important; 
}     
  

 
  
  
/*  Поиск в прошлых покупках  */
.module_filter_last .search * {
  box-shadow:   none !important;    
  border: none !important;    
}  
.module_filter_last .search .el_form-search {
  border: 1px solid var(--or1) !important;
}  
.module_filter_last .search .el_form-search:hover ,
.module_filter_last .search .el_form-search:focus  {
  border: 1px solid var(--gr1) !important;
}    
/*   поле  */
.module_filter_last .search .el_form-search INPUT {
  font-weight: bold !important; 
  font-size: 16px !important; 
  color: var(--or1)    !important;  
  font-family: Arial  !important; 
}
.module_filter_last .search .el_form-search:hover INPUT ,
.module_filter_last .search .el_form-search:active INPUT ,  
.module_filter_last .search .el_form-search:focus INPUT  {
    color: var(--gr1)    !important; 
}   
/* кн.  */  
.module_filter_last .search .el_form-search BUTTON ,
.module_filter_last .search .el_form-search BUTTON i {
    background-color: transparent!important;
