.module_site_migration_return:nth-child(13)  */
div.module_scroll_top + div.module_site_migration_return    {
  display: none !important;
}    
.module_site_migration_return > a {
  color: #fff !important;
  font-weight: normal !important;
  font-size:   17px !important; 
  line-height: 17px !important;
  height:     50px !important;
  vertical-align:   middle !important;
  margin: 0px !important;
  padding: 16px 0 0 0 !important;
  border: 1px solid  var(--gr1)  !important;
  transition: all 750ms ease  !important;  
}      
/* .module_site_migration_return:nth-child(14) ,
.module_site_migration_return:nth-child(15)  */
div.module_site_migration_return ~ div.module_site_migration_return    {
  min-width:  250px   !important;
  position:      fixed !important;
  left: calc( 50% + 64px ) !important;
  margin-left: -620px !important;
  top: calc( 100% - 16px ) !important;  
  opacity: 0.3  !important;
  transition: all 750ms ease  !important;
}  
div.module_site_migration_return ~ div.module_site_migration_return:hover   
/* .module_site_migration_return:nth-child(14):hover  ,
.module_site_migration_return:nth-child(15):hover */  {
  top: calc( 100% - 50px ) !important;  
  opacity: 0.8  !important;
  transition: all 350ms ease  !important;
}     
