@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imageshack.us") {
   .table_decoration tr td[valign="top"]:first-child, #ymaddiv, img#headlogo, img[src*="rss_icon.jpg"], #bigfootaddiv, #yieldaddiv, #butan_wrap label {
      display: none !important;
   }
   #gaia_content div.subforums .hd, #gaia_content div.subforums .bd {
      padding: 10px 3px 3px 10px !important;
   }
   table[width="755"], table[width="770"], table[width="760"], table[width="755"] tr, table[width="760"] tr {
      width: 400px !important;
   }
   td[align="right"] table td[valign="top"] {
      width: 210px !important;
   }
   td[align="right"] table td[valign="top"] form {
      width: 180px !important;
   }
   .search {
      width: 128px !important;
   }
   img[src="http://imageshack.us/images/lb_left.gif"], img[src="http://imageshack.us/images/lb_right.gif"]  {
      display: none;
   }
   #loginbox td:first-child {
      width: 16px !important;
      background-position: top left !important;
      background-image: url(http://img441.imageshack.us/img441/762/lbleftdw6.gif) !important;
   }
   #loginbox td:last-child {
      width: 16px !important;
      background-position: top right !important;
      background-image: url(http://imageshack.us/images/lb_right.gif) !important;
   }

   
   #container {
      width: 390px !important;
   }
   input[name="user"], input[name="email1"], input[name="email2"] {
      width: 150px !important;
   }
   input[onClick='highlight(this);'], textarea[onClick="highlight(this)"], input[onClick="highlight(this)"] {
      width: 250px !important;
   }
   td[width="20"] + td {
      display: none !important;
   }
html > body > table > tbody > tr > td > table > tbody > tr > td > div > table > tbody > tr > td{
display:none;}

.m,html > body > table > tbody > tr > td > table > tbody > tr > td > img{display:none;
}




body
{background-color: #000000!important;
background-image: url("http://img235.imageshack.us/img235/2445/headerdecorationbgtb7.png");
	background-repeat: no-repeat;
	background-attachment: fixed;
background-position: 0cm 0cm;
}
html > body > table > tbody > tr > td > table > tbody > tr > td > center > a,html > body > table > tbody > tr > td > table > tbody > tr > td > form{display:none;}


html > body > table > tbody > tr > td > table > tbody > tr > td > table #loginbox > tbody > tr > td,html > body > table > tbody > tr > td > table > tbody > tr > td > table #loginbox > tbody > tr > td,html > body > table > tbody > tr > td > table > tbody > tr > td > table #loginbox > tbody > tr > td{display:none;}


table #loginbox > tbody > tr > td{display:none;}

div> a {
color: lime;

text-decoration: none;

}

