a[href*="goto=nextoldest"]     { border-radius:15px 0px 0px 15px !important; position:absolute !important; top:5px !important; right:143px !important; width:140px !important; height:22px !important; line-height:20px !important; border:1px solid black !important; background-color:#FFFFFF !important; display:inline-block !important; font-size:15px !important; font-weight:bold !important; overflow:hidden !important; text-decoration:none !important; }
