a[href*="goto=nextnewest"]:hover { background-color:#A0A0A0 !important; color:#FFFFFF !important; }
a[href*="goto=nextoldest#top"] { border-radius:9px !important; padding-right:20px !important; position:relative !important; top:47px !important; right:10px !important; width:50px !important; height:20px !important; display:inline-block !important; font-size:15px !important; }

img[alt="The phun.org forum - new world of entertainment! - Powered by vBulletin"] { height:40px !important; }
}
