css:
@font-face {
  font-family: 'Open Sans';
  src: local('Open Sans'), local('OpenSans'), /* in case the font is installed */
       url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
@-moz-document domain("trello.com") {
	body {
		background: #f9f9fb !important;
		font-family: "Open Sans", "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif !important;
		font-weight: 300 !important;
	}

	h3 {
		font-weight: 300 !important
	}

	.header-woof {
		display: none !important
	}

	#header {
		background: #1E2632 !important;
		padding: 10px !important;
	}

	.board-header-btn-name-org-logo {
		-webkit-filter: contrast(3000%) invert(100%);
	}

	.js-open-board {
		font-weight: 300 !important;
		border: 1px solid #eee;
	}

	.js-open-board:hover {
		border-color: #ddd;
	}

	.board-canvas {
		padding-top: 20px
	}

	.thumbnail {
		background: #979798 !important
	}

	.sidebar-section-header-title {
		text-decoration: none !important;
		font-weight: 300 !important
	}

	#search-text {
		border-radius: 0px !important
	}

	.board-header {
		margin-bottom: 20px !important;
		padding-bottom: 0px !important
	}


	/* header buttons */

	.header-btn.header-notifications,
	.header-btn.header-boards,
	.header-btn.header-search,
	.header-btn.header-login,
	.header-btn.header-signup {
		background: none !important;
	}

	.header-btn:hover {
		background: none !important;
	}

	.header-user {
		top: 10px !important;
		right: 5px !important;
		font-weight: 300 !important
	}

	.board-header {
		height: 40px !important;
		background: #1E2632 !important;
	}

	.header-btn-text {
		font-weight: 300 !important
	}

	.board-header-btn-text {
		text-decoration: none !important;
	}

	.board-header-btn-name {
		font-weight: 300 !important
	}

	.text {
		text-decoration: none !important;
	}

	.header-member {
		background: none !important;
	}

	.member {
		border-radius: 20px !important;
		border: none !important;
		background: none !important;
	}

	.member img {
		border-radius: 0 !important;
		border: none !important;
	}

	.list-wrapper.placeholder {
		background: #eee;
	}

	.list-wrapper:first-child {
		margin-left: 30px !important;
	}

	.list-area {
		margin-top: 30px !important;
	}

	.list {
		padding: 8px !important;
	}

	.list-header {
		margin: 5px 0px 10px 0px !important;
	}

	.list-header-name {
		color: #1E2632 !important;
		font-weight: 700 !important;
		text-transform: none;
	}

	.list {
		background: #fff !important;
		border: 1px solid #eee !important;
		border-top: 2px solid #1E2632 !important;
		border-radius: 0px;
	}

	.list-card {
		border: 1px solid #eee !important;
		padding: 5px !important;
	}

	.active-card {
		background: #fff !important;
		transition: border 0.2s ease-out !important;
		border: 1px solid #1E2632 !important;
	}

	.list-card-operation {
		background: none !important;
	}


	/* Add list field */

	.js-add-list {
		background: none !important;
		padding: 0px !important;
	}

	.edit-controls {
		margin-top: 8px !important;
	}

	.js-add-list:hover {
		color: #1E2632 !important;
	}

	.js-add-list span {
		color: #bbb !important;
	}

	.js-add-list span:hover {
		color: #1E2632 !important;
	}

	.list-name-input {
		background: #fff !important;
		border: 1px solid #eee !important;
		padding: 12px !important;
		box-shadow: none !important;
	}


	/* Add card field */

	.open-card-composer {
		color: #999 !important;
	}

	.open-card-composer:hover {
		text-decoration: none !important;
		background: none !important;
		color: #1E2632 !important;
	}

	.js-composer:hover {
		border: 1px solid #eee !important;
	}

	.window-overlay {
		/* blue background: rgba(30, 136, 229, 1) !important; */
		background: rgba(255, 255, 255, 0.8) !important;
	}

	strong {
		font-weight: 300 !important
	}

	.js-open-move-from-header {
		color: #1E2632 !important;
		font-weight: 300 !important
	}

	.window-title {
		margin-top: 20px;
	}

	.window-title-text {
		font-weight: 300 !important;
		color: #1E2632 !important;
	}

	.button-link {
		font-weight: 300;
	}

	.button-link:hover {
		background: #1E2632 !important;
		border-color: #1E2632 !important;
	}

	.attachment-add-link-submit {
		background: #1E2632 !important;
		box-shadow: none !important;
		font-weight: 700 !important;
		color: #fff !important;
	}

	/* Primary button */

	input.primary {
		background: #1E2632 !important;
		box-shadow: none !important;
		font-weight: 700 !important;
	}

	input.primary:hover {
		background: #1E2632 !important;
	}

	input.primary:disabled {
		background: none !important;
		border: 1px solid #eee;
		color: #ddd !important;
	}

	/* Cancel icon button */

	.js-cancel {
		margin-left: 4px;
	}

	.ui-state-active {
		background: #F45B5B !important
	}


	/* ------------------- */
	/* --- CARD DETAIL --- */
	/* ------------------- */

	.window {
		background-color: #1E2632 !important;
		box-shadow: 0 19px 38px rgba(0, 0, 0, 0.30), 0 15px 12px rgba(0, 0, 0, 0.22);
		border-radius: 0px;
	}

	.dialog-close-button {
		margin: 12px 12px 0px 0px !important;
	}

	.window-wrapper {
		margin-top: 4px;
		background: #fff !important;
	}

	.window-wrapper a {
		text-decoration: none !important
	}


	/* Window header */

	.window-header {
		margin-top: 0px;
	}

	.window-header-icon {
		display: none !important;
	}

	.window-title-text {
		font-weight: 700 !important;
	}

	.field {
		box-shadow: none !important;
	}


	/* hide 'in Liste bla' */

	.window-header-inline-content {
		display: none;
	}

	/* label */
	.card-label {
		text-shadow: 1px 1px 0px rgba(0,0,0,.2),0 0 0px #000;
	}


	/* card description */

	.card-detail-item-header-edit {
		display: none !important;
	}

	.card-detail-item-header {
		display: none !important;
	}

	.card-detail-item {
		margin-top: 12px;
		font-weight: 500;
	}


	/* separators */

	.card-detail-item {
		margin-bottom: 40px;
	}

	.window-module {
		margin-bottom: 40px;
	}


	/* section titles */

	.window-module-title-icon {
		display: none !important;
	}

	h3 {
		font-weight: 300 !important;
		color: #000;
	}

	.checklist-progress-bar-current {
		background: #1E2632 !important;
	}


	/* comments */

	.action-comment {
		box-shadow: none !important;
		border-left: 2px solid #1E2632;
		border-radius: 0px !important;
		margin-top: 12px;
		margin-bottom: 12px;
	}

	.phenom {
		border-bottom: 1px solid #eee;
	}

	.window-module-title {
		border-top: 2px solid #1E2632;
		padding: 14px 0px 18px 0px;
	}


	/* ------------------- */
	/* --- BOARD MENU --- */
	/* ------------------- */

	.board-menu {
		background: #fff !important;
	}

	/* ----------------------- */
	/* --- BOARDS SWITCHER --- */
	/* ----------------------- */

	.boards-drawer {
		box-shadow: 0 3px 6px rgba(0,0,0,0.16);
		padding: 0px 24px;
	}

	.boards-drawer input {
		margin: 6px 0px;
	}

	input.js-search-boards {
		border: none;
		border-bottom: 1px solid #ddd;
		background: none;
		border-radius: 0px;
		padding: 0px;
		margin-top: 12px;
		margin-bottom: 24px;
	}

	input.js-search-boards:hover {
		border-color: #1E2632;
	}

	input.js-search-boards:focus {
		border-color: #1E2632;
		box-shadow: none;
	}

	.js-board-drawer-buttons {
		margin: 36px 0px;
		margin-bottom: 12px;
		border-top: 2px solid #1E2632;
	}

	.sidebar-section-header-toggle-icon:hover {
		background: none;
	}

	.quiet-button:hover {
		background: none;
		color: #1E2632;
	}

	.boards-drawer .empty, .boards-drawer .quiet-button {
		margin: 12px 0px;
		padding: 0px;
	}

	.sidebar-section-header {
		margin: 1pc 30px 10px 0px;
		position: relative;
	}

	/* ----------------------- */
	/* --- BOARDS SWITCHER --- */
	/* ----------------------- */

	.js-add-board {
		color: #999 !important;
		background: #fff !important;
		border: 1px solid #eee !important;
	}

	.js-add-board:hover {
		transition: border 0.2s ease-out !important;
		border: 1px solid #1E2632 !important;
	}
}
