css:
@-moz-document domain("www.thegamesmachine.it") {

html {
    background: #bbb none no-repeat; 
}


.doc_header {
    background: #eee; 
}


#top_header {   
    background: #eee;
}
.toplinks ul.isuser li.welcomelink {
    color: #f80;
}



li.announcerow,                                   
.announcerow .announceinfo,
.announcerow .announcement {
    background: #222 !important; 
}
 

.body_wrapper {  
    background: #888;
}


.navtabs ul.floatcontainer {      
    border-top: solid 12px #eee;
    background-color: #555;
}


ul.floatcontainer {
    background: #eee;
    color: #f80;
}


.navtabs {
    background: #555 !important;
}




.navbar {
   background: #f80 none;    
}


.navtabs li.selected a.navtab { 
    color: #ffffff;
    background: #f80 none;
}


.navtabs li a.navtab {       
    background: #555 none;
    color: #fff;
}


.navtabs > li {
    border-left: 12px solid #eee; 
}


.toolsmenu div {
    background: #fff;
}



body {
    color: #222;
}

body a {
    color: #2173cb;
}
body a:hover,
body a:hover .time, 
body a:hover .shade, 
body a:hover .understate {
    color: #f80;
    text-decoration: underline;
}


body b {
    color: #000;
}


.pagination span a{
    color: #444;
}    

.pagination span.selected a {
    background: #f80 none;
    color: #444;
    border: 1px solid #f80;
}





.newcontent_textcontrol > span,
.pagetitleinfo.textcontrol > span { 
    background-color: #f80;
}
.newcontent_textcontrol {
    background: #333 none;
}


.column-left,
.column-right {
    background-color: #333;
}


.footer{
    border-bottom: 10px solid #f80;;
}




.below_body .time { 
    color: #fff;
}


.blockhead {
    color: #ffffff;
    background: #444 !important;
    border: 1px solid #444;
}

.blockbody {
    background: #999;
}


.wgo_block .section {
    background: #999;
    border: 0px;
}

.wgo_block .section div p {
    color: #fff !important;
}



.forumbit_nopost .forumhead .forumtitle,
.forumbit_nopost .forumhead span, 
.forumbit_nopost .forumhead .collapse, 
.forumbit_post .forumhead h2 span
.forumbit_nopost .forumhead span.forumlastpost,
.forumbit_post .forumhead span.forumlastpost,
.forumbit_nopost .forumhead h2, 
.forumbit_post .forumhead h2 {
    background-color: #333;
}


.forumbit_nopost .forumbit_nopost .forumrow,
.forumbit_post .forumrow {
    color: #444;
    background: #ccc none !important;
    border-bottom: 1px solid #777;
}


.forumbit_post .foruminfo .forumdata .forumtitle a, 
.forumbit_nopost .forumbit_nopost .forumrow .forumtitle a {
    color: #222;
}


.forumbit_post .forumdescription {
    color: #444;
}

a.threadtitle {
    color: #333;
}



.forumhead {
    background: #333 none;
}


.threadlisthead {
    background: #444 none;
}


.threadbit .sticky {
    background: #c7c7c7 none;
    border: 1px solid #ddd;
}


.threadbit .nonsticky {
    background: #bbb none;
    border: 1px solid #ddd;
}


.threadbit .title {
    color: #444;
}
.threadbit .threadtitle_unread {
    color: #333;
    font-weight: bold;
}


#pagetitle a {
    color: #fff;
}


.postbit, .postbitlegacy, .eventbit {
    width: 100%;
    border: none;
}


.postbit .posthead,
.postbitlegacy .posthead,
.eventbit .eventhead { 
    background: #444 none;
    color: #fff;
    
}

.moderatori {     
    color: #22c;
}
.admin {
    color: #a21111;
}


.postbitlegacy .postbody,            
.eventbit .eventdetails .eventbody {
    border-left: none;
    background: #999 none;
}
.postcontent {
    color: #242424;
    font-size: 14px;
}


.signature {
    border-top: 1px solid #777;   
}

.postbit .posttitle,            
.postbitlegacy .title {
    border-bottom: 1px solid #7c7c7c;
}


.postbitlegacy .postdetails, 
.postbitlegacy .userinfo,
.eventbit .eventdetails {
    background: #7c7c7c none;
}

.postbit .postfoot .textcontrols,
.postbitlegacy .postfoot .textcontrols,
.eventbit .eventfoot .eventcontrols { 
    background: #ddd none;
    border-top: none;
}


.shade {  
    color: #555;
}



.formcontrols .blockrow {
    background: #999 none repeat-x center bottom;
}
.cke_skin_kama .cke_wrapper.cke_ltr,
.cke_skin_kama .cke_wrapper.cke_rtl {
    background: #999;
}
body.forum {
    color: #222;
    background: #bbb none;
}


.formcontrols .blockrow .description {
    color: #383838;
}
}
