css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("caf.fr") {
  
  body, html {
    color: whitesmoke;
    background: black;
  }
  
  #theme-header,
  .section-caf #header,
  .section-public-2 #header {
    background-image: none;
  }
  
  #theme-header-bas-caf {
    display: none;
  }
  
  #theme-contenu-page #connexion .conteneur-titre #bienvenue span,
  #theme-header-navbar #links a,
  #theme-filariane a,
  #theme-filariane span,
  #theme-page-sidebar #menu li a,
  #theme-contenu-page .conteneur .conteneur-titre h2,
  #theme-contenu-page .conteneur .conteneur-contenu,
  ul#links li a,
  #primary .view-content .item-list ul li a,
  #secondary .view-content .item-list ul li a,
  #trois .view-content .item-list ul li a,
  .bloc-custom.ma-caf span.intro,
  .intro,
  .bloc-custom.mon-compte span.intro, .bloc-custom.mon-compte .contenu.intro {
    color: whitesmoke !important;
  }
    
  #bas_caf {
    background-color: initial;
  }
  
  #page-content ul#acces-direct-links a,
  ul#acces-direct-links a,
  #primary ul, #secondary ul, #trois ul,
  .home-bottom #title,
  .home-bottom .view-content .views-field-nothing,
  .home-bottom .view-content .date-display-single,
  .home-bottom .view-content .views-field-title a {
    color: whitesmoke !important;
  }
  
  #informer-aides-combobox,
  #liste-aides > ul {
    border: none;
    color: whitesmoke !important;
    background: #252C33;
  }
  
  div#liste-aides ul > li > a {
    color: whitesmoke !important;
  }
  
  .home-bottom {
    box-shadow: initial;
    background: black;
  }
  
  .msie9 #page-content .bloc-acces-direct .bloc-content,
  .msie9 .bloc-acces-direct .bloc-content,
  .otherBrowser #page-content .bloc-acces-direct .bloc-content,
  .otherBrowser .bloc-acces-direct .bloc-content,
  .msie9 #page-content .bloc-dans_ma_caf .bloc-content,
  .msie9 .bloc-dans_ma_caf .bloc-content,
  .otherBrowser #page-content .bloc-dans_ma_caf .bloc-content,
  .otherBrowser .bloc-dans_ma_caf .bloc-content {
    background: #252C33;
  }
  
  #theme-contenu-page .conteneur,
  #theme-page,
  #theme-filariane a,
  #theme-filariane span {
    background: black;
    color: whitesmoke !important;
  }
  
  #theme-bloc-bar-moncompte {
    background: none;
  }
  
  #theme-page-sidebar #menu li.on {
    background: #252C33;
  }
  
  #theme-page-sidebar #menu li.sub ul li a {
    color: #B0B0B0 !important;
  }
  
  #theme-page-sidebar #menu li.on ul li.on a.on, #activemacaf #theme-page-sidebar #menu li.on ul li.on a {
    color: #ADA3A3 !important;
}
  
  .onglet-actif {
    background-color: #252C33;
  }
  
  #theme-contenu-page .table-conteneur {
    background: black;
  }
  
  #theme-contenu-page .table-conteneur td, #theme-contenu-page .table-conteneur th, #theme-contenu-page .table-conteneur-mes-droits td, #theme-contenu-page .table-conteneur-mes-droits th {
    color: whitesmoke;
  }
  
  #theme-contenu-page #ma-situation .conteneur-contenu .bloc-situation {
    background: #252C33;
  }
  
  /*Lien bleu*/
  a,
  div.deconnexion a,
  #theme-bloc-bar-moncompte .derniere-connexion,
  .home-bottom .view-content .lire a,
  .linkbot ul.ulcaf li a,
  .bloc-custom.ma-caf h2,
  .bloc-custom.mon-compte h2
  #theme-contenu-page.moncompte-accueil #votre-caf-vous-informe .conteneur-contenu .titre,
  #theme-contenu-page.moncompte-accueil #votre-caf-vous-informe .conteneur-contenu .resume,
  #theme-filariane,
  #theme-contenu-page.moncompte-accueil #votre-caf-vous-informe .conteneur-contenu .lien-suite,
  #theme-contenu-page .conteneur .conteneur-titre a,
  #theme-contenu-page .conteneur-contenu .liens li a, #theme-contenu-page .conteneur-contenu a, #theme-contenu-page .conteneur .conteneur-bas a, #theme-contenu-page #contenu-page a, #theme-contenu-page .menu-onglet a, #theme-contenu-page .table-conteneur a {
    color: #81CFE0 !important;
  }

  
  #theme-contenu-page.moncompte-accueil .unlayeredContainerRow2 .layoutRow,
  #theme-contenu-page.moncompte-accueil .unlayeredContainerRow3 {
    background: black;
  }
  
  /*footer*/
  #theme-footer {
    background: black;
  }
  
  #theme-footer-principal .mentions-legales a,
  #mention_legale a,
  #footer-2 .block1 ul ul a {
    color: whitesmoke !important;
  }
  
  #footer-outside {
    background: black;
  }
  
}
