css:
@-moz-document domain("ptt.cc") {
body {
    background: #eee;
}

body, .bbs-content {
    font-family: 'Noto Sans',sans-serif;
}

#topbar-container {
    display: none;
}
a:visited {
    color: #666;
}

a:link {
    color: #222;
}
.b0 {
    background-color: #e5e5e5;
}

.b1 {
    background-color: #C70000;
}

.b2 {
    background-color: #0FB22B;
}

.b3 {
    background-color: #D78F04;
}

.b4 {
    background-color: #4280E5;
}

.b5 {
    background-color: #A47AB6;
}

.b6 {
    background-color: #4B6E68;
}

.bbs-content {
    color: #333;
    font-size: 18px;
    line-height: 30px;
    width: 720px;
}

.bbs-screen {
    background: #eee;
}

.f0 {
    color: #333;
}

.f1 {
    color: #C70000;
}

.f2 {
    color: #108024;
}

.f3 {
    color: #D78F04;
}

.f4 {
    color: #4280E5;
}

.f5 {
    color: #A47AB6;
}

.f6 {
    color: #80A29C;
}

.hl.f0 {
    color: #666;
}

.hl {
    color: #333;
}

.hl.f1 {
    color: #FF6F6F;
}

.hl.f2 {
    color: #28D345;
}

.hl.f3 {
    color: #FF9532;
}

.hl.f4 {
    color: #75D3FF;
}

.hl.f5 {
    color: #EC78D5;
}

.hl.f6 {
    color: #8AE9D8;
}

/*post header*/

.article-meta-tag {
    background: transparent;
    color: #333;
    padding: 0;
}

.article-metaline, .article-meta-value, .article-metaline-right {
    background: transparent;
    color: #333;
}

.article-metaline-right {
    opacity: .5;
}

.article-metaline:first-child {
    font-size: 15px;
    padding-top: 75px;
}

.article-metaline:nth-child(3) .article-meta-tag {
    display: none;
}

.article-metaline:nth-child(3) .article-meta-value {
    left: 0;
    padding: 0;
    position: absolute;
    top: 30px;
    font-size: 24px;
    font-weight: bold;
}

.article-metaline:nth-child(4) {
    padding-bottom: 15px;
    border-bottom: 1px solid #ccc;
    font-size: 15px;
}

[class^="article-metaline"]:nth-child(3n+1) [class^="article-meta"] {
    color: #999;
}

/*push messages*/

.f2+div.push {
    margin-top: 30px;
}

.push+.f2+div.push {
    margin-top: 0;
}

.push {
    box-sizing: border-box;
    background: #e5e5e5;
    height: 45px;
    padding-left: 10px;
    border-bottom: 1px solid #ccc;
}

.push span {
    line-height: 45px;
}

.push-content {
    color: #333;
}

.push-ipdatetime {
    top: 0;
    right: 5px;
    position: relative;
}

.push-tag {
    line-height: 30px;
}

.push-tag.f1 {
}

/*hide repost message*/

div.push+.f2, div.push+.f2+.f2, div.push+.f2+.f2+.f2 {
    display: none;
}

div.push+.f2+.f2+.f2+div.push {
    position: relative;
    margin-top: -30px;
}

.richcontent {
    margin: 0;
}

a[href*='png'],a[href*='jpg'] {
    display: block;
    font-size: 14px;
    position: relative;
    top: 15px;
}

a[href*='png']:after, a[href*='jpg']:after {
    content: ' :';
}

a[href*='png']:hover,a[href*='jpg']:hover {
    background-color: transparent;
}

@media screen {
  #topbar a:link, #topbar a:visited, #topbar a:hover {    
    text-decoration: underlined;
  }
}

img {
    box-shadow: 0 0 10px rgba(0,0,0,0.2);
    max-height: 500px;
    max-width: 720px;
}

img:hover {
    max-height: 1000px;
    max-width: 1000px;
    -moz-transition-property: all;
    -o-transition-property: all;
    -webkit-transition-property: all;
    transition-property: all;
    -moz-transition-duration: 0.5s;
    -o-transition-duration: 0.5s;
    -webkit-transition-duration: 0.5s;
    transition-duration: 0.5s;
    -moz-transition-timing-function: ease-out;
    -o-transition-timing-function: ease-out;
    -webkit-transition-timing-function: ease-out;
    transition-timing-function: ease-out;
}

#article-polling {
    box-sizing: border-box;
    max-width: 720px;
    font-size: 18px;
    padding: 10px;
    background: none;
}

#navigation-container {
    padding: 10px 0;
    background: #e5e5e5;
    box-shadow: 0 0 20px rgba(0,0,0,.2);
}

#navigation > * {
    padding: 0;
}

#navigation .board {
    color: #333;
}

#navigation .bar {
    background: none;
    width: auto;
}

#navigation .bar:after {
    content: '|';
    margin: 0 10px;
    color: #333;
}
}
