css:
@-moz-document domain('www.solarmovie.ph'),
	url-prefix('http://www.solarmovie.ph'),
	url-prefix('https://www.solarmovie.ph') {

.relative {
	background-color: #000!important;
}
.shortMainPage {
	background: #000 none repeat scroll 0% 0%;
	color: #B2B2B2;
	margin: 0px;
	padding: 0px;
	font-family: Verdana;
	font-size: 1em;
}
a{
	color: #00ccff!important;
}

.headerpage {
	background-color: #000 !important;
}

.frame .wrapper h1 {
	background-color: #000 !important;
}

.frameWrapper {
	background-color: #000 !important;
}

.frameWrapper .wrapper {
	background: transparent url('http://i61.tinypic.com/m9x8cx.png') no-repeat scroll -119px 0;
	background-color: #000 !important;
}

.ratingsWrapper {
	background-image: linear-gradient(to top,#000,#000);
}

.reportabug {
	background-image: linear-gradient(to right, #000 0%, #000 100%) !important;
}

.SearchesBlock {
	color: white!important;
}
.commentPreviewBody {
	color: grey!important;
}

.oddComment {
	background-color: #000!important;
}

.comingSoonPage {
	background-color: #000!important;
}

.calendarSort {
	background-color: #000!important;
}

.mediaDescription {
	color: grey!important;
}

.latestTvEpisode {
	background: #000!important;
}

.tagSeoContainer {
	background: rgb(0,0,0)!important;
}
.comments {
	color: grey!important;
}
.headerText {
	color: grey!important;
}

.sourceNameCell {
	background-color: #000!important;
	color: grey!important;
}

.qualityCell {
	background-color: #000!important;
	color: grey!important;
}

.ageCell {
	background-color: #000!important;
	color: grey!important;
}

.verionFavoriteCell {
	background-color: #000!important;
	color: grey!important;
}
.dataTable tbody .oddCell, .modevents tbody .oddCell {
	background-color: #000;
}
.twitterNews {
	background-color: #000!important;
	color: grey!important;
}
.dataTable tbody tr, .modevents tbody tr {
	background-color: #000;
}
.searchField {
	background: black!important;
	color: grey!important;
}

.message {
	background: rgb(0,0,0)!important;
	color: grey!important;
}

.js-stars {
	background-color: #000!important;
}

h3 {
	background-color: rgb(0,0,0)!important;
	color: grey!important;
	text-shadow: none!important;
}

.commentDate {
	background-color: rgb(0,0,0)!important;
	color: grey!important;
}

.watchMovieBlock {
	background: none!important;
	color: grey!important;
}

.pseudoText {
	color: grey!important;
}

.tagContainer {
	background: none!important;
	color: grey!important;
}

#footer {
	background-color: #000!important;
}

#footer p {
	background-color: #000!important;
}
		
ul,li,hr,h3,a,span,div {
	border:none !important;
	background: #000 !important;
	background:none !important;
}

input {
	background: none !important;
}
.searchBoxHeader .searchField {
	float: none;
	order: 1;
	flex: 4 1 auto;
	margin-right: 0px;
	width: 100%;
	font-size: 2em;
	border-radius: 0px;
	font-family: Verdana;
	padding: 0px 1.7em 0px 0.4em;
	height: 2em;
	font-weight: 100;
	background-color: #000;
	border: 3px solid #00B1FF;
}
}
