@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(?!store\\.)(.*\\.)?digg\\.com/(?!reader$).*") {
  body, .digg-story-card__front, .digg-permalink__heading, .digg-permalink__new-top-level-comment--fixed .digg-new-comment {
    background: #212121;
  }
  
  body, .site-header, site-header a, .marquee .digg-story__title, .digg-story__title, .marquee .digg-story__title-link, .digg-story__title-link, .digg-story--video-landing .digg-story__title, 
  .digg-permalink__heading__nav-item__link.is--active, .no-touch .digg-permalink__heading__nav-item__link.is--active:hover, .digg-story-stream__nav-item.is--active, .no-touch .digg-story-stream__nav-item.is--active:hover,
  .user-profile__name {
    color: #CCC;
  }

  .site-header, .site-navigation-bottom {
    background: #171717;
  }
  
  .site-navigation-bottom {
    border-color: #171717;
    height: 45px;
  }

  .site-navigation__nav-item, .site-navigation__nav-item-link {
    color: #999;
  }
  
  .site-navigation__nav-item-link.is--active, .no-touch .site-navigation__nav-item-link:hover {
    color: #21759B;
  }
  
  .nav-profile-tooltip {
    background-color: #292929;
    border-color: #171717;
  }
  
  .nav-profile-tooltip__action, .btn-login {
    color: #797979;
  }
  
  .nav-profile-tooltip:before {
    border-color: #171717 transparent;
    border-width: 0 11px 10px;
  }
  
  .nav-profile-tooltip:after {
    border-color: #292929 transparent;
    border-width: 0 10px 10px;
  }
  
  .btn-login {
    background-color: #212121;
    border-color: #121212;
  }
  
  .channel__subnav-wtf {
    border-bottom: 1px dotted #212121;
  }
  
  .channel__subnav__nav-item.is--active {
    color: #797979;
  }
  
  .icon, .ico-camera, .ico-close--sml, .ico-bolt, .ico-harry-potter, .ico-pencil, .ico-plus, .ico-reload-arrow, .ico-upload-photo, .ico-trending, 
  .ico-permalink, .ico-ellipsis, .ico-back, .ico-reply-arrow, .ico-notifications, .ico-twitter-blue, .ico-facebook-blue, .ico-google-red, 
  .site-navigation__nav-item-icon--home, .site-navigation__nav-item-icon--video, .site-navigation__nav-item-icon--reader, .site-navigation__nav-item-icon--store, 
  .site-navigation__nav-item-icon--search, .site-navigation__nav-item-icon--profile, .site-header--secondary .site-navigation__nav-item-icon--search, 
  .site-header--secondary .site-navigation__nav-item-icon--profile, .ico-x, .ico-ellipsis--gray, .ico-ellipsis--blue, .ico-back--gray, .ico-back--blue, 
  .ico-reply-arrow--gray, .ico-reply-arrow--blue, .digg-user__context-menu-link, .digg-user__context-menu-link:hover, .item-actions__show-additional-btn__icon, 
  .item-actions__show-additional-btn:hover .item-actions__show-additional-btn__icon, .focused-story.digg-story .item-actions__show-additional-btn__icon, 
  .show-story-actions.digg-story .item-actions__show-additional-btn__icon, .no-touch .digg-story:hover .item-actions__show-additional-btn__icon, 
  .focused-story.digg-story .digg-comment .item-actions__show-additional-btn__icon, .show-story-actions.digg-story .digg-comment .item-actions__show-additional-btn__icon, 
  .no-touch .digg-story:hover .digg-comment .item-actions__show-additional-btn__icon, .digg-permalink__heading__back-link__icon, 
  .digg-permalink__heading__back-link:hover .digg-permalink__heading__back-link__icon {
    background: transparent;
    text-indent: 200%;
    white-space: nowrap;
    overflow: hidden;
    display: inline-block;
    background-repeat: no-repeat;
  }
  
  .site-navigation__nav-item-link.is--active .site-navigation__nav-item-icon--home, .no-touch .site-navigation__nav-item-link:hover .site-navigation__nav-item-icon--home, 
  .site-navigation__nav-item-link.is--active .site-navigation__nav-item-icon--video, .no-touch .site-navigation__nav-item-link:hover .site-navigation__nav-item-icon--video, 
  .site-navigation__nav-item-link.is--active .site-navigation__nav-item-icon--reader, .no-touch .site-navigation__nav-item-link:hover .site-navigation__nav-item-icon--reader, 
  .site-navigation__nav-item-link.is--active .site-navigation__nav-item-icon--store, .no-touch .site-navigation__nav-item-link:hover .site-navigation__nav-item-icon--store, 
  .site-navigation__nav-item-link.is--active .site-navigation__nav-item-icon--search, .no-touch .site-navigation__nav-item-link:hover .site-navigation__nav-item-icon--search, 
  .site-navigation__nav-item-link.is--active .site-navigation__nav-item-icon--profile, .no-touch .site-navigation__nav-item-link:hover .site-navigation__nav-item-icon--profile {
    background-color: transparent;
  }
  
  /*
  * Credit to flaticon.com for these images - Start
  */
  .site-navigation__nav-item-icon--home {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAApUlEQVQ4jcXQoQrCYBSG4TWjzTRkweBleAuCQcGwaLQZbUaj1cvwEsQmWIw246LNx7KhzOk2UXzhlP/734/DCYJfgQ62OKBbV+5j584eo6pyjKNnTpiUyVOcC+SMBLMisYE5Lm/kjCsWaGZyC8sKYp4V2gHWH8gZm/zxqjJ9df2vFozRS2eYy+LSgoI8qbXBXwoGJQWPDJ8K0k8hIkQFWZRO+Ph+A62jMF/+8KY7AAAAAElFTkSuQmCC');
  }
  
  .site-navigation__nav-item-icon--video {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAdUlEQVQ4jcXTMQ0CYRBE4U1oaFGAAxRccTVCUIACUICCU4AQzsApwAH9R0F+A7MFT8BLdnamqqow4Y59JWD2Y8PcEcAHD5xSweCFC3apYLBg6ghGNlccUsHgiXNHAG/ccPyLID6hFeIifOMqLFKryvGY4jl/AQ8q9J8o/Ca2AAAAAElFTkSuQmCC');
  }
  
  .site-navigation__nav-item-icon--reader {
    background-image: url('data:image/png;base64,');
  }
  
  .site-navigation__nav-item-icon--store {
    background-image: url('data:image/png;base64,');
  }
  
  .site-navigation__nav-item-icon--profile, .site-header--secondary .site-navigation__nav-item-icon--profile {
    background-image: url('data:image/png;base64,');
  }
  
  .site-header__logo, .digg-logo {
	background: transparent url('data:image/png;base64,') no-repeat scroll 50% 8px / 47px 39px;
  }
  
  .site-navigation__nav-item-icon--search, .site-header--secondary .site-navigation__nav-item-icon--search {
    background-image: url('data:image/png;base64,');
  }
  
  .ico-bolt, .ico-harry-potter {
	  background-image: url('data:image/png;base64,');
  }
  
  .user-profile__lock-icon {    
    background-image: url('data:image/png;base64,')
  }
  
  .user-profile__pencil-icon {
    background-image:  url('data:image/png;base64,');
  }
  /*
  * Credit to flaticon.com for these images - End
  */
  
  .digg-logo {
    color: transparent;
    text-shadow: none;
    background-color: transparent;
    border: 0px none;
    width: 45px;
    height: 32px;
    margin: 0px auto 20px;
    background-repeat: no-repeat;
    background-size: 45px 27px;
  }
  
  .onboarding-step-login a.btn-twitter-auth, .onboarding-step-login a.btn-facebook-auth, .onboarding-step-login a.btn-google-auth {
    color: #959595;
  }
  
  .site-navigation__nav-item-icon--search, .site-navigation__nav-item-icon--home, .site-navigation__nav-item-icon--video, .site-navigation__nav-item-icon--reader, 
  .site-navigation__nav-item-icon--store, .site-navigation__nav-item-icon--profile, .user-profile__pencil-icon {
    background-size: 12px 12px;
  }
  
  .ico-bolt, .ico-harry-potter {
	  background-size: 11px 11px;
  }
  
  .digg-story__description, .digg-story__kicker, .user-profile__stream-notice {
    color: #797979;
  }
  
  .digg-story--video-landing .embed-code {
    border-color: #292929;
    background-color: #343434;
  }
  
  .story-actions--v2 .story-action {
    border-color: transparent;
    background-color: #121212;
  }
  
  .story-actions--v2 .story-action:hover {
    background-color: #191919;
  }
  
  .story-actions--v2 .story-action__text {
    color: #21759B;
  }
  
  .digg-story--video-landing .digg-story__description a, .digg-story--video-landing .digg-story__hattip a {
    color: #EEE;
  }
  
  .digg-story--original .story-body p, .digg-story--original .digg-story__body p, .digg-story--original .story-body ul, .digg-story--original .digg-story__body ul, 
  .digg-story--original .story-body ol, .digg-story--original .digg-story__body ol, .digg-story--original .story-body blockquote, .digg-story--original .digg-story__body blockquote, 
  .digg-story--original .story-body .author-detail, .digg-story--original .digg-story__body .author-detail {
    color: #797979;
  }
  
  .digg-story--original .story-body h4, .digg-story--original .digg-story__body h4, .digg-story--original .story-body h2, .digg-story--original .digg-story__body h2,
  .digg-story--permalink .digg-story__title, .digg-story-stream .digg-story:first-child .digg-story__title-link {
    color: #DDD;
  }
  
  .originals-popular-stories, .digg-story-stream__header {
    border-color: #121212;
  }
  
  .digg-story--original hr {
    background-color: #121212;
  }
  
  .digg-story--original .story-byline a {
    color: #666;
  }
  
  .popular-upcoming-wrap {
    width: 1007px;
    margin: 0px auto;
    background-image:  url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mMQERH5DwABuAE8tIxiHAAAAABJRU5ErkJggg==');
    background-repeat: repeat-y;
    background-position: 678px 0px;
    min-height: 1500px;
  }
  
  .digg-footer .footer-inner {
    border-top: 1px solid #121212;
  }
  
  .channel__subscribe-widget {
    border-color: #121212;
    background-color: #292929;
  }
  
  .digg-story--original .digg-story__byline a, .channel__subscribe-widget__title {
    color: #888;
  }
  
  .channel__subscribe-widget__success {
    color: #555;
  }
  
  /*
  * Settings Page
  */
  .settings-hed, .settings-section-hed {
    color: #CCC;
  }
  
  .digg-permalink__comments .digg-user__name-link {
    color: #000;
  }
  
  .digg-user__name-link {
    color: #EEE;
  }
  
  .settings-section-content p {
    color: #CCC;
  }
  
  .settings-section {
    border-top: 1px solid #121212;
  }
  
  .form-control, .btn-action, .form-toggle-switch-container, .form-control-select, .form-control-options, .btn-primary, .form-toggle-group__toggle {
    background-color: #333;
    border: 1px solid #121212;
  }
  
  .form-control:hover, .btn-action:hover, .form-toggle-switch-container:hover, .form-control-select:hover, .form-control-options:hover, .btn-primary:hover, .form-toggle-group__toggle:hover {
    border-color: #000;
  }
  
  .is--on .form-toggle-switch-pos-false, .is--off .form-toggle-switch-pos-true, [data-switch-value="true"] .form-toggle-switch-pos-false, [data-switch-value="false"] .form-toggle-switch-pos-true {
    background-color: #121212;
    color: #393939;
  }
  
  .is--off .form-toggle-switch-pos-false, [data-switch-value="false"] .form-toggle-switch-pos-false {
    background-color: #000;
    color: #FFF;
    border-left: 1px solid #292929;
  }
  
  .btn, .btn--heavy, .btn--success, .btn--warning, .digg-graph-btn.is--following, .modal--v2__btn, .user-update-profile__submit, .modal--v2__btn--success {
    background-color: #333;
    border: 1px solid #121212;
    color: #CCC;
  }
  .btn:hover, .btn--heavy:hover, .btn--success:hover, .btn--warning:hover, .digg-graph-btn.is--following:hover, .modal--v2__btn:hover, .user-update-profile__submit:hover, .modal--v2__btn--success:hover {
    color: #000;
  }
  
  /*
  * Search Page
  */
  .digg-story, .object-header, .ftr-nav {
    border-color: #121212;
  }
  
  /*
  * Notification Page
  */
  .notifications-feed__header-txt {
    color: #CCC;
    border-color: #121212;
  }
}
