css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {
    body::before {
    background-color: #1D0F24;
    background-image: url("http://static.tumblr.com/d5546e0126bcd8b0e7d041380b05f322/7gpz7la/BLanqrx3m/tumblr_static_b6mv1je05eok4koco8cwks4go.jpg");
    background-size: cover;
    background-repeat: no-repeat;
    z-index: 104;
    content: "";
    position: fixed;
    width: 100%;
    height: 100%;
    left: 0px;
    top: 0px;
    z-index: 0;
    filter: blur(7px) brightness(70%) contrast(95%);
    margin:0px;
}
.newtab-cell {
    border-radius: 24px;
    background-color: rgba(255, 255, 255, 0);
}
.newtab-link{
    opacity:1;
    transition:0.2s ease;
    border-radius: inherit;
}
body .newtab-link:hover{
    opacity:1;
    filter: brightness(112%);
    transition:0.3s ease;
}
.newtab-cell:not([ignorehover]) .newtab-control:hover ~ .newtab-link, .newtab-cell:not([ignorehover]) .newtab-link:hover, .newtab-site[dragged] {
    border:none;
    box-shadow: 0 3px 30px 5px RGBA(255, 255, 255, 0.2);
}
.newtab-thumbnail{
    filter: brightness(85%);
    opacity: 0.6;
}
.newtab-site:hover .newtab-thumbnail{
    opacity: 1;
}
.newtab-control{
    transition: 0.2s ease;
}
.newtab-title {
    background-color: rgba(21, 12, 57, 0.86);
    color: #b4b4b4;
    font-size: 16px;
    border: 1px solid RGBA(57, 58, 69, 0.78);
    transition:ease 0.2s;
}
.newtab-site{
    box-shadow: 0 3px 10px 5px RGBA(255, 255, 255, 0.2);
    font-weight: bold;
}
#newtab-search-text {
    -moz-box-flex: 1;
    padding-top: 6px;
    padding-bottom: 6px;
    padding-left: 34px;
    padding-right: 8px;
    background: hsla(253, 35%, 20%, 0.5) padding-box;
    border: 1px solid;
    border-spacing: 0;
    border-radius: 2px 0 0 2px;
    border-color: hsla(210,54%,20%,.15) hsla(210,54%,20%,.17) hsla(210,54%,20%,.2);
    box-shadow: 0 1px 0 hsla(210,65%,9%,.02) inset, 0 0 2px hsla(210,65%,9%,.1) inset, 0 1px 0 hsla(0,0%,100%,.2);
    color: white;
}
.newtab-site:hover .newtab-title {
    color: #fbfbfb;
    background-color: RGBA(56, 54, 91, 0.84);
    border: 1px solid #000;
    border-top: 1px solid black;
}}
