css:
@-moz-document url-prefix("http://topfrag.rip") {

#content {
background: #111;
}
.post-header h1 {
    color: #999;
}
.post-header h2 a, .post-header h1 {
    color: #999;
}
.breadcrumbs a {
    color: #999;
}
.pmpro_checkout thead th {
    color: #999;
}
.comment-reply-link {
    color: #000;
}
.pmpro_checkout tbody td {
    background: #222;
    color: #999
}
p {
    color: #999;
}
.widget-title {
    background-color: #222;
}
.widget-title {
    color: #999;
}
.comment-box {
    background-color: #444;
}
.comment-box a {
    color: #999;
}
.post {
    border-bottom: 1px solid #999;
}
li.the-comment {
    border-bottom: 1px solid #999;
}
ul.children li.the-comment:first-child {
    border-top: 1px solid #999;
}
.comment-reply-title {
    border-left: 5px solid #444;
    color: #999;
}
.comment-reply-link {
    background-color: #777;
}
.container {
    color: #999;
}
#pmpro_account .pmpro_box h3 {
    color: #999;
}
.post-entry th {
    background: #222;
}
.archive-box h1 {
    color: #999;
}
.pmpro_checkout tr.odd td {
    background: #222;
}
.post-reply textarea {
    background-color: #999;
}
.footer-social h4 {
    color: #999;
}
#copyright p.left a {
    color: #ef3418;
}
.ScrollContainer-content {
    background-color: #111;
}
.ButtonGroup {
    background-color: #111;
}
.u-paddingVM {
    background-color: #111;
}
.ScrollContainer-header {
    background: #111;
    border-bottom: .09167rem solid #999;
}
.u-textXHeight {
    color: #999;
}
.ScrollContainer {
    background-color: #999;
}
.Form-field {
    background: #999;
    color: #111
}
.Form-field input, .Form-field select, .Form-field textarea {
    color: #000;
}
.c-btn--secondary {
    background-color: #333;
    color: #999;
}
input[type=submit] {
    color: #999;
}
input, textarea {
    border: none;
}
.comment-text td, .comment-text th, .post-entry td, .post-entry th {
    border-bottom: 1px #999 solid;
}
#pmpro_account .pmpro_box {
    border-top: 1px solid #999;
}
}
