css:
@-moz-document domain("miscursos.itesm.mx") {
html {font-size:13px;font-weight:400;color:#222}
* {font-family:Open Sans,sans-serif!important;text-shadow:0 0 0 transparent!important;border:none!important;box-shadow:none!important;}
body {background:url(http://play.pokemonshowdown.com/fx/client-bg-shaymin.jpg) 0% 50% / cover no-repeat fixed rgb(84, 107, 172)!important}
#contentArea, #schemePreviewBody, .container,.reorderableModule {background:rgba(255,255,255,0.7)!important}
.breadcrumbs, .paneTabs {background:rgba(255,255,255,0.7)!important}
.paneTabs .active a:after, .paneTabs .active a:before {display:none!important;}
#contentArea, #schemePreviewBody, .contentBox {background:transparent}
a {color:#E91E63;}
div.buttonCm ul.courseMenu li, div.buttonCm ul.courseMenu li.here {
    border-radius: 20px;
    box-sizing: border-box;
    margin: .4em auto;
    width:90%;
    white-space: nowrap;
    background: #ff4081!important;
    box-shadow: 0 0 2px rgba(0,0,0,.12),0 1px 2px rgba(0,0,0,.24)!important;
    padding:.2em 1em;
}

.contentPane {height:497px!important;overflow:hidden;}
.container {height:400px!important;overflow:auto;overflow-x:hidden;background:rgba(255,255,255,0.7)!important}
.container *,#contexto *,#contexto {background:none!important}
#contexto {height:0;font-size:120%;}
#contexto *:not(input) {color:#fff!important;font-weight:700;text-shadow:0px 1px 3px #000!important}
.pie {position:fixed;bottom:0;}
#navigationPane {height:auto!important;position:fixed;top:64px;}

#courseMenuPalette h3 a {color:#9C27B0!important;}

#courseMenuPalette div.navPaletteContent ul li a, #previewCourseMenu h3 a, .navPaletteContent h3 a.submenuLink:before, .navPaletteContent h3 a.submenuLink_active:before, #courseMenuPalette div.navPaletteContent ul li a span, #courseMenuPalette .navPaletteContent .subhead {color: #fff!important;padding:0;margin:0}


#navFrame {-webkit-filter: saturate(0.3) brightness(1.5)}

#topFrame, .bgBanner {background:url(http://www.zingerbug.com/Backgrounds/background_images/pink_christmas_lights_out_of_focus_seamless_texture.jpg)  repeat ;}


}
