css:
/* i really want this to be global */
.mint-content-box__header {
    height: 2rem;
}

.mint:after, .logged-out:after {
    background-color: #f2f2f2;
    bottom: 0;
    content: "";
    height: 100%;
    left: 0;
    opacity: 1;
    position: fixed;
    right: 0;
    top: 0;
    width: 100%;
    z-index: -1;
}

.container.mint-layout__wrapper__container {
    background-color: #fff;
    border-radius: 9px;
    width: 984px;
    margin: 100px auto 0;
    border: 1px solid #ededed;
}

#main-container {
    background-color: #fff;
}

.mint-subjects-filter {
    background-color: #fff;
    margin: -48px auto 0;
    max-width: 984px;
    border-top-left-radius: 11px;
    border-top-right-radius: 11px;
    border-top: 1px solid #ededed;
    border-left: 1px solid #ededed;
    border-right: 1px solid #ededed;
}

.mint-layout__container--no-margin {
    background-color: #fff;
    width: 1024px;
    margin: 0 auto 48px;
    border: 1px solid none;
    border-bottom-left-radius: 11px;
    border-bottom-right-radius: 11px;
    border-left: 1px solid #ededed;
    border-right: 1px solid #ededed;
    border-left: 1px solid #ededed;
    border-bottom: 1px solid #ededed;
}

.mint-subjects-filter__list-element a {
    background-color: #fff;
}

.mint-stream__heading--spaced-top {
    margin: 0px 0 24px 0;
}

.logged-in .mint-layout__container, .logged-out .mint-layout__container--reversed-order {
    background-color: #fff;
    width: 1024px;
    margin: 100px auto 48px;
    border: 1px solid none;
    border-top-left-radius: 11px;
    border-top-right-radius: 11px;
    padding-top: 2rem;
}

.mint #page-outer {
    -moz-box-shadow: 0px 0px 11px 2px rgba(117,116,117,0.22);
    -webkit-box-shadow: 0px 0px 11px 2px rgba(117,116,117,0.22);
    background-color: #fff;
    border-radius: 9px;
    box-shadow: 0px 0px 3px 2px rgba(117,116,117,0.02);
    margin: 0 auto;
    margin-bottom: 48px;
    padding-top: 0px;
    top: 100px;
    width: 1024px;
}

.mint #page-outer #page-container {
    margin-top: 24px;
}

.mint #page-outer.stream-horizontal #content, .mint #page-outer.stream-horizontal #aside-content, #stream .task .task-box, #aside-stream .task .task-box, #brainly_stream .task .task-box {
    background-color: transparent;
}

.mint-layout {
    margin: 0;
}
