css:
@-moz-document domain("rt.ic.utoronto.ca") {
/* Solarized Scheme
base03    #002b36 
base02    #073642 
base01    #586e75 
base00    #657b83 
yellow    #b58900 
orange    #cb4b16
red       #dc322f
magenta   #d33682
violet    #6c71c4
blue      #268bd2
cyan      #2aa198
green     #859900

Table of Contents

01 Backgrounds & Borders
02 Text
03 Editor
04 Buttons
05 Miscellaneous
*/
/*Backgrounds & Borders */

body,
#body,
#footer {
    color: #657b83 !important;
    background: #002b36 !important;
    /* base 03 */
}
#header,
#page-navigation,
#page-menu.pinned {
    background: #073642 !important;
    /* base 02 */
    
    border-bottom: 1px solid #859900;
}
#quickbar,
#main-navigation li {
    background: #073642 !important;
    border-bottom: none !important;
}
#main-navigation ul li * {
    border-left: none !important;
}
table,
tr,
td,
.titlebox-title,
.actions {
    border-color: #268bd2 !important;
}
.oddline td,
.transaction.odd {
    background: #002b36 !important;
    /*base 03 */
}
.evenline td,
.transaction.even {
    background: #073642 !important;
    /*base 02 */
}
input {
    background: #073642 !important;
    /* base 02 */
    
    border: 1px solid #657b83;
}
select {
    background: #073642 !important;
    /* base 02 */
}
textarea {
    background: #073642 !important;
    /* base 02 */
}
select,
input,
textarea,
.messagebody,
.transaction,
.titlebox-content {
    border-color: #268bd2 !important;
}
#Get-Customer-Info tr:first-of-type {
    background-color: #586e75 !important;
}
.results .titlebox .titlebox-content,
#li-page-bookmark {
    background: #b58900 !important;
    color: #002b36 !important;
}
/* Text */

body {
    font-size: 12pt;
    font-weight: 500;
}
body,
.message-stanza,
select,
input,
.downloadcontenttype {
    color: #657b83 !important;
    /* base 00 */
}
th {
    color: #cb4b16 !important;
}
a,
a:visited,
.label,
.description {
    color: #b58900 !important;
    /* yellow */
}
h1 {
    color: #cb4b16 !important;
}
/* Editor */

.cke_chrome {
    border: none;
}
.cke_inner {
    background: #586e75 !important;
    /* base 01 */
}
.cke_toolbox_collapser,
.cke_resizer {
    background: #cb4b16 !important;
    border: none;
}
.cke_toolgroup,
.cke_combo_button {
    background: #268bd2 !important;
}
#main-navigation {
    font-weight: 600;
}
/* Buttons */

.actions {
    background-color: #586e75 !important;
    color: #586e75 !important;
}
#CreateTicketInQueue > input {
    background: #002b36 !important;
    color: #dc322f !important;
    font-size: 12pt;
    font-weight: 400;
    text-transform: capitalize;
}
/* Miscellaneous */

.titlebox-content {
    border-bottom: none !important;
}
.titlebox-title .left {
    color: #b58900 !important;
}
.tdempty font {
    color: #657b83 !important;
}
.titlebox-content td {
    padding-top: 6pt !important;
}
.cftype-Select .entry select {
    margin-top: 6pt;
}
/*Remove Credits */

#bpscredits {
    display: none;
}
#logo {
    font-size: 12pt !important;
}
#logo img {
    display: none;
}
/* Highlight Colour */

::selection {
    background: #d33682;
    /* WebKit/Blink Browsers */
}
::-moz-selection {
    background: #d33682;
    /* Gecko Browsers */
}
}
