css:
@-moz-document domain("www.bbc.com"), domain("www.bbc.co.uk"), domain("bbc.co.uk") {

/* http://www.bbc.com/news HOMEPAGE */

/* Make main container and other white backgrounds dark */
div.container, div.sparrow-item__body, div.pukeko-item__body, div[role=main], div#page {
    background: #222 !important;
}
div.sparrow-item {
    background: #222 !important;
}
.sparrow.sparrow__3 .sparrow-item {
 	border-color: #222 !important;
}
.markets-index-promo {
    background: #222 !important;
}
li.bananaquit__item a {
    background: #222 !important;
}
h2.most-popular__title, ul.most-popular__header__tabs {
    background: #222 !important;
}
div.spoonbill, div.spoonbill-footer {
    background: #222 !important;
}

/* Make top menu bar border darker */
.orb-nav-active, .orb-nav-active .orb-nav-id, .orb-nav-active .orb-nav-links li, .orb-nav-active .orb-nav-links ul, .orb-nav-active .orb-nav-oldsearch, .orb-nav-active .orb-nav-oldsearch a, .orb-nav-active .orb-panel, .orb-nav-pri-white, .orb-nav-pri-white .orb-nav-id, .orb-nav-pri-white .orb-nav-links li, .orb-nav-pri-white .orb-nav-links ul, .orb-nav-pri-white .orb-nav-oldsearch, .orb-nav-pri-white .orb-nav-oldsearch a, .orb-nav-pri-white .orb-panel {
    border-color: #191919 !important;
}

/* Make all article header links white and more spaced out */
h3.gs-c-promo-heading__title {
 	color: #eee !important;   
    font-weight: 400;
    letter-spacing: 0.3px;
}

/* Hide ads */
div#bbccom_leaderboard_1_2_3_4, div#bbccom_mpu_4, div#bbccom_native_1_2_3_4, div.bbccom_adsense_container {
    display: none !important;
}

/* Make top menu bar background, sub-menu & 'more' dropdown red */
div#orb-header, div#orb-panel-more, div.secondary-navigation {
    background: #bb1919 !important;
}

/* Make mid navigation bar background dark grey */
.site-brand, .navigation--wide {
    background: #1F1F1F !important;
    border: 0px;
}
/* Make  all icons background deep red */
div#orb-header, div#orb-panel-more, 
.badge-text-only--live, .badge-text-and-icon--live-audio, .badge-text-and-icon--live-radio, .badge-text-and-icon--live-video, .badge-text-only--breaking, .badge-text-and-icon--breaking, .badge-text-and-icon--media {
    background: #861414 !important;    
}

/* Make lower-mid navigation bar background lighter grey for an accent */
div.secondary-navigation {
    background: rgb(64, 62, 62) !important
}

/* Make BBC logo brighter */
div.orb-nav-section a img {
   -webkit-filter: invert(1);
   filter: invert(1);
}

/* Make other page icons brighter */
li.simple-list__item span svg path,
li.junglefowl__item span svg path,
li.junglefowl__item span svg circle {
   fill: #eee !important;
}


/* Hide login image */
.orb-nav-id #idcta-statusbar, .orb-nav-active .orb-nav-id #idcta-statusbar {
    background-image: none !important;
}

/* Make text foregrounds bright */
a, p, span, h2.group-title, h2.markets-index-promo__title, tr.markets-index-promo__row td:nth-child(2), h2.most-popular__title, h3.warbler__link-heading {
    color: #c9c9c9 !important;
}

/* Make other languages text bright */
ul.ws-promo-list li a span svg g path, ul.ws-promo-list li a span svg g polygon, ul.ws-promo-list li a span svg path, ul.ws-promo-list li a span svg polygon {
    fill: #eee !important;
}

/* Restore hover color to a lighter blue, add fade effect */
.title-link:hover .title-link__title-text, .title-link:focus .title-link__title-text, a:hover, span.most-popular-list-item__headline:hover, h3.warbler__link-heading:hover {
 	color: #3CA7F7 !important; 
    -o-transition:.3s;
	-ms-transition:.3s;
  	-moz-transition:.3s;
  	-webkit-transition:.3s;
	transition:.3s;
}

/* Change Sport badge text to dark */
span.badge-text-only--sport {
    color: #222 !important;
}

/* Remove underlines from video links in right column */
span.title-link__title-text {
    text-decoration: none !important;
    font-weight: 300 !important;
    letter-spacing: 0.2px;
}

/* Fix fonts */
span,a,p {
    font-family: Helmet, Freesans, Helvetica, Arial, Calibri, sans-serif !important; /* todo: mac */
}

/* Hide the 'Follow Us' tab */
div#comp-follow-us {
    display: none !important;
}

/* Change the colors of the Most Popular tab (non-homepage)*/
div.most-popular-by-day__heading {
 	background-color: #222 !important; 
}
h2.most-popular-by-day__title {
    color: #c9c9c9 !important; 
}

/* The section between videos and markets on the right */
.eu-ref-promo .eu-ref-logo, div.coot  {
    background: #222 !important; 
}

/* Fix the colors and spacing on the 'Most Popular' tab */
.most-popular__header__tabs li a {
    background: #555 !important;
}
.most-popular__header__tabs li.open a {
 	background: #bb1919 !important;
    padding-bottom: 6px !important;
}
.most-popular__header__tabs li {
 	margin: 0 !important;
}
ul.most-popular__header__tabs {
 	margin: 16px;
}



/* Remove timestamps */
.pigeon-item__info-list,
.macaw-item .mini-info-list,
div.buzzard__info-list ul.mini-info-list,
div.robin-item__body ul.mini-info-list,
div.sparrow-item__info ul.mini-info-list
{
 	display: none !important;   
}

/* Remove Sharing section */
div#ugc-promo-title, 
div#comp-pattern-library-14,
ul.simple-list {
 	display: none !important; 
}

/* Remove unnecessary lines */
.macaw-item__body {
 	border-top: 0 !important;   
}



/* http://www.bbc.com/news ARTICLE */

/* Make dark text white */
.story-body__unordered-list .story-body__list-item,
.story-body__h1,
.story-body__crosshead,
.share__title,
.group__title,
h2.heading {
    color: #eee !important;
}

/* Change pullout block to match page */
div.pullout-inner {
    border: 0px;
    padding: 8px;
    background: #333 !important;
}
span.cta:hover {
    color: #3CA7F7 !important; 
}


/* http://www.bbc.com/news/video_and_audio/international */

/* Make video length & icon darker */
.responsive-image__label .icon,
.responsive-image__label-text {
   -webkit-filter: invert(1);
   filter: invert(1);    
}

/* Fix for bbc.com/future */

/* Make background of the page darker */
#story-page #story-content {
   background: #222222;
}

/* Adjust colors for text under pictures at bottom of the article */
.caption-wrapper {
    background: #222222;
}

.headed-section h2.section-title {
    color: #ddd !important;   
}


/*   FOOTER   */

/* Darken the background of the second footer */
#extra-footer {
    background: #222222 !important; 
}

/* Adjust More Stories, Around The BBC colors */
.around-the-bbc, .secondary-unbranded {
   background: #222222 !important; 
}

/* Adjust footer text colors */
.promo-unit .promo-unit-layout-standard .promo-unit-title {
    color: #ddd !important;   
}

/*   SEARCH RESULTS   */

div.page-content, div.secondary-branded {
    background: #222222 !important; 
}

#article .article-content .article-list .promo-unit-wrapper .promo-unit-lining, #story-page-container .article-content .article-list .promo-unit-wrapper .promo-unit-lining, .promo-unit .promo-unit-lining {
    background: #222222 !important; 
}


}
