css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netvibes.com") {

    .moduleContent, div.nv-thumbnailedList, .rssreader-container, .galleryView,  .galleryView .content, .galleryView .content .top, .galleryView .content .pager, .galleryView .content .pager div.thumbnail, .galleryView .content .crop, .magazineView, .carouselView, .blankCarousel .title span, .blankCarousel .description, .borderlessCarousel .description, .carouselView .crop, .magazineView .crop, .scoopView .crop, .galleryView .crop, .blankMagazine .article h3, .borderlessMagazine .article h3, .blankMagazine .article p, .blankMagazine .share a, .borderlessMagazine .article p, .borderlessMagazine .share a, .blankMagazine .item h3, .borderlessMagazine .item h3, .blankMagazine .description p, .borderlessMagazine .description p, .scoopView .top, .loading-overlay, .module.loading .moduleContent {
    background-color: #333;
    color:#ccc;
  }
  
  body.nv-loading, .nv-smallmessage > *
  {
    background-color: #333 !important;
    color:#ccc !important;
  }
  
  .blankMagazine .item, .borderlessMagazine .item, .blankMagazine .right, .borderlessMagazine .right, .blankMagazine .left, .borderlessMagazine .left {
    border:none !important;
  }
  
  .moduleContent a {
    color: #ccc !important;
  }
  
 .moduleContent a:visited {
    color: #999;
  }
  
  .gripper {
    opacity: 0.5 !important;
  }
  
  
.nv-thumbnailedList .read, .nv-thumbnailedList .read h3, .nv-thumbnailedList .read h3 a {
    color: #999;
}
  
  .read, .read h3, .read h3 a {
    color: #555 !important;
  }
  
 .detailed-item:nth-child(2n), .nv-thumbnailedList .even, .nv-richList .even, .nv-productList .even {
    background-color: #3b3b3b !important;
  }
  
 .item .age, .nv-productList .age, .blankCarousel .description small, .borderlessCarousel .description small, .blankMagazine .item small, .borderlessMagazine .item small {
    color: #555 !important;
  }
  
  .twitter .count {
    color:#ccc !important;
  }
  
  .status-edit, .twitter .description, .twitter .uwa-tab {
   opacity:0.6 !important;
  }
  
  .twitter .updateForm .status-edit .inn textarea {
    color:#fff !important;
    background-image: url(http://www.netvibes.com/modules/twitter/img/background-dark.png?v=1) !important;
  }
  
  .twitter .updateForm.status-focus .status-edit .inn textarea {
    background-image: none !important;
  }
  
  .module.loading .moduleContent .module-loading-inner > .module-spinner {
    background:none;
  }
  
  div.uwa-themed-scroller-content > div:first-child > div {
    background-color: #333 !important;
  }
  
  div.uwa-themed-scroller-content > div:first-child > div:not(.nv-reader-mosaic-container):nth-child(even) {
    background-color: #3b3b3b !important;
  }
  
  div.uwa-themed-scroller-content > div:first-child > div:not(.nv-reader-mosaic-container):hover {
    background-color: #2d2d2d !important;
  }
  
  div.uwa-themed-scroller-content > div:first-child > div:not(.nv-reader-mosaic-container):nth-child(even):hover, div.nv-reader-mosaic-container a.item:hover {
    background-color: #3f3f3f !important;
  }
  
  .nv-line-item-renderer:not(.build-image):not(.this-class-does-not-exist) .item-preview-wrapper::after {
    background-image:linear-gradient(90deg, rgba(51, 51, 51, 0), #333);
  }
  
  div.uwa-themed-scroller-content > div:first-child > div:not(.nv-reader-mosaic-container):not(.build-image):not(.this-class-does-not-exist):nth-child(even) .item-preview-wrapper::after {
    background-image:linear-gradient(90deg, rgba(59, 59, 59, 0), #3b3b3b);
  }
  
  div.uwa-themed-scroller-content > div:first-child > div:not(.nv-reader-mosaic-container):not(.build-image):not(.this-class-does-not-exist):hover .item-preview-wrapper::after {
    background-image:linear-gradient(90deg, rgba(45, 45, 45, 0), #2d2d2d);
  }
  
  div.uwa-themed-scroller-content > div:first-child > div:not(.nv-reader-mosaic-container):not(.build-image):not(.this-class-does-not-exist):hover:nth-child(even):hover .item-preview-wrapper::after {
    background-image:linear-gradient(90deg, rgba(63, 63, 63, 0), #3f3f3f);
  }
  
  .nv-condensed-item-renderer .item-time {
    box-shadow: -10px 0 10px 10px #333 !important;
    width: auto !important;
  }
  
  .nv-condensed-item-renderer:hover .item-time {
    box-shadow: -10px 0 10px 10px #2d2d2d !important;
  }
  
  div.uwa-themed-scroller-content > div:first-child > div.nv-condensed-item-renderer:not(.nv-reader-mosaic-container):nth-child(even) .item-time {
    box-shadow: -10px 0 10px 10px #3b3b3b !important;
  }
  
  div.uwa-themed-scroller-content > div:first-child > div.nv-condensed-item-renderer:not(.nv-reader-mosaic-container):nth-child(even):hover .item-time {
    box-shadow: -10px 0 10px 10px #3f3f3f !important;
  }
  
  div.nv-reader-mosaic-container .item {
    background:#3b3b3b !important;
  }
  
  .item-preview-wrapper {
    white-space: normal !important;
  }
  
  .item-title, .uwa-select-options > *, .uwa-message.simple {
    color:#ccc !important;
  }
  
  .item-title {
    font-size:12px !important;
    font-weight:bold !important;
    box-decoration-break:clone !important;
    display:inline !important;
  }
  
  .item-preview {
    color: #999 !important;
  }
  
  .item-time {
    color: #666;
    display: inline !important;
    font-size: 11px;
    margin-left: 6px !important;
  }
  
  .nv-line-item-renderer::after, .nv-line-item-renderer:hover::after {
    border-bottom: 1px solid transparent !important;
    content: "";
    display: block;
  }
  
  .no-touch .nv-line-item-renderer:hover {
    border-top:none;
    margin-top:0;
  }
  
  .nv-item-list-renderer.white-shadows .uwa-themed-scroller-shadow {
    border-color: #333;
    box-shadow: 0 0 12px 10px rgba(51, 51, 51, 1);
  }
  
  .nv-item-list-renderer.white-shadows {
    border:none;
  }
  
  .uwa-themed-scroller-shadow-y-start {
    top:-100% !important;
  }
  
  .uwa-themed-scroller-shadow-y-end {
    top:100% !important;
  }
  
  .uwa-message, .uwa-message.simple {
    background-image:url("data:image/svg+xml;charset=utf-8;base64,");
    background-repeat: no-repeat;
    color: #666;
    margin-left: 5px;
    margin-right: 5px;
  }

.nv-line-item-renderer.build-image .item-title, .nv-line-item-renderer.build-image .item-time, .nv-line-item-renderer.build-image .item-preview {
    margin-left: 0;
}
.item-preview-wrapper {
    margin-left: 92px;
}
.nv-line-item-renderer.build-image .item-preview-wrapper {
    height: 61px;
}
}
