css:
@-moz-document domain("oneforum.ga") {
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto Regular'), local('Roboto-Regular'), url(http://themes.googleusercontent.com/static/fonts/roboto/v11/2UX7WLTfW3W8TclTUvlFyQ.woff) format('woff');
}
/* Normal */

@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 700;
    src: local('Roboto Bold'), local('Roboto-Bold'), url(http://themes.googleusercontent.com/static/fonts/roboto/v11/d-6IYplOFocCacKzxwXSOD8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}
/* Bold (700) */

@font-face {
    font-family: 'Roboto';
    font-style: italic;
    font-weight: 400;
    src: local('Roboto Italic'), local('Roboto-Italic'), url(http://themes.googleusercontent.com/static/fonts/roboto/v11/1pO9eUAp8pSF8VnRTP3xnvesZW2xOQ-xsNqO47m55DA.woff) format('woff');
}
/* Italic */

@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 300;
    src: local('Roboto Light'), local('Roboto-Light'), url(https://fonts.gstatic.com/s/roboto/v15/Pru33qjShpZSmG3z6VYwnRJtnKITppOI_IvcXXDNrsc.woff2) format('woff2');
    unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* Light (300) */

@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 300;
    src: local('Roboto Light'), local('Roboto-Light'), url(https://fonts.gstatic.com/s/roboto/v15/Hgo13k-tfSpn0qi1SFdUfVtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
/* Light (300) - extended */

* {
    font-family: "Roboto" !important;
}
div#disqus_thread {
    background: #FFFFFF !important;
    border: #FFFFFF !important;
}
.navbar-default,
.logo-text a {
    background-color: #FFFFFF !important;
}
.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus {
    color: rgb(162, 112, 129) !important;
}
.navbar-default .navbar-nav > .active {
    border-bottom: 3px solid #888888 !important;
}
.navbar-default .navbar-nav > li > a,
.logo-text a {
    color: rgb(216, 150, 172) !important;
}
.navbar-default .navbar-nav > li:not(.active) > a:hover {
    color: rgb(189, 131, 150) !important;
    border-bottom: 3px solid #CCCCCC !important;
}
.t3-mainnav {
    border-bottom: 1px solid #EEEEEE !important;
}
.article-title a,
h1,
h2,
h3 {
    font-weight: 300 !important;
}
.t3-navbar {
    float: right !important;
}
#t3-content > div > article > section > div.reading-block > h3 {
    margin-top: 0px !important;
}
#t3-content > div > article > header > h1 {
    margin-bottom: 5px !important;
}
#t3-content > div > article > section > div.reading-block > h3 {
    color: #AAAAAA !important;
}
}
