css:
@-moz-document regexp("https?:\\/\\/(?:mysku)\\.ru\\/[^i]?.*") {
@media screen
{

  
   body
  {
    background-color: #999;
  }

.topic .topic-title a , .topic .topic-title span { border-bottom: solid 2px #999; }
  
   #nav
  {    background-color: #999;  }
    
   #wrapper
  {    background-color: #999;  }
     
  #content
  {    background-color: #999;  }
  
  .block  { background: #999;}
  .block.stream span.posted_date_time { color: #555;} 
  .border { border: 1px solid #aaa; }

  .comment .info ul li.date {color: #444;}
  
  .comment .content .text {color: #222; }
  
  .comment .content .main_side { border: 1px solid #aaa;}
  
  .comment .voting.positive .total { color: #333;}
  
  .comment .content blockquote {
    color: #111;
    background-color: #999;
  }
  
.spoiler .spoiler-body {background: #888 none repeat scroll 0% 0% ; border: 1px solid #aaa;}
  
  .spoiler .spoiler-title { margin: 8px 4px;}
 
  .spoiler { background: #999 url("http://cdn.mysku.ru/templates/skin/mysku.v3/images/discount_title_bg.png") repeat-x scroll 0px 38px; border: 1px solid #aaa;}
  
  .topic .content img, .topic-content img { border: solid 2px #aaa; border-radius: 1px; }
  
  
  
 .read_all a {
    background: #888 none repeat scroll 0% 0%;
    border: 1px solid #888;
  }
  
  .purchase_button span.arrow {
    background: #888 url("http://cdn.mysku.ru/templates/skin/mysku.v3/images/purchase_button_arrow.png") no-repeat scroll 50% 50%;
    border-right: 1px solid #666;
  }
  
  .purchase_button { border: 1px solid #666;}
  
  .purchase_button span.number { border-left: 1px solid #666;}
  
  .ilike_button {border: 1px solid #666;}
  
  .ilike_button span.number {
    border-left: 1px solid #666;
    background: #888 none repeat scroll 0% 0%;
  }
  
  ul.category-list a.list-item-link {background: #888  no-repeat scroll 100% 0px;}
  
  .site_about .site_rights {
     text-shadow: 0px 0px 0px #FFF;
     background: #888 repeat-x scroll 0px 0px;
  }
  .site_about .site_rights s { background: transparent repeat-x scroll 0px -18px;}
  
  .site_about .site_rights em { background: transparent repeat-x scroll 0px -36px; }

  
  .main_side.new { background: #aaa none repeat scroll 0% 0% !important;}
  
  
}
}
