css:
@-moz-document domain("slack.com") {
/* ===[ General ]=== */

body,
.gbmc,
#hdtbSum,
.action-menu-panel.ab_dropdown,
#header #logo,
.exydlg,
.hdtb-mn-c,
#qbp,
.goog-menu,
.gb_r,
.gb_Kb,
.gb_Qb {
	background: #101010 url("http://www.cgsociety.org/cgsarchive/newgallerycrits/g10/76910/76910_1351507339_large.jpg") !important;
	background-repeat: no-repeat fixed center!important;
	background-position: center;
	background-size: 100% 100% !important;
}
* {
	background-color: transparent !important;
}
body {
	background: #272a2e;
	color: #e0e0e0;
}
a,
a:link,
a:visited {
	color: #b4b7b4;
}
a:hover,
a:active,
a:focus {
	color: #12B0A4;
}
hr {
	border-top: 1px solid #373b41;
	border-bottom: 1px solid #969896;
}
.message .mention {
	background: #0D595F;
}
input[type="text"]:focus,
input[type="text"]:active,
input[type="number"]:focus,
input[type="number"]:active,
input[type="email"]:focus,
input[type="email"]:active,
input[type="password"]:focus,
input[type="password"]:active,
textarea:focus,
textarea:active,
select:focus,
select:active {
	box-shadow: 0 0 7px rgba(0, 0, 0, 0.15);
	border-color: #373b41;
}
/* ===[ Slack Status ]=== */

.section {
	background: #f3f3f3;
	color: #12B0A4;
}
.section.large_top_margin {
	margin-top: 0 !important;
}
.section .current_incident {
	margin-bottom: 2rem !important;
}
.section a,
.section a:link,
.section a:visited {
	color: #5deaa8;
}
.section a:hover,
.section a:active,
.section a:focus {
	color: #439fe0;
}
.section .cloud_silver {
	color: #babbbf !important;
}
.section .indifferent_grey {
	color: #555459 !important;
}
.section h1,
.section h2,
.section h3,
.section h4 {
	color: #555459;
}
.section hr {
	border: 1px solid #DDD;
}
.section #footer {
	background: none;
}
/* ===[ Loading View ]=== */

.loading #loading-zone {
	background: #272a2e;
	left: 110px;
}
.loading #col_channels_bg {
	background: #272a2e;
}
#self_help_link_div {
	right: 110px !important;
}
#loading_indicator,
#loading_welcome_msg {
	color: #2AB27B!important;
	text-shadow: 0 1px #373b41;
}
#loading_team_menu_bg,
#loading_user_menu_bg {
	border: none;
}
/* ===[ Messages Container ]=== */

.msgs_holder,
#msgs_overlay_div {
	background: #272a2e;
}
.light_theme .message_sender,
.dense_theme .file_reference:hover a.file_download_link {
	color: #b4b7b4 !important;
}
#messages_unread_status,
.file_reference .icon,
.file_list_item .icon {
	background: #5aa368!important;
	color: #FFF;
}
#messages_unread_status.quiet,
.msgs_holder .unread_divider span.divider_label {
	color: #e0e0e0;
}
.msgs_holder .unread_divider hr {
	border-top: 1px solid #a3685a !important;
}
.message .msg_actions .fa-cog,
.bullet,
.file_list_item .bullet {
	color: #b295bb;
}
.day_divider hr {
	border-top: 1px solid #373b41;
	border-bottom: 1px solid #969896;
}
.day_divider_label {
	background: #272a2e;
	color: #00ffec;
}
#message_edit_form span,
#message_edit_form p,
#message_edit_container .timestamp {
	color: #272a2e !important;
}
#message_edit_form #cancel_edit,
#message_edit_form #commit_edit {
	color: #272a2e !important;
	text-decoration: underline;
}
#welcome_slide_app_icons a {
	color: #e0e0e0;
	border: 1px solid #969896;
}
#welcome_slide_app_icons a:hover {
	color: #e0e0e0;
	background: #a3685a;
	border-color: #373b41;
}
.dense_theme .message .message_star_holder {
	position: absolute;
	left: -12px;
	top: 2px;
}
.dense_theme .file_reference:hover a.file_download_link:hover,
.inline_attachment a span:hover,
.inline_attachment a span:active,
.msg_inline_video_buttons_div a,
.msg_inline_video_buttons_div a:visited,
.inline_attachment .attachment_footer a:hover,
.inline_attachment .attachment_footer a:active,
.inline_attachment .attachment_ts a:hover,
.inline_attachment .attachment_ts a:active,
.dense_theme .file_reference:hover a:hover:not(.member):not(.member_name):not(.icon):not(.timestamp) {
	color: #3ba1df !important;
}
.light_theme .msg_inline_img_holder,
.light_theme .msg_inline_video_holder,
.dense_theme .msg_inline_img,
.dense_theme .msg_inline_video {
	border: 2px solid #373b41;
}
.inline_attachment .attachment_bar,
.special_formatting_quote .quote_bar {
	background: #b4b7b4 !important;
}
.msg_inline_video_buttons_div {
	background: dark_button;
}
.light_theme .message,
.dense_theme .message,
#messages_unread_status.quiet a,
.delete_attachment_link,
.bot_message .message_sender,
.bot_message .message_sender a,
.msg_inline_img_expander:hover,
.msg_inline_img_collapser:hover,
.msg_inline_video_expander:hover,
.msg_inline_video_collapser:hover,
.msg_inline_audio_expander:hover,
.msg_inline_audio_collapser:hover,
.msg_inline_attachment_expander:hover,
.msg_inline_attachment_collapser:hover,
a.file_download_link:hover,
#msgs_scroller_div #end_display_div #end_display_welcome h1,
#msgs_scroller_div #end_display_div #end_display_meta h1,
.snippet_preview pre,
#msgs_scroller_div #end_display_div p,
a:hover .file_inline_icon {
	color: #e0e0e0;
}
.light_theme .message.joined .message_content,
.light_theme .message.left .message_content,
.light_theme .message.topic .message_content,
.light_theme .message.purpose .message_content,
.light_theme .message.rename .message_content,
.light_theme .message.archived .message_content,
.light_theme .message.unarchived .message_content,
.dense_theme .message.joined,
.dense_theme .message.left,
.dense_theme .message.topic,
.dense_theme .message.purpose,
.dense_theme .message.rename,
.dense_theme .message.archived,
.dense_theme .message.unarchived,
.star,
.light_theme .message .timestamp,
.dense_theme .message .timestamp,
.message .edited,
.light_theme .message .meta,
.dense_theme .message .meta,
.dense_theme .message.first .timestamp,
.light_theme .message.unprocessed,
.dense_theme .message.unprocessed,
.light_theme .message:hover .timestamp,
.dense_theme .message:hover .timestamp,
.dense_theme .file_reference a:not(.member):not(.member_name):not(.icon):not(.timestamp),
.dense_theme .file_reference:hover a:not(.member):not(.member_name):not(.icon):not(.timestamp),
a.member_preview_link.member_name,
.inline_attachment span.attachment_author_subname,
.inline_attachment span.attachment_service_name,
.inline_attachment span.attachment_author_name,
.inline_attachment .attachment_ts a,
.inline_attachment .attachment_footer a,
.help i.callout,
.file_inline_icon {
	color: #b4b7b4;
}
.dense_theme .message.divider,
.dense_theme .message.first,
.dense_theme .message.first.divider {
	margin-top: .2rem;
	border-top: 1px solid transparent;
}
.message .msg_actions,
.day_divider {
	background: none;
}
.delete_attachment_link:hover,
.msg_inline_img_expander,
.msg_inline_img_collapser,
.msg_inline_video_expander,
.msg_inline_video_collapser,
.msg_inline_audio_expander,
.msg_inline_audio_collapser,
.msg_inline_attachment_expander,
.msg_inline_attachment_collapser {
	color: #2999ee;
}
.message.edit_mode,
.message .mention {
	background: #81a2be;
}
/* ===[ Channel Menu ]=== */

#header .client_header_button {
	background: #3e434a;
	border: 1px solid #272a2e;
}
#header .client_header_button:hover {
	background: #272a2e;
	color: #81a2be;
	border: 1px solid #272a2e;
}
.client_header_icon {
	background: none;
}
.client_header_icon:before {
	color: #3d9836;
	font-family: FontAwesome;
}
.client_header_icon.icon_help:before {
	display: inline-block;
	content: "\f129";
}
#help_icon #help_icon_info {
	left: 4px;
	top: 6px;
}
.client_header_icon.icon_menu:before {
	content: "\f141";
}
#flex_menu .icon_menu {
	left: 6px;
	top: 7px;
}
#flex_toggle .icon_flex_pane:before {
	content: "\f104";
	font-size: 18px;
}
.flex_pane_showing #flex_toggle .icon_flex_pane:before {
	content: "\f105";
	font-size: 18px;
}
#flex_toggle .icon_flex_pane {
	left: 9px;
}
.flex_pane_showing #flex_toggle .icon_flex_pane {
	left: 11px;
}
#active_channel_name:hover i {
	color: #e0e0e0;
}
#channel_members_toggle {
	background: #272a2e;
	color: #e0e0e0;
}
#channel_members {
	border: 1px solid #272a2e;
}
#header,
ul.member_list li:hover {
	background: #272a2e;
}
#active_channel_name .name,
#active_channel_name .topic,
#active_channel_name .prefix {
	color: #5deaa8;
}
#active_channel_name:hover .prefix,
#channel_header .star,
#flex_menu .fa-list-ul,
#active_channel_name i,
#channel_members_toggle:hover {
	color: #81a2be;
}
/* ===[ Search ]=== */

#header_search_form .search_input,
#autocomplete_menu .tips_header .new_badge {
	background: #3e434a;
	border: 1px solid #272a2e;
	color: #81a2be;
}
#search_results_items .search_paging,
#paging_in_options .search_paging,
#autocomplete_menu,
#autocomplete_menu .keyword_match .modifier,
.search_form .icon_search,
.search_message_result .search_message_result_meta a,
.search_message_result .search_message_result_meta .date_links a {
	color: #81a2be;
}
.search_form .icon_search:before {
	content: "\f002";
}
#header_search_form.search_form.feature_assistive_search .icon_search {
	top: 6px;
}
.search_form .icon_close:before {
	content: "\f00d";
}
.popover_menu,
#menu.popover_menu {
	background-color: #272a2e;
	border-top: 1px solid #373b41;
}
.popover_menu .arrow_shadow:after {
	box-shadow: 0 0 0 1px #373b41, 0 0 2px rgba(0, 0, 0, 0.1);
}
.popover_menu .arrow:after,
.popover_menu .arrow_shadow:after,
.popover_menu.showing_header .arrow:after,
.popover_menu.showing_header .arrow_shadow:after {
	background-color: #373b41;
}
.highlighter_underlay .modifier:before,
.highlighter_underlay .keyword:before {
	background: #3e434a;
	background: -webkit-gradient(linear, left bottom, left top, color-stop(0, #3e434a), color-stop(1, #a3685a));
	background: -ms-linear-gradient(bottom, #3e434a, #a3685a);
	background: -moz-linear-gradient(center bottom, #3e434a 0, #a3685a 100%);
	background: -o-linear-gradient(#a3685a, #3e434a);
	border: 1px solid #373b41;
}
.highlighter_underlay .modifier.incomplete:before {
	background: #373b41;
	background: -webkit-gradient(linear, left bottom, left top, color-stop(0, #373b41), color-stop(1, #272a2e));
	background: -ms-linear-gradient(bottom, #373b41, #272a2e);
	background: -moz-linear-gradient(center bottom, #373b41 0, #272a2e 100%);
	background: -o-linear-gradient(#272a2e, #373b41);
	border: 1px solid #373b41;
}
#autocomplete_menu footer .modifier:before,
#autocomplete_menu header .modifier:before,
#autocomplete_menu footer .keyword:before,
#autocomplete_menu header .keyword:before,
#autocomplete_menu .history .modifier:before,
#autocomplete_menu .history .keyword:before {
	background: #272a2e;
	background: -webkit-gradient(linear, left bottom, left top, color-stop(0, #272a2e), color-stop(1, #3e434a));
	background: -ms-linear-gradient(bottom, #272a2e, #3e434a);
	background: -moz-linear-gradient(center bottom, #272a2e 0, #3e434a 100%);
	background: -o-linear-gradient(#3e434a, #272a2e);
	border: 1px solid #373b41;
}
.highlighter_underlay .keyword:before,
#autocomplete_menu .history .keyword:before {
	border-left: 0;
}
#autocomplete_menu .history .modifier:after {
	border-right: 0;
}
#autocomplete_menu .boxed {
	background: #3e434a;
	background-image: -o-linear-gradient(-89deg, #272a2e 0, #3e434a 100%);
	background-image: -moz-linear-gradient(-89deg, #272a2e 0, #3e434a 100%);
	background-image: -ms-linear-gradient(-89deg, #272a2e 0, #3e434a 100%);
	background-image: linear-gradient(-179deg, #272a2e 0, #3e434a 100%);
	border: 1px solid #373b41;
	-webkit-box-shadow: 0 1px 0 0 #373b41;
	-moz-box-shadow: 0 1px 0 0 #373b41;
	box-shadow: 0 1px 0 0 #373b41;
}
#autocomplete_menu .magnifying_glass {
	display: none !important;
}
#autocomplete_menu .section_header .header_label {
	background: #373b41;
	color: #e0e0e0;
	padding-left: .8rem;
}
.search_result_with_extract {
	background: #272a2e;
	box-shadow: 0 1px 10px #272a2e;
	border: 1px solid #373b41;
}
.search_result_with_extract:hover {
	border: 1px solid #272a2e;
}
#autocomplete_menu header,
#autocomplete_menu ol li:hover {
	background-color: #272a2e;
}
#autocomplete_menu .monkey_scroll_wrapper {
	background-color: #373b41;
}
.search_result_for_context::after {
	background: #272a2e;
	background: -webkit-gradient(linear, left bottom, left top, color-stop(0, #272a2e), color-stop(1, dark_fade));
	background: -ms-linear-gradient(bottom, #272a2e, dark_fade);
	background: -moz-linear-gradient(center bottom, #272a2e 0, dark_fade 100%);
	background: -o-linear-gradient(dark_fade, #272a2e);
}
#search_tabs,
#search_filters a,
#advanced_options_toggle,
#search_options,
#advanced_options {
	background: #272a2e;
	color: #e0e0e0;
}
#search_filters a:hover {
	border-bottom: 4px solid #272a2e;
}
#search_filters.messages #filter_messages,
#search_filters.files #filter_files {
	color: #e0e0e0;
	border-bottom: 4px solid #272a2e;
}
.search_light_grey {
	color: #81a2be !important;
}
.search_message_result {
	background: #272a2e;
	color: #e0e0e0;
	border: none;
}
.search_message_result:hover {
	border: none;
}
.search_message_result.feature_search_extracts,
.search_message_result:hover.feature_search_extracts {
	border-top: 2px solid #373b41;
}
span.match {
	background: #81a2be;
}
#search_filters,
#search_options {
	border-bottom: 1px solid #272a2e;
}
/* ===[ Sidebar ]=== */

#team_menu {
	color: #5deaa8;
	border-bottom: none;
	border-right: 2px solid #373b41;
}
#col_channels {
	background: #272a2e;
	border-right: 2px solid #373b41;
}
#user_menu {
	border-right: 2px solid #373b41;
}
#menu_header .menu_simple_header {
	background: #272a2e;
	border-bottom: 1px solid #272a2e;
}
.dm_list_real_name {
	color: #e0e0e0;
}
#menu_footer .menu_footer {
	background: #272a2e;
	border-top: none;
}
#about_dms_link {
	color: #b4b7b4;
}
#team_menu i,
#user_menu i,
.dm_list_username {
	color: #5deaa8;
}
#team_menu:hover i,
#user_menu:hover i,
#about_dms_link:hover {
	color: #81a2be;
}
/* ===[ Footer ]=== */

#footer {
	background: #272a2e;
}
#primary_file_button {
	background: #272a2e;
	background: -webkit-gradient(linear, left bottom, left top, color-stop(0, #272a2e), color-stop(1, #3e434a));
	background: -ms-linear-gradient(bottom, #272a2e, #3e434a);
	background: -moz-linear-gradient(center bottom, #272a2e 0, #3e434a 100%);
	background: -o-linear-gradient(#3e434a, #272a2e);
	color: #3d9836;
	text-shadow: 0 1px 1px #373b41;
	border: 2px solid #373b41;
	border-right: none;
}
#primary_file_button:hover {
	background: #3e434a;
	color: #e0e0e0;
	border-color: #3e434a;
}
#typing_text,
#special_formatting_text {
	color: #b4b7b4;
}
#chat_input_tab_ui {
	background: #3e434a;
	border: 1px solid #373b41;
}
#chat_input_tab_ui_header {
	background: #3e434a;
	color: #e0e0e0;
	border-bottom: 1px solid #373b41;
	text-shadow: 0 1px #373b41;
}
#chat_input_tab_ui ul.type_emoji li {
	color: #e0e0e0;
}
#chat_input_tab_ui li.chat_input_tab_item.active,
#chat_input_tab_ui li.chat_input_tab_item:hover {
	background: #272a2e;
	color: #e0e0e0;
	border-bottom-color: #373b41;
	text-shadow: 0 1px rgba(0, 0, 0, 0.1);
}
#chat_input_tab_ui li.chat_input_tab_item,
.tab_complete_ui li.tab_complete_ui_item.active,
.tab_complete_ui li.tab_complete_ui_item:hover {
	border-bottom: 1px solid #373b41;
}
.tab_complete_ui li.tab_complete_ui_item {
	border-bottom: 1px solid #3e434a;
}
.special_formatting_tips code {
	color: #373b41;
}
/* ===[ Flexpane ]=== */

#flex_toggle i {
	transform: scaleX(-1);
	margin: 5px;
}
.flex_pane_showing #flex_toggle i {
	transform: none;
	margin: 5px 7px;
}
#flex_contents .heading {
	background: #272a2e;
}
#flex_contents .heading .menu_heading,
.toolbar {
	background: #272a2e;
	color: #5deaa8;
}
#flex_contents .heading a {
	color: #5deaa8;
}
#flex_contents .heading .menu_heading:hover,
#flex_contents .heading a:hover,
#file_preview_container .file_header .title a:hover,
#advanced_options_toggle:hover,
.team_list_item a:hover,
.file_list_item:hover .title a,
.file_list_item:active .title a,
.file_list_item.active .title a,
.file_list_item #share_dialog .title a,
.no_results,
#menu.search_filter_menu label,
.search_sort_prefix,
.file_list_item .actions a,
.file_list_item .actions span {
	color: #3d9836;
}
.secondary_file_button {
	color: #5deaa8 !important;
}
.monkey_scroll_wrapper,
#dms_filter,
#file_member_filter {
	background: #272a2e;
}
.indifferent_grey,
.icon_new_window:hover {
	color: #e0e0e0 !important;
}
.file_header .member,
.cloud_silver,
.file_meta,
.file_meta > a,
.activity_item .member_preview_link,
.comment .member,
.team_list_item {
	color: #b4b7b4 !important;
}
.file_list_item:hover,
.file_list_item:active,
.file_list_item.active,
.file_list_item #share_dialog,
#share_dialog .file_list_item,
#client-ui #team_list.feature_new_team_directory .team_list_item.expanded,
#client-ui #team_list.feature_new_team_directory .team_list_item:hover {
	background-color: #272a2e;
	border-color: #373b41;
}
.file_list_item:hover .actions,
.file_list_item:active .actions,
.file_list_item.active .actions,
.file_list_item #share_dialog .actions {
	background-color: #272a2e;
}
.filetype_button {
	color: #b4b7b4 !important;
	background-color: #272a2e;
	border-color: #373b41;
}
.filetype_button:hover {
	background-color: #272a2e;
}
.filetype_button .file_download_label {
	color: #e0e0e0;
	background-color: #a3685a;
	border-color: #373b41;
}
.filetype_button:hover .file_download_label {
	background-color: #a3685a;
}
.search_segmented_control,
#team_list.feature_new_team_directory a.member_action_button:hover {
	color: #b4b7b4 !important;
	border-color: #373b41 !important;
}
.search_segmented_control.active,
.search_segmented_control:hover {
	color: #e0e0e0 !important;
}
.file_list_item:hover .actions a,
.file_list_item:active .actions a,
.file_list_item.active .actions a,
.file_list_item #share_dialog .actions a,
.file_list_item:hover .actions span,
.file_list_item:active .actions span,
.file_list_item.active .actions span,
.file_list_item #share_dialog .actions span,
.search_segmented_control.active {
	background-color: #272a2e;
	border-color: #373b41;
}
#advanced_options p {
	border-top: 1px solid #373b41;
}
a.snippet_preview_more {
	color: black !important;
}
.extract_expand_icons .down_arrow {
	top: 8px;
}
.CodeMirror-lines {
	background: white;
}
#flex_contents .help {
	border: none;
	color: #e0e0e0;
}
#flex_contents .heading .menu_icon {
	background: none;
	color: #3d9836;
}
#menu ul li a.delete_link {
	color: #cc6666;
}
.nav > li > a:hover,
.nav > li > a:focus {
	background-color: #272a2e;
}
.nav-tabs > .active > a,
.nav-tabs > .active > a:hover,
.nav-tabs > .active > a:focus {
	background: #272a2e;
	border: 1px solid #373b41;
}
.secondary_file_button:hover,
.activity_item .member_preview_link:hover,
.icon_new_window,
.sky_blue {
	color: #3d9836 !important;
}
