    height: 18px;
    position: absolute;
    right: 3px;
    top: 3px;
    pointer-events: none;
}
   
a[title="Home"] img {
    display: none !important;
}

a[title="Home"] {
    width: 100%;
    height: 200px;
    background-image: url("http://i.imgur.com/wVi5yUE.png");
    display: block;
    background-position: center center;
    background-repeat: no-repeat;
}

.post.op > span img, .summary > img {
      opacity: 0;
  }

.post.op > span:after, .summary:before {
  width: 18px;
  height: 18px;
  pointer-events: none;
}

.post.op > span:after {
  display: block;
