css:
@-moz-document url-prefix("http://verbalkonter.forumieren.com/") {
body {
    background-image: url(http://blauergrashalm.de/doner/blub.png);
    color: white;
}
a,
a:link,
a:visited {
    color: #09944a;
}
a:hover {
    color: black;
    background-color: rgba(9, 148, 74, 0.5);
}
#submenu {
    background: #222;
    border-bottom: solid 0.2em #09944a;
    padding: 0.5em;
}
#userlinks.not-connected {
    background-color: transparent;
    border: none;
}
#submenu ul li a {
    color: white;
    margin: 0.1em;
    padding: 0.55em;
}
#submenu ul li a:hover {
    color: black;
    background-color: #09944a;
    border-bottom: 0.3em solid black;
}
#submenu ul li a > img {
    display: none;
}
.box-content {
    background-color: transparent;
}
.borderwrapm,
div.borderwrap {
    background: none;
    border: none;
    border-bottom: solid 0.2em #09944a;
    margin: 0.2em;
}
div.newslink {
    border-top: solid 0.2em #09944a;
    background: #333;
}
div.newslink p:nth-child(2),
div.newslink > form {
    display: inline;
}
div.newslink > form input {
    background: none;
    border: none;
    color: white;
}
div.maintitle {
    background: none;
    background-color: #09944a;
}
table.ipbtable th {
    background-color: black;
    border-right: solid 0.1em #09944a;
    border-bottom: solid 0.1em #09944a;
}
.row1,
.row2 {
    border-bottom: 0.1em black solid !important;
}
.row1 {
    background-color: #333;
}
.row2 {
    background-color: #222;
}
table.ipbtable tfoot td {
    display: none;
}
.formsubtitle,
table.ipbtable th.formsubtitle {
    background-color: #333;
}
.activeusers {
    border: solid 0.1em black;
    background-color: #333;
}
.timeline {
    background-color: #333;
    border: solid 0.1em black;
}
.p-author .profile .p-name {
    color: #fff;
}
}
