css:
@-moz-document domain(weblancer.net) {
body, .page_body {
    background: #F8F9FA;
}

.page_content > div, .sidebar > div {
    background: #FFF;
    box-shadow: 1px 2px 8px 1px #ccc;
        border-radius: inherit;
}

li.dropdown:hover .dropdown-menu {
    display: block;
    color: #fff;
}

.dropdown-menu>li>a 
{
    color: #fff;
}

.navbar.navbar-default.navbar-static-top.top_navbar {
    border: 0;
    position: fixed;
    width: 100%;
    box-shadow: 1px 1px 1px 1px #ccc;
}

.page_header {
    padding-top: 75px;
}

.top_navbar {
    background: #149FE0;
}

.top_navbar .navbar-nav > li > a:hover
{
     background: none;
}


.top_navbar .dropdown-menu, .top_navbar .open a {
    background: #1283B9;
    color: #FFF;
}

.top_navbar .dropdown-menu li > a:hover {
    background: #1283B9;
    color: #FFF;
    text-decoration: underline;
}

.btn, .btn:hover {
    color: #fff !important;
    background-color: #149FE0 !important;
    border-color: #149FE0 !important;
}

.btn-success {
    color: #fff;
    background-color: #2686D8  !important;
    border-color: #2686D8  !important;
}

.btn-success:hover {
    
    box-shadow: 1px 1px 1px 1px #ccc;
}


.amount {
    color: #149FE0;
}

a.title {
    color: #0877AB;
}

a:hover
{
    text-decoration: none;
}


.col-sm-7>span.text-muted {
    color: #AD0E0E;
}

@media (min-width: 768px)
{
    .page_body .col-sm-7 {
         width: 59.33333333%;
    }
    
    .page_body .col-sm-3 {
    width: 24%;
     }
}





.text-success {
    color: #149FE0;
    font-weight: bold;
}



.cols_table.show_visited .row a:visited:not(.blocked):not(.text-muted) {
    color: #9E9E9E;
}


.track-progress li.done + li > div:before {
    border-left-color: #149FE0;
}

.track-progress li > div:after {
    border-left-color: #F9F9F9;
}





.track-progress li.done > div {
    background-color: #149FE0;
    color: #FFF;
}

.pr_content
{
    display: none !important;
}



.form-group.days, .form-group.amount, .form-group.ready_to_deal {
    width: 33%;
    display: inline-block;
    vertical-align: top;
}
}
