@font-face {
  font-family: 'FontAwesome';
  font-style: normal;
  font-weight: 400;
  src: local('FontAwesome'), url(//cdnjs.cloudflare.com/ajax/libs/font-awesome/4.5.0/fonts/fontawesome-webfont.woff) format('woff');
}

#ca-edit span a {
    border-bottom-width: 0;
    border-color: #bbb;
    border-left-width: 0;
    border-right-width: 1px;
    border-style: solid;
    border-top-width: 0;
}

#ca-talk span a {
    border-bottom-width: 0;
    border-color: #bbb;
    border-left-width: 0;
    border-right-width: 1px;
    border-style: solid;
    border-top-width: 0;
}

#mw-content-text h2 {
    border: none;
    font-size: 32px;
    font-weight: 300;
}

#mw-content-text h3 {
    font-size: 24px;
    font-weight: 300;
}

* {
    font-family: 'Roboto', sans-serif;
}

.mw-body p {
    line-height: 1.8em;
    margin: 1em 0;
}

.mw-body-content {
    font-size: 18px;
    font-weight: 300;
}

.thumbcaption {
    margin-top: 10px;
}

@media  (min-width: 1330px) {
  div#toc {
    position: fixed;
    color: #000;
    top: 79px;
    left: 1030px;
    border: none;
    background-color: #fff;
    width: 250px;
    font-size: 14px;
    font-weight: 100;
    padding: 10px;
    display: block;
    max-height: calc(100% - 170px);
    -ms-overflow-y: auto;
    overflow-y: auto;
    -ms-overflow-x: hidden;
    overflow-x: hidden;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.11);
  }
  .mw-body #toc h2, .mw-body .toc h2 {
    font-family: 'Roboto Slab';
    color: inherit;
    font-size: 16px;
    font-weight: 500;
    letter-spacing: 2px;
  }
  div#toc a {
    color: inherit;
  }
  #toc #toctitle, .toc #toctitle, #toc .toctitle, .toc .toctitle {    
    margin: 10px auto;
  }
}

/* MAIN ELEMENTS */

/* HEADINGS */

div#content {
    border-left-color: #bbb;
    border-right-color: #bbb;
    border-right-width: 1px;
    border-top-color: #bbb;
    max-width: 780px;
}

div#mw-head {
    left: 0;
    max-width: 1005px;
}

div#mw-head-base {
    width: 829px;
}

div#mw-page-base {
    background-color: transparent;
    background-image: none;
    width: 1005px;
}

div#p-search {
    height: 25px;
}

div.thumbinner {
    background-color: transparent;
    border: none;
}

h1.firstHeading {
    font-family: 'Roboto Slab';
    font-size: 2.4em;
    color: #586979;
    font-weight: 700;
    border: none;
}


div.vectorTabs span a {
	padding-top: 0.3em;
}

div.vectorTabs li a {
	height: 1.5em;
}

div.vectorTabs {
	height: 1.4em;
	background-image: none;
}

div#left-navigation, div#right-navigation {
	margin-top: 3.45em;
}

#ca-unwatch.icon a, #ca-watch.icon a {
	padding-top: 1.8em;
	background-image: none;
	position: relative;
}

div.vectorMenu h3 span {
	padding-top: 0.5em;
}

div.vectorMenu h3 a {
	height: 1.5em;
}

div#mw-head div.vectorMenu h3 {
	height: 1.5em;
}

div.vectorMenu div.menu {
	top: 1.5em;
}

div.vectorTabs ul li {
	border-top: 1px solid #bbb;
	border-left: 1px solid #bbb;
	border-right: 1px solid #bbb;
	border-bottom: 1px solid #bbb;
	margin-left: -1px;
}

div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
	height: 1.75em;
}

div.vectorTabs li.selected {
	position: relative;
	top: -0.15em;
	height: 1.65em;
	background-image: none;
	background-color: #fff;
	border-bottom: 0;
}


#p-search form, #p-search input {
	margin-top: 0;
}

div#simpleSearch {
	margin-top: 0;
}

div.vectorTabs ul li, div.vectorTabs li.selected {
	background-image: none;
}

div.vectorTabs span {
	background-image: none;
}


#ca-edit span a, #ca-talk span a {
	border: 0;
}

#ca-unwatch.icon a::after, #ca-watch.icon a::after {
	content: '\f006';
	font-family: FontAwesome;
	font-size: 1.3em;
	line-height: 1.5em;
	text-align: center;
	display: block;
	height: 100%;
	width: 100%;
	position: absolute;
	top: 0;
	left: 0;
	color: #999;
}

#ca-unwatch.icon a::after {
	content: '\f005';
	color: #71DEFF;
}


#toc #toctitle, .toc #toctitle, #toc .toctitle, .toc .toctitle {
	text-align: left;
}
}
