css:
@-moz-document domain("last.fm") {
/*** GENERAL ***/

body, div, p, tr, td, table {
    font-family: Helvetica, "Lucida Grande", Arial, Helvetica, Verdana, sans-serif;
    font-size: 11px!important;
    line-height: 1.5em
}

p {
    line-height: 1.75em
}

a {
    color: #b90000;
}

h2 {
    font-size: 14px;
    font-weight: 700;
}

.masthead-logo a {color: #000!important}

.header-title-label-wrap .label+.label
{font-size: 8px}

/* HIDE ADS */
.chartlist tr[data-ad-container].open-ad-container-desktop, .chartlist tr[data-ad-container].open-ad-container-mobile,
#qa-page-load, #mpu_top, .features-footer, .mpu-subscription-upsell--mpu {
    display: none!important
}



/*** HEADER ***/

.header .header-background--has-image {
    opacity: 0.9;
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    filter: grayscale(100%);
}

.header .header-background:before {
    background-color: rgba(0, 0, 0, 0.75);
}

li.masthead-nav-item .masthead-nav-control {
    font-size: 9px;
    text-transform: uppercase;
}


/* FEATURED ITEM */

.featured-item-heading,
.recommended-next-page-title {
    padding-top: 5px;
    font-size: 9px!important;
}

.featured-item-name,
.recommended-next-page-name {
    font-size: 13px!important;
    font-weight: 700!important;
    line-height: 18px!important;
}

.featured-item-artist,
.recommended-next-page-artist {
    margin-top: -2px;
    font-size: 11.5px!important;
}

/* HEADINGS */
h1.header-title {font-size: 16px!important;}

.header--overview .header-crumb, .header--sub-page .header-crumb {font-size: 12px;
    font-weight: 700}

.header.header--user h1.header-title {
    font-weight: 700!important;
    font-size: 14px!important;
    -webkit-font-smoothing: antialiased;
}

.header-scrobble-since, .header-title-display-name {
    color: #B0B0B0;
}

/* NAVIGATION */

.secondary-nav-item-link, .secondary-nav--scrollable.secondary-nav--more .secondary-nav-more, .masthead-nav-links {
    text-transform: uppercase;
    font-size: 9px!important;
}

.header-avatar button {
    line-height: 1em!important;
    font-size: 9px!important;
}


/* METADATA */

h2.metadata-title,
th.metadata-title, h4.header-metadata-title,
.dropdown-buylinks--light .dropdown-buylink-title {
    font-size: 8.5px!important;
}
p.metadata-display,
p.header-metadata-display a, p.header-metadata-display {
    line-height: 1.25em!important;
    font-size: 14px!important;
}

/*** CHARTLISTS ***/


 table.chartlist td, .chartlist tr
  {
    padding: 1px!important;
    box-shadow: inset 0 0 #FFF!important;
  }


.chartlist--with-buylinks .lazy-buylinks-toggle {font-size: 9px;
padding-top: 2px}

.buylinks-dropdown-menu .buylink-category {
    font-size: 9px!important;}

.chartlist .chartlist-duration {text-align: center}

  .chartlist--no-big-image .countbar-bar-slug
  {
    height: 100%!important;
    border-top-right-radius: 7px;
    line-height: 35px!important;
    border-bottom-right-radius: 7px;
    background: #666!important;
  }

  table.chartlist .love-button
  {
    margin-top: 2px!important;
    margin-left: 6px!important;
  }

  td.chartlist-countbar
  {
    background-color: #fff!important;
  }

  .selectable-range .chartlist td, .selectable-range .chartlist th
  {
    box-shadow: inset 0 0 #fff!important;
  }

  .chartlist .chartlist-index
  {
    text-align: center!important;
color: #d0d0d0!important;
font-weight: 700;
font-size: 16px!important;
letter-spacing: -1px
  }
/* CHARTS W/ IMAGES */


.chartlist.chartlist--big-image .cover-art {
    width: 42px!important;
    height: 42px!important;
}


  .chartlist--big-image .countbar-bar-slug
  {
                        font-size: 12px;
    height: 100%!important;
    border-top-right-radius: 7px;
    line-height: 42px!important;
    border-bottom-right-radius: 7px;
    background: #666!important;
  }

/* TINY BUTTONS */

  .chartlist .chartlist-play-button
  {
    margin-top: 3px!important;
    margin-left: 15px!important;
    width: 33px!important;
    height: 25px!important;
    zoom: 53%;
    -ms-zoom: 0.53;
    -webkit-zoom: 0.53;
    -moz-transform: scale(0.53, 0.53);
    -moz-transform-origin: left center;
  }

  .stationlink-list .stationlink:before
  {
    padding-left: 33px!important;
    zoom: 53%;
    height: 30px;
    -ms-zoom: 0.53;
    -webkit-zoom: 0.53;
    -moz-transform: scale(0.53, 0.53);
    -moz-transform-origin: left center;
  }

  .chartlist .love-button
  {
    margin-top: 3px!important;
    zoom: 70%;
    -ms-zoom: 0.7;
    -webkit-zoom: 0.7;
    -moz-transform: scale(0.7, 0.7);
  }

  section#recent-tracks-section .love-button
  {
    margin-left: 15px!important;
  }

/* RECENT TRACKS */
.chartlist .chartlist-row--highlight, .chartlist .now-scrobbling {
    background-color: #fffcca!important;
}

.chartlist-timestamp {
    font-size: 9px!important;
    color: #999!important;
}

.content-form .chartlist td {
    height: 30px!important;
}

/* IMAGE GRIDS */

  .grid-items-item-main-text a
  {
    font-size: 12px!important;
  }

  .grid-items-item-aux-text a
  {
    font-size: 11px!important;
  }
.grid-items-item-aux-text a:last-child {color: #C0C0C0!important}


/*** LIBRARY ***/
h1.content-top-header {
    font-weight: 700!important;
    font-size: 16px!important;}

.h3-4-anhv1 {
    font-weight: 700!important;
    font-size: 12px!important;}

.highcharts-axis-title {font-size: 9px}

.secondary-nav .secondary-nav-item-link {padding-top: 5px}

.content-form .jumpto-form .control-label, .content-form .jumpto-form label,
.content-form .jumpto-form input[type=number] {font-size: 11px;}

.date-heading {
    font-weight: 700!important;
    font-size: 13px!important;}

.delete-catalogue-item-image {
    margin: auto}

.delete-catalogue-item-image img 
{margin: auto;}

h2.library-header-title {font-size: 14px}

/*** LISTENING REPORTS ***/
.listening-report-promo-title {color: #555}
.listening-report-promo-date {font-size: 13px}

/*** SETTINGS ***/
.content-form .form-horizontal .control-label {font-size: 12px}
.content-form .form-control, .content-form input[type=date], .content-form input[type=email], .content-form input[type=number], .content-form input[type=password], .content-form input[type=text], .content-form input[type=url], .content-form select, .content-form textarea {font-size: 12px}

.content-form legend {
    font-size: 12px;
}

/*** NEIGHBOURS ***/

.user-list-link, .user-list-link:focus, .user-list-link:hover {font-size: 12px}


.user-list-shared-artists, .user-list-shared-artists:first-child {
    margin-top: 3px;
}

  .text-light
  {
    color: #777;
  }

  p.user-list-about-me
  {
display: none!important;
  }

/*** LAST WEEK ***/
.metadata-title {font-size: 9px}
.user-dashboard-heading {font-size: 18px;
    line-height: 30px;}

.user-dashboard-date {font-size: 16px; font-weight: 700;
    line-height: 12px;}

.user-dashboard-module-title {font-size: 14px}

.user-dashboard-catalogue-item-percentile-label {font-size: 8.5px;

    line-height: 11px;}

.user-dashboard-catalogue-item-percentile-number {font-size: 19px}
.user-dashboard-catalogue-item .user-dashboard-comparison {font-size: 11px}

.user-dashboard-catalogue-item-total-inner,
.user-dashboard-loved-tracks .user-dashboard-big-datapoint-value a {font-size: 42px; letter-spacing: -0.5px}
.user-dashboard-catalogue-item--artists .user-dashboard-catalogue-item-total-inner  {
    color: #ff7700;
}
.user-dashboard-catalogue-item--albums .user-dashboard-catalogue-item-total-inner{ color:
#008357}

.user-dashboard-catalogue-item--tracks .user-dashboard-catalogue-item-total-inner{ color:
#1f69ff}
.user-dashboard-loved-tracks .user-dashboard-big-datapoint-value a {Color: #cc7e7e}

.discovery-stat-heading, .user-dashboard-discovery-label  {font-size: 8.5px;
    line-height: 11px;}

.user-dashboard-discovery-stats .user-dashboard-discovery-data-point
{font-size: 20px;
    line-height: 15px}


/*** SHOUTBOXES ***/
.shout-user {font-size: 11px}
.shout-timestamp {font-size: 9px;
    color: #C0C0C0}
  .shout-reply
  {
    text-transform: uppercase;
    letter-spacing: .08em;
    font-size: 9px!important;
  }

/*** LISTENING TRENDS ***/
.kerve.widget .widget_title, .kerve.widget h2 {
    font-size: 13px;
    font-weight: 700}

.kerve a.btn_trendnav {font-size: 11px}

.kerve .trendstats span {
    font-size: 12px;
    font-weight: 700}
.kerve .trendglobal, .kerve .trenduser {font-size: 8.5px!important;
letter-spacing: 0.5px}


.kerve .nowslider_imgwrap {width: 60px;
    height: 60px;
top: 15px}

.kerve .nowslider_img {width: 50px;
    height: 50px;
}

.kerve .nowslider_copywrap {max-width: 200px!important}

.kerve .nowslider_name {font-size: 11px;
color: #888; 
line-height: 25px}
.kerve .nowslider_artist {font-size: 11px}
.kerve .nowslider_track {font-size: 12px;
font-weight: 700;
color: #000}

.kerve .nowslider_scrobbles {font-size: 8.5px!important;
letter-spacing: 0.5px;
color: #999}


 

/*** RECOMMENDATIONS PAGE ***/

.h2-anhv1,
  h1.h2
  {
    font-family: "Lucida Grande", Arial, Helvetica, Verdana, sans-serif;
    font-size: 14px!important;
    font-weight: 700;
  }

  .recs-feed .recs-feed-title
  {
    font-size: 9.5pt!important;
  }

  .recs-feed .recs-feed-description
  {
    font-size: 8pt!important;
  }

  .search-field
  {
    font-size: 12px!important;
  }


/*** FOOTER ***/

  h2.footer-heading
  {
    font-size: 12px!IMPORTANT;
    line-height: 18px;
    letter-spacing: 0px!important;
    font-weight: 700!important;
    text-transform: none!important;
  }

  .footer .footer-links
  {
    font-size: 11px!important;
    line-height: 1.5em!important;
  }

  .footer .footer-links a, .footer .mimic-link
  {
    color: #aaa;
  }
/*** MAKE ARTIST IMAGES B&W ***/

  #mantle_skin > div.container.page-content > div > div.col-main > section:nth-child(5) ol
  {
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    filter: grayscale(100%);
  }

/*** MUSIC PAGE ***/
.globalchart-name {font-size: 13px!important}

.globalchart .globalchart-rank   {
color: #d0d0d0!important;
font-weight: 700;
font-size: 16px!important;
letter-spacing: -1px
  }

.sidebar-heading {font-size: 12px;
    line-height: 12px;}

.sidebar-group-heading {font-size: 9px;
    line-height: 8px;}

.album-grid--sidebar .album-grid-item-title {font-size: 14px;
    line-height: 16px}

.album-grid--sidebar p.album-grid-item-artist {margin-top: 30px; line-height: 1em
}
.anhv1 .album-grid--vertical-to-horizontal .album-grid-item-aux-text {font-size: 9px!important}
.album-grid-item-date {text-transform: uppercase; margin-top: 2px; font-size: 8.5px!important}
.anhv1 .album-grid--charts-releases .album-grid-item-aux-text+.album-grid-item-aux-text {
    margin-top: 0px;
}

.anhv1 .album-grid--vertical-to-horizontal .album-grid-item-title {font-size: 12px!important;
    line-height: 1.5em}

.album-grid--vertical-to-horizontal .album-grid-item-wrap {width: 150px;}


.album-grid--vertical-to-horizontal .album-grid-item-wrap {
    margin-top: 10px;
}
.anhv1 .album-grid--vertical-to-horizontal .album-grid-item-aux-text {
    margin-top: 0px;    
}

.anhv1 .album-grid--vertical-to-horizontal .album-grid-item-artist {font-size: 12px!important; margin-top: 0px}

.group-heading {font-size: 12px; font-weight: 700}

.album-grid--vertical-to-horizontal .album-grid-item-date+.album-grid-item-context:before {
    content: "";
    display: block;
    width: 30px;
    height: 1px;
    background: #ccc;
    margin-top: 5px;
    margin-bottom: 3px;
}

/*** NEW RECS PAGE ***/

/* ARTISTS PAGE */
.music-recommended-artists-artist-name {font-size: 12px}
.music-recommended-artists-artist-tags {line-height: 14px; font-size: 9px!important;}
.music-recommended-artists-artist-stats {margin-top: 5px; font-size: 9px!important;}
.music-recommended-artists-artist .factbox-summary  {font-size: 9px!important;}

.factbox-summary+.music-recommended-artists-context, .music-recommended-artists-artist-stats+.music-recommended-artists-context {font-size: 11px!important;}

/* ALBUMS PAGE */
.music-recommended-albums .album-grid-item-title {font-size: 12px}
.music-recommended-albums-album-info-item,
.music-recommended-albums-album-info p {line-height: 14px; font-size: 9px!important;}

/* TRACKS PAGE */

.music-recommended-tracks-track-name {font-size: 11px}

.music-recommended-tracks-track-stats {font-size: 9px!important;
line-height: 10px!important}
.music-recommended-tracks-track-context {font-size: 9px!important;
    font-style: italic;
margin-top: 10px!important;
color: #888;
line-height:14px!important}

}
