body {
    font-family: 'Source Sans Pro', serif;
    font-size: 1.2em;
    
    background: #FFF;
}

/* Links */
a {
	color: #0080C0 !important;
}

a:hover {
    text-decoration: underline;
    color: inherit;
}

a:active {
    text-decoration: none;
    color: inherit;
}

/* Header */
#mw-head {
    background: #FFF !important;
    max-width: 1024px !important;
    
    left: 0 !important;
    
    margin-left: auto;
    margin-right: auto;
    
    padding-bottom: 16px !important;
}

#p-personal li {
    font-size: 0.8em;
    color: #555 !important;
}

#p-personal a {
    color: inherit !important;
}

#left-navigation a, #right-navigation a {
    color: #666 !important;
    font-family: 'Teko';
    font-size: 1.2em;
    font-weight: 300;
    
    padding-top: 0.9em;
}

#left-navigation {
    margin-left: 0;
}

/* Search */
#simpleSearch {
    border: none !important;
    border-bottom: solid 1px #AAA !important;
    
    background: #FFF !important;
    
    max-width: 12em !important;
}

/* Sidebar */

#mw-panel {
    background: #FFF;
}

/* Logo */
.mw-wiki-logo {
    background-image: url(https://upload.wikimedia.org/wikipedia/commons/4/46/Wikipedia-W-visual-balanced.svg);
    background-size: 6em 6em;
    
    margin-top: 38px;
    margin-left: -1.6em;
}

/* Links */
#mw-panel {
    top: 100px !important;
}

#mw-panel h3 {
    font-family: 'Teko' !important;
    font-size: 1.2em !important;
    font-weight: 300 !important;
    
    color: #666 !important;
    
    margin-bottom: -8px !important;
}

.portal {
    background: inherit !important;
    color: #555 !important;
}

.portal a {
    color: inherit !important;
}

.portal h3 {
    font-size: 1em !important;
    font-family: 'Source Sans Pro', sans !important;
    font-weight: normal !important;
}

.portal .body {
    margin-left: 0.25em !important;
    font-size: 1em !important;
}

/* Content */
#content {
    border: none;
    max-width: 1024px;
    
    margin-left: auto;
    margin-right: auto;
    margin-top: 32px;
    
    color: #3c3d3e !important;
}


/* Heading */
#firstHeading {
    font-family: 'PT Serif', serif;
    font-weight: bold;
    font-size: 2.4em;
    
    color: #282828;
    
    text-align: center;
    
    border: none;
    
    margin-bottom: 0.18em;
}

/* From Wikipedia, the free encyclopedia */
#siteSub {
    font-family: 'PT Serif', serif;
    font-weight: normal;
    font-size: 1em;
    
    color: #999;
    
    text-align: center;
}

/* Table of contents */
#toc {
    background: none !important;
    border: none !important;
    
    margin-top: 1.4em !important;
}

#toctitle {
    text-align: left !important;
}

.tocnumber, .toctext {
    color: #3c3d3e !important;
}

/* Headings */

h2{
    font-size: 1.6em !important;
    font-weight: bold;
    font-family: 'PT Serif', serif !important;
    
    color: #383838;
    
    border: none;
}

h3 {
    font-size: 1.2em !important;
    font-weight: bold;
    font-family: 'PT Serif', serif !important;
    
    color: #333;
    
    border: none;
}

/* Some fixes */

/* White background */
#mw-page-base, .infobox, .thumbinner, #ca-nstab-main, #ca-talk, #ca-viewsource, #ca-history {
    background: #FFF !important;
}

/* No blue borders */
