css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2ch.hk"), domain("2ch.pm") {
  html, body{
    background: linear-gradient(rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)), url("http://www.zastavki.com/pictures/originals/2013/Anime___________________________From_Up_On_Poppy_Hill_________________________________048518_.jpg");
    background-size: cover;
    background-attachment: fixed;
    background-color: #414A3E;
    background-position: 100% 100%;
    background-repeat: no-repeat;
    color: #FFFDD0;
    margin: 0;
  }
  
  /*Главная*/
  .welcomefriend, .hdescription{
    color: #FFFDD0;
  }
  .mainlogo{
    background: transparent url("http://images6.fanpop.com/image/photos/38800000/Umi-and-Shun-from-up-on-poppy-hill-38800959-900-1231.png") no-repeat scroll 0px 0px;
    background-size: contain;
  }
  .content{
    margin-left: 10%;
    margin-right: 10%;
    width: auto;
  }
  .postscontainer{
    background: #414A3E none repeat scroll 0% 0%;
    border: 5px solid #597172;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
  }
  .hdescription-stats, #tabbed{
    display: none !important;
  }
  .postscontainer a:hover{
    text-decoration: none;
  }
  .hboardlist .column .boardsubj{
    color: #977B48;
  }
  .news-title{
    border: none !important;
    box-shadow: none !important;
  }
  /**/
  
  /*Dollchan Extension Tools*/
  #de-panel{
    background: #414A3E !important;
    border: 2px solid #597172;
    left: 5px;
    bottom: 5px;
    top: auto !important;
    right: auto !important;
    border-radius: 0px 15px 0px;
  }
  #de-panel-buttons{
    border: none !important;
    width: 100%;
  }
  #de-panel-logo, #de-panel-cfg, #de-panel-hid, #de-panel-fav, #de-panel-vid, #de-panel-refresh, #de-panel-gonext, #de-panel-goup, #de-panel-godown, #de-panel-expimg, #de-panel-maskimg, #de-panel-preimg, #de-panel-catalog, #de-panel-enable{
    color: #FFFDD0 !important;
  }
  .de-abtn{
    color: #FFFDD0;
  }
  .de-parea-btn-thrd, .de-parea-btn-close, .de-parea-btn-reply{
    background-color: #414A3E;
    position: relative;
    margin-left: -50px;
    margin-right: -50px;
    z-index: 2;
    padding: 5px;
    padding-left: 10px;
    padding-right: 10px;
    border: 5px solid #597172 !important;
    color: #FFFDD0;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
  }
  .de-win-body{
    margin-bottom: 15px;
    border: 0px !important;
  }
  .de-win-head{
    margin-bottom: 0px !important;
  }
  .postform{
    background-color: #414A3E;
    padding: 15px !important;
    border: 5px solid #597172 !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
    color: #FFFDD0;
    width: auto !important;
  }
  #de-image-captcha{
    border: 0px !important;
    box-shadow: none !important;
  }
  /*Settings*/
  #de-win-cfg{
    background-color: #414A3E;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
    width: 30%;
    bottom: 25% !important;
    right: 25% !important;
    width: 50% !important;
    border: solid 5px #597172 !important;
    z-index: 3 !important;
  }
  .de-win-head{
    background-color: #414A3E !important;
    border-radius: 0px;
    padding: 5px !important;
    margin-bottom: 5px;
  }
  .de-win-title{
    font-size: 1.1em;
    color: #FFFDD0;
  }
  .de-cfg-body, #de-cfg-buttons{
    background-color: #414A3E !important;
    border: none;
    color: #FFFDD0;
  }
  #de-cfg-bar{
    background-color: #414A3E !important;
  }
  .de-cfg-tab{
    background-color: #414A3E !important;
    padding: 5px !important;
    background-image: none !important;
    border: none !important;
    border-radius: 0px !important;
    color: #FFFDD0;
  }
  .de-cfg-tab[selected], .de-cfg-tab:hover{
    background-color: #414A3E !important;
    background-image: none !important;
    color: #52AAD4;
  }
  .de-menu{
    background: #414A3E !important;
    border: 5px solid #597172 !important;
    color: #FFFDD0;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);    
  }
  .de-menu-item{
    background: #414A3E !important;
    color: #FFFDD0;
  }
  /**/
  /*Popups*/
  .de-popup{
    border: 0px !important;
    border-radius: 0px;
    color: #FFFDD0;
    border: 5px solid #597172 !important;
    background: #414A3E !important;
  }
  #de-wrapper-popup{
    background: #414A3E !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
    right: 1% !important;
    top: 2% !important;
  }
  .de-popup-btn{
    color: #52AAD4;
  }
  /**/
  /**/
  
  /*Доска*/
  
  /*Убрать "Топ тредов"*/
  .boardstats{
    display: none !important;
  }
  /**/
  
  /*Шапка*/
  header, .header{
    background: #414A3E none repeat scroll 0% 0%;
    padding: 10px;
    border: 5px solid #597172 !important;
    text-align: center;
    margin-left: 10%;
    margin-right: 10%;
    margin-bottom: 30px;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);  
  }
  /**/
  
  /*Верхнее меню по центру*/
  #adminbar ul.menu, .menu-links-mob{
    padding: 5px;
    text-align: center !important;
    float: none !important;
  }
  /**/
  
  /*Убрать переключатель стилей и поиск*/
  .search, .style-switcher{
    display: none !important;
  }
  /**/
  
  /*Убирает разделители*/
  hr{
    border-width: 0px;
  }
  /**/
  
  /*Скрыть верхний блок со ссылками*/
  .rmenu{
    display: none;
  }
  /**/
  
  /*Скрыть баннер и название доски*/
  .logo{
    display: none;
  }
  /**/
  
  /*Скрыть описание доски*/
  .top-user-boards{
    display: none;
  }
  /**/
  
  /*Картинки*/
  .preview{
    border: 5px solid #FFFDD0 !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
  }
  img{
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
  }
  .de-img-full{
    width: 100% !important;
  }
  /**/
  /*Видео*/
  video{
    width: 100% !important;
    height: auto !important;
  }
  /**/
  
  /*Треды*/
  .thread{
    margin-left: 10%;
    margin-right: 10%;
  }
  .post-details{
    text-align: left;
  }
  /*Кнопки справа*/
  .de-post-btns{
    float: right !important;
  }
  /**/
  .de-post-counter::after{
    color: #597172;
  }
  .oppost-wrapper, .post-wrapper{
    background-color: #414A3E;
    border: 5px solid #597172;
    padding: 15px;
    color: #FFFDD0;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
  }
  
  /*Отступы*/
  .oppost-wrapper{
    margin-top: 30px;
  }
  .post-wrapper{
    margin-top: 15px;
    margin-bottom: 15px;
  }
  /**/
  
  .reply{
    background: none !important;
    width: 100%;
    border: none;
  }
  .de-pview{
    width: 50% !important;
    background: #414A3E !important;
    border: 5px solid #414A3E !important;
    color: #FFFDD0;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
  }
  .mess-post{
    margin-top: 15px !important;
    margin-bottom: 15px !important;
    text-align: center !important;
    color: #FFFDD0;
  }
  .de-thread-buttons:before{
    display: none !important;
  }
  .de-thread-buttons{
    text-align: center;
  }
  .file-attr{
    color: #597172 !important;
  }
  .ophui{
    color: #79C4D4;
  }
  
  /*Разметка*/
  .unkfunc{
    color: #52AAD4;
  }
  span.spoiler, span.spoiler a{
    color: transparent;
    background: transparent none repeat scroll 0% 0%;
    text-shadow: 0px 3px 3px #3F4E50, 0px 1px 4px #111, 1px -1px 5px #3F4E50;
    transition: all 0.5s ease 0s;
    
  }
  span.spoiler:hover{
    color: #FFFDD0;
    text-shadow: 0px 0px 0px #3F4E50, 0px 0px 0px #111, 0px 0px 0px #3F4E50;
  }
  
  /*Выпил рекламы*/
  .relap-default__block-title, .relap-default__items-container{
    display: none !important;
  }
  
  center{ /*временное решение*/
    display: none !important;
  }
  /**/
  
  .de-thread-buttons{
    margin-top: 20px !important;
    margin-bottom: 20px !important;
  }
  /**/
  
  /*Ссылки*/
  a, a:link, .orange, .a-link-emulator, .reply-label{
    color: #FFFDD0 !important;
    text-decoration: none;
  }
  a:hover{
	  color: #52AAD4 !important;
  }
  /**/
  
  /*Каталог тредов*/
  #threads{
    margin-left: 10%;
    margin-right: 10%;
  }
  .thread_wrap{
    background: #414A3E !important;
    border: 5px solid #597172 !important;
    color: #FFFDD0;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3); 
  }
  .thread, .thread_wrap::after{
    background: none !important;
  }
  .thumb{
    border: 5px solid #FFFDD0 !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
  }
  .centered{
    margin-left: 10%;
    margin-right: 10%;
    text-align: right !important;
  }
  /**/
  
  /*Мобильная версия*/
  .menu{
    background: #414A3E none repeat scroll 0% 0%;
    border: 0px;
    position: relative;
    padding: 5px;
  }
  .button-mob{
    background-color: #414A3E;
    position: relative;
    margin-bottom: 15px;
    z-index: 2;
    padding: 10px;
    color: #FFFDD0;
    border: 5px solid #597172 !important;
    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.3);
    border-radius: 0px !important;
    text-decoration: none !important;
  }
  .post .post-details{
    background: #414A3E none repeat scroll 0% 0%;
    border: 0px;
  }
  .oppost{
    background: #414A3E none repeat scroll 0% 0%;
  }
  .oppost-options-mob{
    background: #414A3E none repeat scroll 0% 0%;
    border: 0px;
  }
  #postform td #submit{
    background: #414A3E none repeat scroll 0% 0%;
    border: 0px;
    color: #FFFDD0;
  }
  .moneymoneymoney{
    display: none;
  }
  /**/
  
}
