@-moz-document domain("csgolounge.com") {
/**CREATED BY YaVOR aka HDDTHRFL**/
/*[ENG]'This topic has been written since the beginning of the hand. Keep this in mind if you want to change and a transfer it into other hands.*/
/*[RUS]'Данная тема написана от всей души и нетерпения. При передаче этого кода с вашими изменениями другим людям проявите уважение к автору, пожалуйста, и укажите в заголовке его никнейм(ы).*/
body {
    /*BACKGROUND IMAGE*/background-image: url("http://puu.sh/nUWSV/288caf4652.jpg") !important;/*Make changes on that line.*/
    background-size: cover !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    font-family: Comfortaa !important;
    text-shadow: #ffffff 0px 0px 10px !important;
    color: #ffffff !important;
}
.box {
    background-size: cover !important;
    background-position: 10% !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important
}
.left,
.right,
.winsorloses,
div.bpheader {
    border: 0px !important;
    background: none !important;
}
.match,
.winnings {
    width: 100%;
    color: rgba(100, 100, 100, 0.4) !important;
    background-color: rgba(100, 100, 100, .03) !important;
    box-shadow: none !important;
    background-image: none !important;
}
.tradeheader,
.matchheader,
.matchmain, 
div.box-shiny {
    box-shadow: none !important;
    border: none !important;
    background-image: none !important;
}
.msgtxt a, 
.clreff {
    padding-left: 0px;
    background: rgba(0, 0, 0, 0) no-repeat 3px !important;
    color: #ff8a00 !important;
    text-shadow: #ff8a00 0px 0px 10px !important;
}
.clreff {
	padding: 0px 2px !important;
}
/*standart text (white)*/
#tradelist a,
.user,
.a,
span.user,
aside a,
a,
a.tab,
.buttonright,
.button,
.name a,
#submenu nav a,
h2 {
    color: #ffffff !important;
    text-shadow: #ffffff 0px 0px 10px !important;
}
div {
    text-shadow: #ffffff 0px 0px 10px !important;
    color: rgba(255, 255, 255, 1) !important;
    background-color: rgba(111, 111, 111, .0) !important;
    border: none !important;
}
div.rarity.Covert,
.rarity,
div#queue,
.slvl {
    background-color: rgba(111, 111, 111, .1) !important;
    box-shadow: none !important;
}
.name {
    background: rgba(255, 255, 255, .3) !important;
}
.item,
.message {
    color: #ffffff !important;
    box-shadow: none !important;
    border: 2px solid rgba(111, 111, 111, .1) !important;
    background: rgba(111, 111, 111, .1) !important;
}
div.matchleft,
div.matchmain,
div.box-shiny-alt,
#satrade, div.tabselect {
    background: rgba(111, 111, 111, .1) !important;
}
div.teamtext {
    background: none !important;
}
aside#submenu, 
tr, 
.value {
	background: none !important;   
}
.won,
span, 
.team {
    color: #adffad !important;
    text-shadow: #adffad 0px 0px 100px !important;
}
.lost, 
.format {
 	color: #ffadad !important;
    text-shadow: #ffadad 0px 0px 10px !important;
}
/*buttons*/
a.tab,
.buttonright,
.button,
#submenu nav a,
.simplePagerNav li a {
    background: linear-gradient(to bottom, rgba(44, 44, 44, 0.2), rgba(44, 44, 44, 0.4) 55%);
	background-color: rgba(44, 44, 44, 0.1) !important;
    box-shadow: 0px 0px 0px  #adffff !important;
}
.selectbox {
 	background: rgba(111, 111, 111, .1) !important;
    box-shadow: none !important;
}
#submenu nav a, 
aside {
 	border-top: 0px solid !important;
    border-bottom: 0px solid !important;
	text-align: center !important;    
}
header {
    background: rgba(44, 44, 44, 0.4) !important;
    text-align: center !important;
}
img {
 	color: #ffffff !important;   
}
.info {
    background: rgba(255, 255, 255, .1) !important;
    font-size: 9px !important;
    box-shadow: 0px 0px 0px  #fff !important;
    border: none !important;
}
div#g2aitem, 
article.standard #betreturns .bpheader,
aside#submenu a img {
    display: none !important;  
}
nav#menu {
	color: rgba(190,190,190,0.3);
}
.tradecnt, #satrade .tradepoll {
    background-image: url("http://i.imgur.com/nlIixe6.png") !important;
    background-position: 50% 39%;
	background-size: 10%;
}
.ember-chat .chat-messages .chat-lines, #ember627.ember-view.chat-display, iframe#chat_embed, .ember-chat .chat-messages .chat-line, ul.chat-lines, .tse-scroll-content, .tse-content, #ember627 {
	background-color: rgba(255, 255, 255, .1) !important;
    color: #adffff !important;
}
}
