css:
@-moz-document domain("twinoid.com") {
/* Dark-Twinoid
*  V1.0.2
* By Soniop
*/
    
/* Général */
/* Fond */
    
body {
    background-color: rgb(17,19,23) !important;
    background-image: none !important;
}
    
#content {
    background-color: rgb(17,19,23) !important;
    background-image: none !important;
}

.tid_twinoidWall .tid_wallEvent table td.tid_content .tid_talkEvent {
    background-color: rgb(20,23,28) !important;
    border: outset rgb(87,89,104) 1px !important;
}
    
.tid_twinoidWall .tid_wallEvent table.tid_comments td.tid_content .tid_post {
    background-color: rgb(20,23,28) !important;
    border: 1px solid rgb(26,26,26) !important;
}

.tid_twinoidWall .tid_form form {
    background-color: rgb(20,23,28) !important;
}

.tid_twinoidWall .tid_form textarea {
    background: rgb(17,19,23) none repeat scroll 0 0;   
    box-shadow: 1px 2px 1px black inset;
    padding-left:2px !important;
}
    
.tid_twinoidWall .tid_newPost table td.tid_content textarea.tid_fake {
    background-color: rgb(17,19,23) !important;
}
    
.tid_questionModule {
    background-color: rgb(17,19,23) !important;
    background-image: none !important;
    border: inset rgb(87,89,104) 1px !important;
}
    
#notification .about, .siteContent .about, #frameContent .about {
    color: rgb(210,210,210) !important;
    text-shadow: none;
}
    
/* Texte */
    
.tid_user.tid_userNoBg.tid_userFriend {
    color: rgb(230,230,230) !important;
}

#notification, .siteContent {
    color: rgb(210,210,210) !important;
}    
    
#notification strong, .siteContent strong {
    color: rgb(190,190,190) !important;
}

.tid_twinoidWall .tid_form textarea {
    color: rgb(230,230,230) !important;
}
    
.tid_questionModule .tid_questionLine .tid_questionText {
    color: rgb(230,230,230) !important;
}
    
.scontainer.nexus .title {
    color: rgb(219,106,3) !important;  
}

#notification a, .siteContent a {
    color: rgb(219,106,3) !important;
    text-shadow: none;
}  
    
#notification h2, .siteContent h2, #frameContent h2 {    
  text-shadow: 0 1px 0 black !important;
}
 
.tid_questionModule .tid_questionLine .tid_questionResults .tid_value {
    color:white !important;
}
    
    
/* Menu */

    
#mainMenu .line {
    background-image: none !important;
    background-color:rgb(189, 61, 0) !important;
    background: -moz-linear-gradient(top, rgb(189,61,0) 0%, rgb(189,61,0) 50%, rgb(17,19,23) 50%, rgb(17,19,23) 100%) !important;
    background: -webkit-linear-gradient(top, rgb(189,61,0) 0%, rgb(189,61,0) 50%,rgb(17,19,23) 50%,rgb(17,19,23) 100%) !important;
    background: linear-gradient(to bottom, rgb(189,61,0) 0%,rgb(189,61,0) 50%,rgb(17,19,23) 50%,rgb(17,19,23) 100%) !important;
}    

#mainMenu {
    background-image: none !important;
}    
    
#mainMenu .scontainer {
    background-image: none !important;
}
    
#mainMenu ul.menu li.selected {
    background-image: none !important;
    background-color:rgb(189, 61, 0) !important;

}
    
#mainMenu ul.menu li.selected a {
    background-image: none !important;
}

/* Panneau Droit */   
    
#tid_bar #tid_sidePanel_user .tid_events a.tid_eventItem.tid_read_false {
    background-color: transparent !important;
    box-shadow: inset 20px 0px 0px -6px rgb(255,121,0) !important;
    -webkit-transition: 1s ease-in-out !important;
    -moz-transition: 1s ease-in-out !important;
    transition: 1s ease-in-out !important;
    -webkit-transition-delay: 0.1s !important;
    -moz-transition-delay: 0.1s !important;
    transition-delay: 0.1s !important;
}

#tid_bar #tid_sidePanel_user .tid_events a.tid_eventItem.tid_read_false:hover {
    background-color: transparent !important;
    box-shadow: inset 350px 0px 0px -6px rgb(255,121,0) !important;
}
   
#tid_bar .tid_topBar .tid_newsNotif, #tid_bar .tid_topBar .tid_userNotif {
   background-color: rgb(255,109,3) !important;
   box-shadow: 0 0 8px rgb(219,109,3) !important;
}
/* Panneau gauche */ 
#tid_bar #tid_sidePanel_twinoid .tid_news .tid_story {
    background-color: transparent !important;
}
 
#tid_bar #tid_sidePanel_twinoid .tid_news .tid_story.tid_read_false {
    background-color: transparent !important;
    box-shadow: inset 20px 0px 0px -6px rgb(255,121,0) !important;
    -webkit-transition: 1s ease-in-out !important;
    -moz-transition: 1s ease-in-out !important;
    transition: 1s ease-in-out !important;
    -webkit-transition-delay: 0.1s !important;
    -moz-transition-delay: 0.1s !important;
    transition-delay: 0.1s !important;  
}
 
#tid_bar #tid_sidePanel_twinoid .tid_news .tid_story.tid_read_false:hover {
    background-color: transparent !important;
    box-shadow: inset 350px 0px 0px -6px rgb(255,121,0) !important;
}
    
/* Footer */
    
#footer {
    background-color: rgb(17,19,23) !important;
    background-image: none;
}


#tid_bar_down {
    background-color: rgb(17,19,23) !important;
}
    
/* Jeux */    
    
#subBar {
    background-color: rgb(17,19,23) !important;
    background-image: none !important;
}
    
#subBar ul.tabs a:hover {
    background-color: rgb(30,30,30) !important;
    color: rgb(210,210,210);
}

.allGames #tagDesc .desc {
    text-shadow: 0 1px 0 black;
}
    
/* Forum */

#tid_forum .tid_forums .tid_group.tid_twinoid {
    background: rgb(5,12,16) !important;
}

#tid_forum .tid_forums .tid_group.tid_misc {
   background: rgb(5,12,16) !important; 
}

#tid_forum .tid_gameForums {
    background: rgb(5,12,16) !important;
}
#tid_forum .tid_bg1 {
    background-color: rgb(20,23,28) !important;
    color: rgb(210,210,210) !important;
}

#tid_forum .tid_bg2 {
    background-color: rgb(20,23,28) !important;
    color: rgb(230,230,230) !important;
}    
    
#tid_forum .tid_bg3 {
    background-color: rgb(17,19,23) !important;
    color: rgb(210,210,210) !important;
}
    
#tid_forum .tid_bg4 {
    background-color: rgb(17,19,23) !important;
}
    
.tid_buttonBar a {
    background-color: rgb(12,15,16) !important;
    border-top: 1px solid rgb(27,29,33) !important;
}
 
.tid_pagination div.tid_cur {
    background-color: rgb(12,15,16) !important;
    border-top: 1px solid rgb(27,29,33) !important;
}
    
.scontainer.forum .tid_editorContent {   
  color: rgb(210,210,210) !important;  
}
    
#tid_forum .tid_forums a {   
    background-color:  rgb(20,23,28) !important;
    border: 1px solid rgb(100,56,0) !important;
    background-image: none !important;
}

#tid_forum .tid_bg2, #tid_group .tid_bg2, .tid_example .tid_bg2 {
    background-color:  rgb(20,23,28) !important;
    border: 1px solid rgb(100,56,0) !important;
    color: rgb(210,210,210) !important;
}

input.tid_field {
    background-color: rgb(17,19,23) !important;
    color: rgb(210,210,210) !important;  
    box-shadow: none !important;
}
    
#tid_forum .tid_postForm textarea {
    background-color: rgb(17,19,23) !important;
    color: rgb(210,210,210) !important;
    border : 1px solid rgb(219,106,3) !important;
    border-radius:5px !important;
}
    
/* Groupes */    
    
#notification .help, .siteContent .help, #frameContent .help {
    background-color: rgb(20,23,28) !important;
    color: rgb(210,210,210) !important;
}

.scontainer.group .members select.role {
  color: rgb(210,210,210) !important;  
}

#tid_group .tid_editpage form textarea { 
    background-color: rgb(17,19,23) !important;
    color: rgb(210,210,210) !important;
    border : 1px solid rgb(219,106,3) !important;
    border-radius:5px !important;
}
    
#tid_group .tid_editpage form input.tid_field {
    background-color: rgb(17,19,23) !important;
    color: rgb(210,210,210) !important;  
    box-shadow: none !important;
}

#tid_forum .tid_bg1, #tid_group .tid_bg1, .tid_example .tid_bg1 {
    background-color: rgb(20,23,28) !important;
}

.scontainer.group .groupHeader .menu a.selected {    
    background-color: rgb(17,19,23) !important;
}
    
/* Copains */     
    
.tid_user.tid_userBg.tid_userFriend {
    background-color: rgb(30,33,38) !important;
}    

#notification table.table th, .siteContent table.table th, #frameContent table.table th { 
  color: rgb(210,210,210) !important; 
}
    
#frameContent.contact .table td.name {
    color: rgb(210,210,210) !important; 
}

#frameContent.contact .table td.friend {    
    color: rgb(219,106,3) !important;  
}
    
    
/* Pas copains */    
 
    
.tid_user.tid_userBg.tid_userUnknown {
  background-color: rgb(94,33,6) !important;  
  border-top: 1px solid rgb(136,47,9) !important;
}
 
/* User */    
    
#notification .leftColumn, #notification .rightColumn, .siteContent .leftColumn, .siteContent .rightColumn, #frameContent .leftColumn, #frameContent .rightColumn {
    box-shadow:none !important;
}
    
.scontainer.userProfile .groups {
    box-shadow:none !important;
}    

.scontainer.userProfile .userDesc {
    background-color: rgb(13,15,19) !important;
    box-shadow:none !important;
}
    

.tid_userGoals .tid_bg2 {
    background-color: rgb(13,15,19) !important;
}
    
.scontainer.userProfile .groups {
    background-color: rgb(13,15,19) !important;
}
    
.tid_userSheet .tid_compact .tid_bgRight {       
   background-color: rgb(13,15,19) !important;   
}
    
#notification strong, .siteContent strong{
   color: rgb(210,210,210) !important;
}

.tid_userSheet table {
    background-color: rgb(13,15,19) !important;  
}


.tid_userSheet .tid_footer {
    background-color: rgb(13, 15, 19) !important;
    background-image: none !important;
}

.tid_userSheet .tid_footer .tid_footerRight {
    background-image: none !important;
}

.tid_userSheet .tid_userSheetBg {
    background-image:none !important;
    background-color: rgb(13, 15, 19) !important;
    border: 1px solid rgb(150,150,150) !important;
}   
    
.tid_userSheet .tid_userSheetBg .tid_bgRight {
    background-image:none !important;
}
    
.tid_userSheet .tid_header {
    background-color: rgb(20,23,28) !important;
    background-image: none !important;
}

.tid_userSheet .tid_header .tid_headerRight {
    background-image: none !important;
}

.tid_avatarImg {
    -webkit-transition: 1s ease-in-out !important;
    -moz-transition: 1s ease-in-out !important;
    transition: 1s ease-in-out !important;
    -webkit-transition-delay: 1s !important;
    -moz-transition-delay: 1s !important;
    transition-delay: 1s !important;
}    
    
.tid_avatarImg:hover {    
    transform: rotate(360deg) !important;
}
      
.scontainer.userProfile .profileLinks span {
    text-shadow: none !important;
}
/* Titre */   
    
#frameContent.userTitle .block      {
    background-color: rgb(20,23,28) !important; 
}
    
#frameContent.userTitle .block .title {    
    color: rgb(210,210,210) !important;
}

#frameContent.userTitle .block .prevTitle {
    color: rgb(210,210,210) !important;
}
    
    
/* Réglages */  
    
.scontainer.account .block {
    background-color: rgb(17,19,23) !important;
    background-image: none !important;
    border: 1px solid rgb(150,150,150) !important;
    border-radius: 5px !important;
    box-shadow: none !important;
}
    
.scontainer.account .blockMenu {
    border: 1px solid rgb(150,150,150) !important;
    border-radius: 5px !important;
    box-shadow: none !important;
}
    
.scontainer.account .blockMenu a.active {
    background-color: rgb(17,19,23) !important;
    box-shadow: 3px 0 5px rgb(189, 61, 0) inset !important;
}
 
.scontainer.account .blockMenu a:hover {    
    background-color: rgb(17,19,23) !important;
    box-shadow: 3px 0 5px rgb(40, 43, 48) inset !important;
}

.scontainer.account label.radio {
    background-color: rgb(15,16,20) !important;
    border: 1px dashed rgb(100,100,100) !important;
}

form .row textarea {
    background-color: rgb(17,19,23) !important;
    color: rgb(210,210,210) !important;
}
    
.scontainer.account .block.oauth .service {
    background-color: rgb(17,19,23) !important;
}    

/* Raccourcis */
.scontainer.customizeBar ul.preview {
    background-color: rgb(17,19,23) !important;
    border: 1px solid rgb(150,150,150) !important;
    border-radius: 5px !important;
    box-shadow: none !important;  
}

.scontainer.customizeBar .sortableContainer.selection { 
    background-color: rgb(17,19,23) !important;
    border: 1px solid rgb(150,150,150) !important;
    border-radius: 5px !important;
    box-shadow: none !important;    
}
  

.scontainer.customizeBar .sortableContainer.stock {
    background-color: rgb(17,19,23) !important;
    border: 1px solid rgb(150,150,150) !important;
    border-radius: 5px !important;
    box-shadow: none !important;      
}
    
.scontainer.customizeBar .table td.links a.link {
    background-color: rgb(27,29,33) !important;
}
    
#notification h1, .siteContent h1, #frameContent h1 {
    text-shadow: none !important;
    color: rgb(219,106,3) !important;
}
    
/* Mp */
    
#frameContent {
    background-image: none !important;
    background-color: rgb(20,23,28) !important;
}
    
#frameContent.discuss td.threadsPanel table.lcontainer td.main .subject a {
    color: rgb(210,210,210) !important;
}
    
#frameContent.discuss td.threadsPanel table.lcontainer td.avatar, #frameContent.discuss td.threadsPanel table.lcontainer td.main {
    border-bottom: 1px solid rgb(189, 61, 0) !important;
}
    
#frameContent.discuss td.threadsPanel table.lcontainer tr.selected {
    background-color: rgb(20,23,28) !important;
    box-shadow: 0 0 7px rgb(189, 61, 0) inset !important;
}
    
#frameContent.discuss #discussSearch {
    background-color: rgb(23,27,33) !important;
    border-bottom: 1px solid rgb(17,19,23) !important;
    box-shadow: none !important;
}
    
#frameContent.discuss td.threadsPanel .menu .tabs a.tab.active {
    background-color: rgb(17,19,23) !important;
} 
    
#frameContent.discuss td.threadsPanel .threadsWrapper {
    border: 1px solid rgb(17,19,23) !important;
}
    
#frameContent.discuss td.threadsPanel .menu .tabs {
    border-bottom: 2px solid rgb(17,19,23) !important;
}

#frameContent.discuss td.msgPanel #myContacts {
    background-color: rgb(17,19,23) !important;
    border-left: 1px solid white !important;
}    
    
form .row label {
    color: rgb(210,210,210) !important;
    text-shadow:0 1px 1px black !important;
}
    
#frameContent.discuss td.msgPanel form textarea {
    background-color: rgb(17,19,23) !important;
    border : 1px inset black !important;
    color: rgb(210,210,210) !important;
}
    
#tid_popup .tid_black {
    background-color: rgb(17,19,23) !important;
}
    
#frameContent.discuss td.msgPanel ul.participants {
    background: rgb(30,33,38) none repeat scroll 0 0 !important;    
}

#frameContent.discuss td.msgPanel ul.participants li.label {
    text-shadow: none !important;
}

form .row input.field {
    background-color: rgb(17,19,23) !important;
    border : 1px inset black !important;
    color: rgb(210,210,210) !important; 
    box-shadow: none !important;
}  
  
#frameContent.discuss #discussSearch input.field {
    background-color: rgb(17,19,23) !important;
    border: 1px solid black !important;
    box-shadow: none !important;
    color: rgb(210,210,210) !important; 
}
      
.tid_editorContent .tid_sideNote {
    color: rgb(210,210,210) !important;
}

#frameContent.discuss td.msgPanel .discussMsg {
    background-color: rgb(17,19,23) !important;
    box-shadow: none !important;
    border : 1px solid rgb(219,106,3) !important;
}        

.tid_editorContent .tid_messageTitle {    
    color: rgb(210,210,210) !important;
}
 
#frameContent.discuss td.msgPanel .discussMsg p {
    color: rgb(210,210,210) !important;
}

#frameContent.discuss td.msgPanel #topShadow {
    border-bottom: 1px solid rgb(219,106,3) !important;
    box-shadow: 0 1px 1px rgb(219,106,3) !important;
}

#frameContent.discuss td.msgPanel .discussMsg .date {    
    color: rgb(210,210,210)
}
    
/* Nexus Cleaner */
    
#subBar ul.tabs li.selected a {
    background-color: rgb(30,30,30) !important;
} 

#frameContent .frameFooter .logo {
    text-shadow: none;
}
 
#notification p, .siteContent p, #frameContent p {
    color: white;
}
    
}
