css:
@-moz-document domain("crownofthegods.com") {
@font-face {
font-family:FontAwesome;
src:url(https://maxcdn.bootstrapcdn.com/font-awesome/4.5.0/fonts/fontawesome-webfont.eot?#iefix&v=4.5.0) format(embedded-opentype),url(https://maxcdn.bootstrapcdn.com/font-awesome/4.5.0/fonts/fontawesome-webfont.woff2?v=4.5.0) format(woff2),url(https://maxcdn.bootstrapcdn.com/font-awesome/4.5.0/fonts/fontawesome-webfont.woff?v=4.5.0) format(woff),url(https://maxcdn.bootstrapcdn.com/font-awesome/4.5.0/fonts/fontawesome-webfont.ttf?v=4.5.0) format(truetype),url(https://maxcdn.bootstrapcdn.com/font-awesome/4.5.0/fonts/fontawesome-webfont.svg?v=4.5.0#fontawesomeregular) format(svg);
font-weight:400;
font-style:normal;
}
.fa {
    display: inline-block;
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale
}
* {
    font-family: 'Arial', sans-serif;
    -webkit-font-smoothing: antialiased;
}
.greenb,
.greenpb,
.greensel,
#cityDropdownMenu-button {
    background: rgb(112, 163, 187);
    background: -moz-linear-gradient(top, rgba(112, 163, 187, 1) 0%, rgba(56, 108, 132, 1) 100%);
    background: -webkit-linear-gradient(top, rgba(112, 163, 187, 1) 0%, rgba(56, 108, 132, 1) 100%);
    background: linear-gradient(to bottom, rgba(112, 163, 187, 1) 0%, rgba(56, 108, 132, 1) 100%);

    text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.3);
    border-radius: 4px;
    box-shadow: inset 0px -2px 5px rgba(0, 0, 0, 0.15);
}
.greenb:hover,
.greenpb:hover {
    cursor: pointer;
    background: rgb(107, 174, 205);
    background: -moz-linear-gradient(top, rgba(107, 174, 205, 1) 0%, rgba(49, 126, 161, 1) 100%);
    background: -webkit-linear-gradient(top, rgba(107, 174, 205, 1) 0%, rgba(49, 126, 161, 1) 100%);
    background: linear-gradient(to bottom, rgba(107, 174, 205, 1) 0%, rgba(49, 126, 161, 1) 100%);
    border-radius: 4px;
}
#buildQueueWindow,
#recruitmentQueueWindow,
#commandWindow,
#reinforcementsWindow,
#tradesWindow,
#defensesWindow {
    border: 1px rgba(0, 0, 0, 0.3) solid;
    border-radius: 0px 0px 4px 4px;
    padding-top: 5px;
}
#incomingRes,
#buildqbar,
#enliqbar,
#commqbar,
#reinfqbar,
#tradesqbar,
#defenqbar {
    background-image: none;
    background-color: rgba(0, 0, 0, 0.8);
    border-radius: 4px 4px 0px 0px;
    border: 1px solid rgba(0, 0, 0, 0.9);
}
#toolbar {
    background-image: url(http://i.imgur.com/d26s85y.jpg);
}
#topPageDiv {
    padding: 5px 0px 5px 0px !important;
    box-sizing: padding-box;
}
#canvasborders {
    top: 74px;
}
#toolbar {
    top: 10px;
}
/* Let's get this party started */

::-webkit-scrollbar {
    width: 12px;
}
/* Track */

::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    -webkit-border-radius: 10px;
    border-radius: 10px;
}
/* Handle */

::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 10px;
    background: rgba(255, 255, 255, 0.8);
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.5);
}
::-webkit-scrollbar-thumb:window-inactive {
    background: rgba(255, 255, 255, 0.4);
    background-image: none;
}
#chatUpExpand,
#chatRightExpand,
#chatMin {
    width: 30px;
    overflow: hidden;
    color: rgba(255, 255, 255, 0) !important;
    text-shadow: none;
}
#chatRightExpand {
    right: 0;
}
#chatUpExpand {
    right: 35px;
}
#chatMin {
    right: 70px;
}
#chatRightExpand,
#chatUpExpand,
#chatMin,
#centxbuttondiv {
    display: inline-block;
    font: normal normal normal 14px/1 FontAwesome;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    content: none;
    font-size: 14px;
    text-align: center;
        color: rgba(255, 255, 255, 0) !important;
    text-shadow: none;
}
#mapchange {
    border-radius: 4px;
    padding-bottom: 5px;
}
#mapchange tr td {
    padding-bottom: 5px;
}
#chatRightExpand:before {
    content: "\f07e";
    color: white;
    line-height: 17px;
    margin-left: -2px;
}
#chatUpExpand:before {
    content: "\f07d";
    color: white;
    line-height: 17px;
    text-align: center;
    padding-left: 3px
}
#chatMin:before {
    content: "\f141";
    color: white;
    line-height: 17px;
    text-align: center;
}
#quickBuildMode, #cityNotesGo {
    width: 30.4%;
}
.tooltipster-base {box-shadow:0px 5px 10px rgba(0,0,0,0.6); border:none; padding:10px; background:rgba(0,0,0,0.9); text-shadow:0px 1px 3px rgba(0,0,0,0.4); }
#centxbuttondiv {background:none;margin-top:-15px;
}
#xbuttondiv > div {top:10px;text-align:center; left:14px}
#centxbuttondiv:before {
    content: "\f00d ";
    color: white;
    text-align: center;
}
#xcoord,
#ycoord {
    border-radius: 4px;
    text-align: center;
    border: none;
}
}
