/*AGENT_SHEET*/ 

/*--------------------------
Name: Compact Scrollbar
Author: João
License: CC BY-NC-ND
--------------------------*/

@-moz-document url-prefix("http://"), url-prefix("https://"), url-prefix("ftp://"), url-prefix("file:"), url-prefix("about:reader")
{html scrollbar[orient="vertical"] {-moz-margin-start: -9px !important}
html scrollbar[orient="horizontal"] {margin-top: -9px !important}}
@-moz-document url("about:addons"), url("chrome://mozapps/content/extensions/extensions.xul")
{.addon-view[type="userstyle"][name="Compact Scrollbar"] .description-container:before {content: "Slim and transparent scrollbar" !important; font-size: 13px !important; font-weight: bold !important; position: relative !important; top: 1px !important}
.addon-view[type="userstyle"][name="Compact Scrollbar"] .description {display: none !important}
.addon-view[type="userstyle"][name="Compact Scrollbar"] .details.button-link .button-text {visibility: collapse !important}
.addon-view[type="userstyle"][name="Compact Scrollbar"] .details.button-link:after {content: "Web page and update manager" !important; font-weight: bold !important}}
select scrollbar[orient="vertical"], menulist scrollbar[orient="vertical"] {-moz-margin-start: 0 !important; min-width: 11px !important; max-width: 11px !important; padding-left: 1px !important}
select scrollbar[orient="horizontal"], menulist scrollbar[orient="horizontal"] {margin-top: 0 !important; min-height: 11px !important; max-height: 11px !important; padding-top: 1px !important}
menulist dropmarker {margin-left: -1px !important}
scrollbar scrollbarbutton {display: none !important}
scrollbar {-moz-appearance: none !important; position: relative !important; background-color: transparent !important; background-image: none !important; border: 0 !important; z-index: 2147483647 !important; pointer-events: none !important}
scrollbar[orient="vertical"] {min-width: 9px !important; max-width: 9px !important; padding: 1px 0 !important}
scrollbar[orient="horizontal"] {min-height: 9px !important; max-height: 9px !important; padding: 0 1px !important}
scrollbar thumb {-moz-appearance: none !important; border-radius: 4px !important; border: none !important; pointer-events: auto !important}
scrollbar[orient="vertical"] thumb {min-height: 25px !important; min-width: 9px !important; max-width: 9px !important; background: -moz-linear-gradient(to right,rgba(220,220,220,0.7), rgba(30,30,30,0.5), rgba(220,220,220,0.7)) !important}
scrollbar[orient="horizontal"] thumb {min-width: 25px !important; min-height: 9px !important; max-height: 9px !important; background: -moz-linear-gradient(to bottom,rgba(220,220,220,0.7), rgba(30,30,30,0.5), rgba(220,220,220,0.7)) !important}
scrollbar[orient="vertical"]:hover thumb {background: -moz-linear-gradient(to right,rgba(180,180,180,0.7), rgba(30,30,30,0.5), rgba(180,180,180,0.7)) !important}
scrollbar[orient="horizontal"]:hover thumb {background: -moz-linear-gradient(to bottom,rgba(180,180,180,0.7), rgba(30,30,30,0.5), rgba(180,180,180,0.7)) !important}
