css:
@-moz-document domain("wuxiaworld.com") {
body {background: none !important;}
body {background-color: #282828 !important;}
.sp-body {background: none !important;background-color: #282828 !important;}
.stacked-list {background: none !important;background-color: #282828 !important;}
.stacked-list{padding:0;margin:0.5rem 0 1rem 0;border:1px solid #C4B88A;border-radius:2px;overflow:hidden;position:relative;}
.stacked-list-item {background: none !important;background-color: #282828 !important;}
.stacked-list-item:hover {background: none !important;background-color: #C4B88A !important;}
.stacked-list-item.collapsible {background: none !important;background-color: #282828 !important;}
.stacked-list-item .collapsible-header{background-color: #282828 !important;}
a {color: #C9C4C4;}
a:hover {color: #C4B88A;}
article {background-color: #3c3c3c !important;color: #afafaf;font-size: 1rem !important;width: 100% !important;}
object {display: none;}
#button {background: rgb(60, 60, 60) !important;}
#comments {background-color: #282828 !important; color: #afafaf; width: 100%;border:1px solid #C4B88A !important;}
#primary {width: 72%}
#secondary {width: 26%;/*display:none; */}
#site-navigation {background: none !important;}
#site-navigation {background-color: #3c3c3c !important;}
.widget {background-color: #3c3c3c !important; color: #afafaf; width: 100%;}
.sp-wrap{background: none; background-color: #3c3c3c; border-color: #C9C4C4;}
}
