css:
@-moz-document url-prefix("https://blockchain.info/") {
a {background:none !important;background-color:#282828 !important;}
h1 {background:none !important;background-color:#282828 !important;}
body, html :after, html :before {background:none !important;background-color:#282828 !important;}
body {color: #BDBDBD !important;}
.breadcrumb, footer{position:inherit;max-width:0%;padding:0px;}
div {background:transparent !important; background-color:transparent !important;}
div .navbar{background:none !important;background-color:transparent !important;}
.navbar div {background:none !important;background-color:#383838 !important;}
.logo {background:none !important;background-color:#383838 !important;}
img {max-width: 0%}
div img {max-width:100;}
div.modal-header {background:282828}
.zeroblock a{color:#0882BC !important;}
.table {background:none !important;background-color:transparent !important;}
.table-striped tbody tr:nth-child(odd) th{background:none !important;background-color:#282828 !important}
.table-striped tbody tr:nth-child(odd) td{background:none !important;background-color:#282828 !important}
.skin-slidedeck {background:none;border:0px !important;}
.skin-slidedeck dl.slidedeck > dd {background:none;border:0px !important;}
.navbar .container {background:none !important;background-color:#383838 !important;}
.navbar-inner {background:none !important;background-color:#383838 !important;}
button.btn.download-backup-btn,button.btn.paper-wallet-btn,button#dropbox-backup-btn,button#email-backup-btn,button#gdrive-backup-btn,button.send,button#my-account-btn,button#show-import-export,button#new-addr,a.btn,button.btn.btn-secondary,button.btn.btn-primary,a.btn.dropdown-toggle{background:none;background-color:#191919;text-shadow:none;color:#0882BC}
a.btn:hover{color:grey}
u1.dropdown-menu{color:#fff}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {background:none}
.navbar .nav>li>a,li.active{color:#0882BC}
}
