css:
@-moz-document domain("dnt.abine.com") {
.header-notification {visibility:hidden}
input#global-header-search.form-control {color:#ffffff;background-color:#282828}
th.sortable.renderable{background:none;background-color:transparent}
input#appendedInputButton-04 {color:#ffffff;background-color:#282828}
.form-control+.input-icon{background-color:transparent}
#mobileHeaderBtn:hover{background-color:#333333}
#country-code{background:none}
#country-code.form-control{color:#54585C;}
#main-region.container{margin-left:180px}
#email-input{background:none}
#phone_number {background:none}
#dashboard-icons-region.full .dashboard-icon-cnt {margin:23px}
.btn.btn-primary:active, .btn-group.open .btn.btn-primary.dropdown-toggle, .btn.btn-primary.active {background-color:#383838;}
.dropdown-menu li>a {background-color:#383838;color:#fff}
.dropdown-menu li>a:hover{background-color:#282828}
.navbar.navbar-inverse .nav li.dropdown.open>.dropdown-toggle {background-color:transparent;}
.fui-plus:before,.fui-question:before,.fui-new:before,.fui-heart:before,.fui-lock:before,.fui-gear:before,.fui-mic:before{display:none}
.btn.btn-primary:hover, .btn.btn-primary:focus, .btn-group:focus .btn.btn-primary.dropdown-toggle {background-color:#282828;background:70%}
body,html :after,html :before,html,a,a:hover,b {background:none; background-color:#282828;}
body {color:#aaa;}
.navbar-inverse,.navbar-inner,.home-register .navbar-inner {background:none; background-color:#282828;}
div {background:none; background-color:transparent;}
.btn.btn-primary {background-color:#383838;}
element.stlye {background:white}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {background-color:#383838}
.panel-default>.panel-heading {background:none;background-color:#282828;}
div.panel-body {background:none;background-color:#282828;}
a {color:#ffffff}
button#change-email {background-color:#E74C3C}
button#new-account {background-color:#E74C3C}
button#email-change {background-color:#E74C3C}
button#change-phone {background-color:#E74C3C}
button#phone-change {background-color:#E74C3C}
.navbar .nav li.dropdown .dropdown-menu>li>a {color:#ffffff;}
button#deactivate-card-button {background-color:#E74C3C}
.navbar-inverse .navbar-inner, .home-register .navbar-inner {background:#282828;}
.navbar-inverse .navbar-brand {border-bottom:0px}
#main-region.container {background:none;border:0px}
.backgrid thead th {background:transparent;}
#breadcrumb-region,#breadcrumb,breadcrumb,.breadcrumb,container,.container{background:none; background-color:#282828;}
.row{background:none; background-color:#282828;}
region.container{background:none;background-color:#282828;}
h1,h2,h3,h4,h5,h6,h7,h8,h9 {background:none; background-color:#282828;}
}
