/*Image Zoom*/
img{-webkit-transition: all 0.3s ease-in-out!important;-moz-transition: all 0.3s ease-in-out!important;-ms-transition: all 0.3s ease-in-out!important;-o-transition: all 0.3s ease-in-out!important;transition: all 0.3s ease-in-out!important;}  
img:hover{transform: scale(1.1)!important; -ms-transform: scale(1.1)!important; -moz-transform: scale(1.1)!important; -webkit-transform: scale(1.1)!important; -o-transform: scale(1.1)!important; }

.upload-active-widget {width: 100% !important;}


::-webkit-scrollbar {height: 12px!important;width: 12px!important;background: -webkit-linear-gradient(40deg , #000000 , #1e1e1e , #000000 100%)!important;}
::-webkit-scrollbar-thumb {background-color: #840000!important;border-radius: 16px!important;}



