css:
@-moz-document regexp("(|http(|s)://)(|www.)m(|.*.)delfi\.ee.*") {
  body {
    background: #222 !important;
  }
  .dweather-mobile-widget, .md-specialblock-id-mobile_schedule, .dgsee-nav, .md-footer {
    display: none !important;
  }
  .dgsee-gallery .dgsee-highlight {
    background: #4C9E00 none repeat scroll 0% 0%;
    border: 0px solid #FFF;
    border-radius: 0px;
}
  .article .md-picture-captions, .article-authors {
    border: 0px !important;
  }
.dgsee-ad-frame{
    background-color: #000 !important;
  }
  .md-mosaic, .dgsee-gallery .dgsee-sidebar-right, .dgsee-sidebar-right .dgsee-gallery-meta, .comment-add .comment-add-footer {
    background-color: #333 !important;
  }
  #md-wrapper {
    background: none !important;
    max-width: 800px !important;
  }
  .comment-add .comment-add-footer {
    border: 0px !important;
  }
  div {
    color: #fff !important;
  }
  #comments-listing div.comment-author h3 {
    color: #ddd !important;
  }
  #comment-list-header .comment-list-header-right_, #comment-list-header .comment-list-header-sort-a {
    background: none !important;
    border-bottom: 2px solid #ddd !important;
    border-top: none !important;
  }
  .comment-quote-who {
    color: #ddd !important;
  } 
  #md-header-wrapper, .comments-about-disclaimer, .md-block-rannainfo, .m-horo, .md-banner-container, .md-banner-placement, .delfifptv_stream {
    display: none !important;
  }
  .md-article-tags, #comment-post-add-form, .thread-pointer-form, .thread-pointer, .upcoming-stream-times:nth-child(2n) {
    background: none !important;
  }
  #wrapper, #comment-post-add-form, .comment-form-wrapper {
    padding: 10px 0px 0px !important;
    background-color: #29292C !important;
  }
  #comments-list li:nth-child(2n+2), #comments-list .comment-list-item fieldset {
    background-color: #39393C !important;
  }
  .quote {
    background: none !important;  
  }
  .md-responsive-img:hover {
    filter: saturate(1.5) !important;
  }
  .md-responsive-img {
    transition: filter 0.2s ease 0s !important;
  }
  .md-mosaic-img {
    background: #222 !important;
  }
  .quote:before {
    content: "" !important;
  }
  a, .article-author-name, .md-article-tags-title, .comment-post-name, .md-article-bottom-headlines-title, #comments-listing, .comment-body {
    color: #ddd !important;
    transition: color 0.2s ease 0s !important;
    font-size: 18px !important;
  }
  a:hover {
    color: #607D8B !important;
  }
  p {
    color: #ddd !important;
    font-size: 15px !important;
  }
  h1 {
    color: #ddd !important;
    font-size: 20px !important;
  }
  .md-specialblock {
    background: none !important;
  }
  .md-specialblock-bottom {
    background: none !important;
  }
  .article-lead {
    font-size: 18px !important;
    font-weight: 800 !important;
  }
  .article-date, .articleSource {
    color: #DDD !important;
  }
  .md-block-title {
    background: #37474F !important;
  }
  .comment-button, #comments-listing, .comment-list-back-link {
    background: #666 !important;
    border: none !important;
  }
  #comment-post-content_, #comment-post-subject_ {
    background: #555 !important;
    border: 1px solid #777 !important;
  }
  #comment-post-add-form, #comments-listing, #comment-post-btn {
    color: #ccc;
}
  .md-picture-smallcaption, .md-related-articles-title {
    color: #ddd !important;
  }
  #comment-post-header, #comments-listing, .comment-list-paging {
    background: none !important;
    color: #ccc !important;
  }
  .md-footer-big-arrow {
    background: #4D4D4D url("http://g2.nh.ee/misc/crs/mobile/img/footer_bigarrow.png") no-repeat scroll 50% 0px !important;
  }
  #comments-listing .comment-list-el-votes > a, #comments-listing .comment-list-el-votes > span {
    border: 1px solid #666 !important;
  }
}
