css:
@-moz-document domain("play.pokemonshowdown.com"),  domain("replay.pokemonshowdown.com") {

body{
    background:url('http://cdn.desktopwallpapers4.me/wallpapers/anime/1920x1080/2/19841-absol-pokemon-1920x1080-anime-wallpaper.jpg')!important;
    background:cover !important;
}
.button {
    background: rgba(0, 0, 0, .6)!important;
    border: solid 1px black!important;
    text-shadow:none;
    color:white;
}
.maintabbarbottom {
    background: rgba(0, 0, 0, .6)!important;
}
.userlist {
    background: rgba(0, 0, 0, .6)!important;
}

.pm-window h3 {
    background: rgba(0, 0, 0, .3)!important;;
    color: white;
    text-align:center;
}
.pm-window.focused h3 {
    background: rgba(0, 0, 0, .6)!important;;
}
.pm-log-add {
    background: rgba(0, 0, 0, .2)!important;;
}
.pm-log {
    background: rgba(0, 0, 0, .6)!important;;
    color:white;
}
.chat-log {
    background: rgba(0, 0, 0, .6)!important;
    color:white;
}
.chat-log-add {
    background: rgba(0, 0, 0, .3)!important;
}

  .broadcast-blue {
    background-color: rgba(0, 0, 0, .6)!important;;
    border: 1px solid black;
    color: white;
    text-align: center;
  }
 
  .broadcast-blue a {
    color: white;
  }
 
  .broadcast-red {
    background-color:rgba(0, 0, 0, .6)!important;
    border: 1px solid black;
    color: white;
    text-align: center;
  }
 
  .broadcast-red a {
    color: white;
  }
 
  .broadcast-green {
    background-color: rgba(0, 0, 0, .6)!important;
    border: 1px solid black;
    color: white;
    text-align: center;
  }
 
  .broadcast-green a {
    color: white;
  }

.battle-log{
    background:rgba(0, 0, 0, .6)!important;
    color:white;
}

.battle-log h2{
    background:black;
    color:white;
}

.battle-log-add{
    background:black;
    color:white;
}
.ps-room.ps-room-opaque{
     background:rgba(0, 0, 0, .8)!important;
    color:white;
}

.ps-room .battle-controls{
 background:transparent;
    text-align:center;
    color:white;
}

.battle-controls .whatdo{
    color:white;
}

.rightbar, .leftbar{
background:rgba(0, 0, 0,.6)!important;
}

 button {
    border: 2px black solid;
    background-color: rgba(0, 0, 0, .6)!important;;
    color: white;
} 
  .backdrop {
    background: transparent url("http://theartmad.com/wp-content/uploads/2015/04/Dark-Forest-3.jpg") !important;
    background-size:cover;                   
   opacity: 0.9;
  } 

.teampane{
    background:rgba(0, 0, 0, .6)!important;
}

.teamlist .team{
    background:black;
    color:white;
}

.select, .team{
    background:rgba(0, 0, 0, .6)!important;
    color:white;
}

.ps-popup {
    background:rgba(0, 0, 0, .6)!important;
    color:white;
}

.popupmenu button{
    background:transparent;
    color:white;
}

.roomlist a.ilink{
    background:rgba(0,0,0,.3)!important;
    color:white;
    text-shadow:none;
    text-align:center;
}

.pad {
    background:rgba(0, 0, 0, .6)!important;
}

.closebutton{
    background:transparent;
}
}
