css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("foro.clubjapo.com") {
  body {
    background-color: #093d83;
  }
  html {
    color: black;
  }
  ::selection {
    background: #002454;
    color: #f5f7fa;
  }
  ::-moz-selection {
    background: #002454;
    color: #f5f7fa;
  }
  /* Barra Superior */
  .d-header {
    background-color: #0c3770;
  }
  /* ACTIVO Iconos Barra Superior */
  .d-header .icons .icon:hover {
   background: #f5f7fa;
    color: #093d83;
  }
  /* Texto Resultados */
  .fps-result .blurb {
   color: #cddaeb;
  }
  /* Categorías, Recientes, Nuevos... */
  .nav-pills > li > a {
    color: #cddaeb;
  }
  /* ACTIVO Categorías, Recientes, Nuevos... */
  .nav-pills > li > a:hover {
    background-color: #f5f7fa;
    color: #093d83;
  }
  /* ACTIVO Categorías, Recientes, Nuevos... */
  .nav-pills > li.active > a, .nav-pills > li > a.active {
    background-color: #206bd0;
    color: #f5f7fa;
  }
  /* Título de las Categorías */
  .topic-list.categories .category h3 a[href] {
    color: #f5f7fa;
  }
  /* Título de las Categorías */
  .topic-list.categories .category h3 a[href]:hover {
    text-shadow: 2px 2px #003330;
  }
  /* Descripción de las Categorías */
  .topic-list td {
    color: #cddaeb;
    font-size: 13px;
  }
  /* Cajón de las Categorías */
  tbody .category {
    background-color: #0c3770;
  }
  /* Estadísticas (Temas) de las Categorías */
  .stats,.topics {
    display:none;
  }
  /* Ocultar Categorías, Recientes */
  .topic-list thead {
    display:none;
  }
  /* Títulos Temas Recientes */
  .topic-list.categories a.title {
    color: #f5f7fa;
    font-size: 0.929em;
    font-weight: bold;
  }
  /* ACTIVO Títulos Temas Recientes */
  .topic-list.categories a.title:hover {
    color: #f5f7fa;
    text-shadow: 2px 2px #003330;
  }
  /* Temas Recientes posición */
  .topic-list.categories td.latest {
    padding: 0 8px 8px;
    text-align: right;
  }
  /* Ocultar Chinchetas Temas Recientes */
  .topic-list.categories td.latest .topic-statuses {
      display: none;
  }
  /* Chincheta */
  .topic-list .fa-thumb-tack::before, .topic-list .fa-lock::before, .topic-list .fa-eye-slash::before {
    color: #206bd0;
  }
  /* Fecha Temas Recientes */
  .topic-list .with-year, .topic-list .with-year:visited,.topic-list.categories .featured-topic a.last-posted-at, .topic-list.categories .featured-topic a.last-posted-at:visited {
    color: #cddaeb;
    font-style: italic;
  }
  /* Fecha dentro de la Categoría */
  body .coldmap-high, body .coldmap-low, body .coldmap-med {
    color: #cddaeb !important;
  }
  /* Número de posts en Temas calientes */
  .heatmap-low a, body .heatmap-low, .heatmap-low, body .heatmap-high, .heatmap-med, body .heatmap-med {
      color: #f5f7fa !important;
  }
  /* Botones */
  .btn {
    background: #206bd0 none repeat scroll 0 0;
    color: #f5f7fa;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
  }
  /* ACTIVO Botones */
  .btn:hover {
    background: #f5f7fa none repeat scroll 0 0;
    color: #093d83;
  }
  /* Ocultar desplegable Todas las Categorías */
  .ember-view .category-breadcrumb {
    display: none;
  }
  /* Tema con Respuestas Nuevas */
  .topic-list a.title {
    color: #f5f7fa;
    font-weight: bold;
    font-size: 14px;
  }
  /* ACTIVO Tema con Respuestas Nuevas */
  .topic-list a.title:hover {
    text-shadow: 2px 2px #003330;
  }
  /* Tema ya leído */
  .topic-list a.title.visited:not(.badge-notification) {
    color: #cddaeb;
    font-weight: normal;
  }
  /* ACTIVO Tema ya leído */
  .topic-list a.title.visited:not(.badge-notification):hover {
    text-shadow: 2px 2px #003330;
  }
  /* Número de Posts en cada tema */
  .badge-posts[href] {
    color: #cddaeb;
  }
  /* Badge */
  .badge-notification.new-posts, .badge-notification.unread-posts, .topic-list.categories .category .badge-notification, .badge-notification.new-topic {
   background-color: #206bd0;
   color: #f5f7fa;
   font-size: 12px;
  }
  /* Borde Lista Temas */
  .topic-list > tbody > tr {
    border-bottom: 1px solid #083067;
  }
  /* Borde Superior Tabla */
  .topic-list > tbody > tr:first-of-type {
    border-top: 3px solid #083067;
  }
  /* Título del Tema */
  #topic-title h1 a {
    color: #f5f7fa;
  }
  /* Borde Superior de cada post */
  .topic-body {
    border-top: 4px solid #083067;
  }
  /* Borde Superior X días después */
  .small-action {
    border-top: 4px solid #083067;
    max-width: 937px;
  }
  /* Nick de Usuario */
  .names span a {
    color: #f5f7fa;
    font-size: 20px;
  }
  /* Rango Usuario */
  .names .new_user a, .names .user-title, .names .user-title a {
    color: #cddaeb;
    font-size: 12px;
    font-style: italic;
}
  /* Bloque Avatar */
  .topic-avatar {
    width: 90px;
    border-top: 4px solid #083067;
  }
  /* Avatar */
  .topic-avatar img {
    height: 80px;
    width: 80px;
  }
  /* Insignia Nombre */
  .names .fa {
    color: #f5f7fa;
    font-size: 14px;
  }
  /* Fondo del Mensaje */
  .topic-body {
    background-color: #0c3770;
    color: #f5f7fa;
  }
  /* Enlaces de los Post */
  .topic-body p a {
    color: #f5f7fa;
    font-style: italic;
    text-decoration: underline;
  }
  /* ACTIVO Enlaces de los Post */
  .topic-body a:hover {
    text-shadow: 2px 2px #003330;
  }
  /* Reloj Días Despues */
  .small-action .topic-avatar i, .small-action .small-action-desc {
    color: #cddaeb;
  }
  /* Badge Mención */
  a.mention {
    background: #083067 none repeat scroll 0 0;
  }
  /* Responder como Tema enlazado */
  .gutter {
    padding-left: 945px;
  }
  /* Ancho del Post */
  .topic-body {
    width: 825px;
  }
  /* Progreso Tema */
  #topic-progress {
    background-color: #f5f7fa;
    border-color: #083067;
    color: #f5f7fa;
    left: 400px;
  }
  /* Barra de progreso */
  #topic-progress .bg {
    background-color: #0c3770;
  }
  /* Barra de progreso Fondo */
  #topic-progress {
    background-color: #093d83;
  }
  /* Ocultar Resumen de Tema Chincheta */
  .topic-excerpt {
    display: none;
  }
  /* Ventana de Responder */
  #reply-control {
    background-color: #0c3770;
  }
  /* Responder a */
  .reply-to {
    color: #f5f7fa;
    font-weight: bold;
    font-size: 16px;
  }
  /* Barra para ampliar ventana */
  body .grippie {
    background: #093d83 no-repeat scroll center 3px;
    border-top: 1px solid #083067;
  }
  /* Popup aviso Responder */
  .composer-popup-container {
    background-color: #f5f7fa !important;
    color: #093d83;
  }
  /* Bloque Cita */
  blockquote {
    background-color: #093d83;
    border-left: 5px solid #083067;
    color: #f5f7fa;
  }
  /* Cabecera Bloque Cita */
  aside.quote .title {
    background-color: #083067;
    border-left: 5px solid #083067;
    color: #f5f7fa;
    font-weight: bold;
  }
  /* Bloque Info Post */
  .topic-map {
    background: #093d83 none repeat scroll 0 0;
    border-color: -moz-use-text-color #083067 #083067;
  }
  .topic-map .map .number, .topic-map .map i, .topic-map h4, .topic-map h3, .topic-map a {
    color: #f5f7fa;
  }
  .topic-map section {
    border-top: 1px solid #083067;
  }
  .topic-map .buttons .btn {
    background: #093d83 none repeat scroll 0 0;
    border-color: #083067 -moz-use-text-color -moz-use-text-color #083067;
    color: #cddaeb;
  }
  .topic-map .buttons .btn:hover {
    background: #f5f7fa none repeat scroll 0 0;
    border-color: #083067 -moz-use-text-color -moz-use-text-color #083067;
    color: #093d83;
  }
  /* Ocultar Footer Sugerencias */
  #suggested-topics {
    display: none;
  }
  /* Fecha Post */
  .topic-meta-data .post-info a {
    color: #cddaeb;
  }
  /* Réplicas en Post */
  .post-cloak .reply-to-tab {
    color: #cddaeb;
  }
  /* Responder como tema enlazado */
  a.reply-new {
    color: #cddaeb;
    margin-top: 3px;
  }
  a.reply-new:hover {
    color: #f5f7fa;
    text-shadow: 2px 2px #003330;
    margin-top: 3px;
  }
  a.reply-new i {
    color: #083067;
    transition: none;
  }
 a.reply-new:hover i {
    background-color: #f5f7fa;
    transition: none;
  }
  /* Botones Post */
  nav.post-controls a, nav.post-controls button, nav.post-controls .highlight-action, nav.post-controls button.create {
    color: #cddaeb;
  }
  /* ACTIVO Botones Post */
  nav.post-controls button:hover {
    background: #f5f7fa none repeat scroll 0 0;
    color: #093d83;
  }
  /* Botón Cita */
  .quote-button {
    background-color: #f5f7fa;
    color: #093d83;
  }
  /* ACTIVAR Botón Cita */
  .quote-button:hover {
    background-color: #cddaeb;
    color: #093d83;
  }
  /* Texto Footer */
  #topic-footer-buttons p {
    color: #cddaeb;
  }
}
