@-moz-document url-prefix("http://4pda.ru/", "http://4pda.ru/forum") {
.mainBox {
background: #000;
margin: 0.5em 0;
}
.mdButton {
font-size: 0.875em;
font-weight: bold;
text-transform: uppercase;
}
.contrast {
color: #ededed;
background: #111;
}
.fsi {
font-size: inherit !important;
}
/*--- Анимация кнопок и ссылок ---*/
.button:active, a:active, .contact-social .social-link > li a:active, .user-profile-list > li:nth-child(2) a[href*="act=UserCP"]:active, .user-profile-list > li:nth-child(2) a[href*="act=UserCP"]:active , #news .page-nav.box:active {
transform: scale(0.9);
opacity:0.5;
}
a:active, .button:active, #news .page-nav.box li:active, #news .page-nav.box:active {
background: #000;
}
.button.disable:active {
transform: none;
opacity:1;
}
