HTML {
font-size: 1em;
}
body {
width: 100%;
height: 100%;
margin: 0;
background: #000;
font-family: "roboto";
font-size: 100%;
color: #dedede;
line-height: 1.25em;
overflow-wrap: break-word;
word-wrap: break-word;
}
a {
text-decoration: none;
color: #0277bd;
}
* {
box-sizing: border-box;
}

/*--- ПРОСМОТР ТОПИКА ---*/
[data-post] > a[name*="entry"] {
position: absolute;
margin-top: -4em;
left: 0;
width: 100%;
}
#topMargin {
background: #000;
}

/*--- НАВИГАЦИОННЫЕ КНОПКИ ---*/
.panel {
margin: 0.5em 0 0;
}
.panel .navi {
font-size: 1em;
margin: 0.5em 0;
padding: 0;
width: 100%;
display: block;
text-align: center;
}
.panel .navi .button {
display: inline-block;
text-align: center;
padding: 0.2em 1em;
font-weight: bold;
background: #111;
border: 0.1em solid #222;
border-radius: 4px;  
}
@media screen and (max-width: 310px) {
.panel .navi .button {
min-width: 35px;
}
}
.panel .navi .button.disable {
color: #222;
border: 0.1em solid #222;
background: #000;
}

/*- НАЗВАНИЕ ТОПИКА -*/
.topic_title_post {
display: block;
margin: 0 0.2em;
border: 1px solid #222;
border-radius: 5px;
background: #111;
text-align: center;
font-variant: small-caps;
font-weight: bold;
font-size: 1.2em;
font-style: oblique;
}
.topic_title_post:active {
background: rgba(255, 255, 255, 0.1);
}
.topic_title_post a {
display: inline-block;
padding: 0.5em;
-webkit-tap-highlight-color: rgba(0, 0, 0, 0);
}
.panel.bottom .topic_title_post {
display: none;
}

/*--- СТРУКТУРА ТОПИКА ---*/
.post_container {
background: #101010;
margin: 0.1em 0.3em;
border-radius: 5px;  
}
/*- Опрос -*/
.poll {
margin: 0;
}
.poll .hidetop {
background: #121212;
margin: 0.6em 0.25em;
margin-bottom: 0;
color: #ededed;
border-radius: 5px;
border: 1 px solid #333;
line-height: 1.8em;
position: relative;
font-size: 1.1em;
font-variant: small-caps;
font-weight: bold;
text-align: right;
padding-right: 0.5em;
}
.poll .hidetop:before {
padding: 0 0.2em;
content: '';
display: inline-block;
position: relative;
top: -0.05em;
width: 1.2rem;
height: 1.2rem;
vertical-align: middle;
text-align: center;
background-size: contain;
background-repeat: no-repeat;
}
.poll .hidetop:after {
display: none;
}
.poll .hidetop b {
display: inline;
}
.poll .hidetop.open {
  border-radius: 5px 5px 0 0
  }
.poll .hidemain {
  background: #101010;
  margin: 0.3em;
  margin-top: 0px;
border-radius: 0 0 5px 5px;
  z-index: 10;
}
.poll .hidemain form {
  display: block;
}
.poll .hidemain form .poll_title {
  padding: 0.75em 1em 0.75em 1em;
  border-bottom: 1px solid #333;
}
.poll .hidemain form .poll_title span {
  font-size: 1.125em;
  font-weight: bold;
  color: #8a8a8a;
}
.poll .hidemain form .poll_body .poll_theme {
  border-bottom: 1px solid #333;
  padding: 1em 1em 0 1em;
}
.poll .hidemain form .poll_body .poll_theme:last-of-type {
  border-bottom: none;
}
.poll .hidemain form .poll_body .poll_theme .theme_title span {
  font-weight: bold;
  color: #8a8a8a;
}
.poll .hidemain form .poll_body .poll_theme .items .item {
  padding: 0.75em 0;
  display: block;
  position: relative;
}
.poll .hidemain form .poll_body .poll_theme .items .item input {
  display: none;
}
.poll .hidemain form .poll_body .poll_theme .items .item input.checkbox + .icon {
  -webkit-border-radius: 2px;
  border-radius: 2px;
}
.poll .hidemain form .poll_body .poll_theme .items .item input.checkbox + .icon:after {
  content: "";
  height: 0.75em;
  width: 0.4375em;
  border: 0.125em solid #333;
  border-color: #ededed;
  box-sizing: border-box;
  border-left: none;
  border-top: none;
  left: 0.25em;
  bottom: 0.1875em;
  -webkit-transform: rotate(45deg) scale(0);
  transform: rotate(45deg) scale(0);
}
.poll .hidemain form .poll_body .poll_theme .items .item input.radiobutton + .icon {
  -webkit-border-radius: 100%;
  border-radius: 100%;
}
.poll .hidemain form .poll_body .poll_theme .items .item input.radiobutton + .icon:after {
  height: 0.625em;
  width: 0.625em;
  border-radius: 100%;
  background: #0277bd;
  left: 0.1875em;
  top: 50%;
  margin-top: -0.3125em;
  -webkit-transform: scale(0);
  transform: scale(0);
}
.poll .hidemain form .poll_body .poll_theme .items .item input:checked.checkbox + .icon {
  background: #0277bd;
}
.poll .hidemain form .poll_body .poll_theme .items .item input:checked.checkbox + .icon:after {
  -webkit-transform: rotate(45deg) scale(1);
  transform: rotate(45deg) scale(1);
  opacity: 1;
}
.poll .hidemain form .poll_body .poll_theme .items .item input:checked.radiobutton + .icon:after {
  -webkit-transform: scale(1);
  transform: scale(1);
  opacity: 1;
}
.poll .hidemain form .poll_body .poll_theme .items .item input:checked + .icon {
  border-color: #0277bd;
}
.poll .hidemain form .poll_body .poll_theme .items .item input:checked + .icon:after {
  opacity: 1;
}
.poll .hidemain form .poll_body .poll_theme .items .item .icon {
  position: absolute;
  height: 1.25em;
  width: 1.25em;
  top: 50%;
  margin-top: -0.625em;
  border: 0.125em solid #333;
  background: #000000;
  box-sizing: border-box;
  -webkit-transition: 0.25s;
  transition: 0.25s;
}
.poll .hidemain form .poll_body .poll_theme .items .item .icon:after {
  content: "";
  display: block;
  position: absolute;
  opacity: 0;
  -webkit-transition: 0.25s;
  transition: 0.25s;
}
.poll .hidemain form .poll_body .poll_theme .items .item .item_body {
  display: block;
  margin-left: 3em;
}
.poll .hidemain form .poll_body .poll_theme .items.voted .item .num_votes {
  float: right;
  font-size: 0.875em;
}
.poll .hidemain form .poll_body .poll_theme .items.voted .item .num_votes span {
  font-weight: bold;
}
.poll .hidemain form .poll_body .poll_theme .items.voted .item .num_votes:after {
  content: " Голосов";
}
.poll .hidemain form .poll_body .poll_theme .items.voted .item .range {
  margin-top: 0.25em;
  position: relative;
  background: rgba(128, 128, 128, 0.2);
  -webkit-border-radius: 2px;
  border-radius: 2px;
}
.poll .hidemain form .poll_body .poll_theme .items.voted .item .range .range_bar {
  height: 1.5em;
  background: #17415f;
  -webkit-border-radius: 2px;
  border-radius: 2px;
}
.poll .hidemain form .poll_body .poll_theme .items.voted .item .range .value {
  position: absolute;
  top: 0;
  line-height: 1.5em;
  height: 1.5em;
  left: 0.5em;
}
.poll .hidemain form .poll_body .poll_theme .items.voted .item .range .value span {
  font-size: 0.75em;
  font-weight: bold;
  color: #ededed;
  text-shadow: 0 0 3px rgba(0, 0, 0, 0.5);
}
.poll .hidemain form .votes_info {
  padding: 0.5em;
  display: block;
  text-align: center;
  border-bottom: 1px solid #333;
  border-top: 1px solid #333;
}
.poll .hidemain form .votes_info span {
  color: #8a8a8a;
  font-size: 0.875em;
}
.poll .hidemain form .buttons {
  text-align: center;
}
.poll .hidemain form .buttons .button {
  -webkit-border-radius: 2px;
  border-radius: 2px;
  background: none;
  color: #0277bd;
  font-size: 0.875em;
  font-weight: bold;
  padding: 0.3em;
  margin: 0.3em;
  border: none;
  text-transform: uppercase;
}
/*- Шапка поста -*/
.post_header .header_wrapper .nick.curator:before {
border-radius: 50%;
border: 1px solid #ffffff;
content: '';
display: inline-block;
position: relative;
top: -0.06rem;
width: 0.7rem;
height: 0.7rem;
vertical-align: middle;
text-align: center;
margin-right: 0.2em;
background-size: contain;
background-repeat: no-repeat;
}
.post_container .post_header .header_wrapper {
background: #222;
height: 5.7em;
border-radius: 5px 5px 0 0;
border-bottom: 1px solid #222;
position: relative;
box-shadow: inset 0 -1.3em #000, inset 0 -1.35em #222, inset 0 -3.1em #101010 , inset 0 0.7em #000; 
}
.post_container .post_header .avatar .img {
position: relative;
left: 0.1em;
top: 0.1em;
height: 4em;
width: 4em;
background: #000;
background-repeat: no-repeat;
background-size: cover;
background-position: center;
-webkit-border-radius: 4em;
border-radius: 4em;
border: 3px solid #222;
}
.post_container .post_header .inf {
position: absolute;
}
.post_container .post_header .inf.date {
left: 4.5em;
right: 0.5em;
top: 1.05em;
}
.post_container .post_header .inf.link {
left: auto;
right: 0.5em;
top: 1.1em;
}
.post_container .post_header .inf.link, .post_container .post_header .inf.date > span {
font-size: 0.875em;
font-weight: bold;
color: #ffffff!important;
}
.post_container .post_header .inf.reputation {
top: 2.7em;
left: 4.2em;
z-index: 1;
color: #0277bd;
}
.post_container .post_header .inf.reputation > span {
font-size: 0.875em;
}
.post_container .post_header .inf.reputation > span:before {
content: "Репутация: ";
}
.post_container .post_header .inf.menu{
left: auto;
right: 0.5em;
top: 2.7em;
color: #0277bd;
}
.post_container .post_header .inf.menu> span {
font-size: 0.875em;
}
.post_container .post_header .inf.menu:active {
background-color: rgba(255, 255, 255, 0.1);
}
.post_container .post_header .inf.nick {
top: 4.45em;
left: 0.5em;
color: #777;
max-width: 50%;
}
.post_container .post_header .inf.nick.online {
color: #12b557;
}
.post_container .post_header .inf.group {
top: 4.45em;
left: auto;
right: 0.5em;
white-space: nowrap;
overflow: hidden;
}
.post_container .post_header .inf.group > span{
font-size: 0.875em;
}
.post_container .post_header .inf.nick> span, .post_container .post_header .inf.nick.online> span {
font-size: 0.875em;
font-weight: bold;
}
/*--- Шапка темы ---*/
.hidetop {
border-top: 1px solid #222;
border-bottom: 1px solid #222;
position: relative;
background: #121212;
text-align: center;
line-height: 1.9em;
}
.hidetop:after {
content: "✪ Шапка темы ✪";
color: #0076a9;
font-size: 1.5em;
font-variant: small-caps;
font-weight: bold;
text-align: center;
}
.hidetop b {
display: none;
}
/*--- Тело поста ---*/
.post_body {
padding: 0.5em;
position: relative;
z-index: 1;
border-bottom: 1px solid #222;
}
.post_body .postcolor {
overflow: auto!important;
height: auto!important;
}
.post_body img {
max-width: 100%;
width: auto!important;
height: auto!important;
}
/*- Редактирование поста -*/
.post_body .edit {
border-top: 1px solid #222;
text-align: right;
display: block;
padding: 0.571em;
font-size: 0.875em;
color: #8a8a8a;
}
.post_body strong .edit {
text-align: left;
}
.post_body .post-edit-reason {
border-radius: 5px;
border: 1px solid #222;
background: #232323;
font-size: 0.875em;
text-align: right;
padding: 0 0.5em 0 0.5em;
margin-top: -2px;
color: #8a8a8a;
}
/*- Значок цитаты (Переход к сообщению) -*/
.post_body a[href*=findpost][title="Перейти к сообщению"] img {
display: none;
}
.post_body a[href*=findpost][title="Перейти к сообщению"]:before {
border-radius: 50%;
border: 1px solid #222;
content: '';
display: inline-block;
position: relative;
top: -0.1em;
width: 0.95em;
height: 0.95em;
vertical-align: middle;
text-align: center;
background-size: contain;
background-repeat: no-repeat;
}
/*- Блоки и модераторские теги -*/
.post-block {
margin: 0.3em 0;
position: relative;
overflow: hidden;
}
.post-block .block-title {
color: #ededed;
font-size: 0.95em;
background: #222;
border-radius: 5px 5px 0 0;
min-height: 1.5em!important;
padding: 0.3em;
position: relative;
z-index: 2;
line-height: 1.5em!important; 
}
.post-block .block-body {
border-radius: 0 0 5px 5px;
