}
.list-group-item,
.our-message {
background: #222; border: 1px solid #333;
margin: 0.3em;
-webkit-border-radius: 5px;
border-radius: 5px;
position: relative;
margin-left: 3.8em;
margin-right: 0.5em;
}
.list-group-item .check-item,
.our-message .check-item {
float: right;
height: 1.8em;
width: 1.8em;
line-height: 1.8em;
display: block;
padding: 0.4em;
}
.list-group-item .check-item input,
.our-message .check-item input {
display: none;
}
.list-group-item .check-item .checkbox,
.our-message .check-item .checkbox {
-webkit-border-radius: 50%;
border-radius: 50%;
width: 100%;
height: 100%;
display: block;
border: 1px solid #000;
border-width: 0.1em;
}
.list-group-item .check-item .checkbox .icon-check,
.our-message .check-item .checkbox .icon-check {
-webkit-border-radius: 5px;
border-radius: 5px;
position: absolute;
top: 0px;
right: 0px;
height: 100%;
width: 100%;
background: rgba(71, 170, 229, 0.3);
-webkit-transition: 0.45s;
transition: 0.45s;
-webkit-transform: scale(0);
transform: scale(0);
-webkit-transform-origin: top right;
transform-origin: top right;
}
.list-group-item .check-item .checkbox .icon-check:before,
.our-message .check-item .checkbox .icon-check:before {
display: none;
}
.list-group-item .check-item .checkbox .icon-uncheck,
.our-message .check-item .checkbox .icon-uncheck {
display: block;
width: 50%;
height: 100%;
margin: 0 auto;
position: relative;
}
.list-group-item .check-item .checkbox .icon-uncheck:before,
.our-message .check-item .checkbox .icon-uncheck:before {
display: none;
}
.list-group-item .check-item input:checked + .checkbox,
.our-message .check-item input:checked + .checkbox {
background: #222;
border: 1px solid #222;
box-shadow: inset 0 0 0 0.15em #17415f;
}
.list-group-item .check-item input:checked + .checkbox .icon-check,
.our-message .check-item input:checked + .checkbox .icon-check {
-webkit-transform: scale(1);
transform: scale(1);
}
.list-group-item .check-item input:checked + .checkbox .icon-uncheck:before,
.our-message .check-item input:checked + .checkbox .icon-uncheck:before {
-webkit-transform: rotate(45deg) scale(1);
transform: rotate(45deg) scale(1);
opacity: 1;
}
.list-group-item .check-item:after,
.our-message .check-item:after {
clear: both;
content: "";
display: table;
}
.list-group-item .time,
.our-message .time {
position: absolute;
bottom: -0.2em;
left: -3.4em;
font-size: 0.9em;
}
.list-group-item .time .read-status,
.our-message .time .read-status {
display: inline-block;
width: 0.6em;
border-radius: 50%;
position: absolute;
bottom: 0.4em;
padding-bottom: 25%;
}
.list-group-item .time .read-status.unread,
.our-message .time .read-status.unread {
background: #cb5242;
}
.list-group-item .avatar-wrap,
.our-message .avatar-wrap {
margin: 0;
border: 2px solid #222;
border-radius: 50% !important;
position: absolute;
top: 0px;
left: -3.3em;
width: 2.8em;
height: 2.8em;
background: transparent;
line-height: 2.8em;
text-align: center;
vertical-align: middle;
}
.list-group-item .avatar-wrap .avatar,
.our-message .avatar-wrap .avatar {
position: relative;
border-radius: 50% !important;
display: inline-block;
line-height: 2.8em;
height: 100%;
max-width: 100%;
overflow: hidden;
text-align: center;
vertical-align: middle;
}
.list-group-item > strong,
.our-message > strong {
display: inline-block;
float: left;
line-height: 1.8em;
height: 1.8em;
padding: 0 0.5em;
}
.list-group-item > br,
.our-message > br {
display: none;
}
.list-group-item .msg-content{
background: #000;
}
.our-message .msg-content {
background: #111;
}
.list-group-item .msg-content,
.our-message .msg-content {
border-top: 1px solid #333;
padding: 0.25em 0.4em;
width: 100%;
overflow: hidden;
border-radius: 0 0 5px 5px;
}
.our-message {
margin-left: 0.5em;
margin-right: 3.8em;
}
.our-message .check-item {
float: left;
}
.our-message .check-item .checkbox:before {
right: auto;
left: -1em;
}
.our-message .check-item .checkbox .icon-check {
right: auto;
left: 0;
-webkit-transform-origin: top left;
transform-origin: top left;
}
.our-message .time {
left: auto;
right: -3.4em;
}
.our-message .time .read-status {
right: auto;
left: -1.3em;
}
.our-message .avatar-wrap {
left: auto;
right: -3.3em;
}
.our-message > strong {
float: right;
}
.list-group-item + .list-group-item .avatar-wrap {
border-radius: 50% !important;
display: none;
text-align: center;
vertical-align: middle;
}
.list-group-item + .our-message {
margin-top: 0.5em;
}
.list-group-item + .our-message .avatar-wrap {
border-radius: 50% !important;
display: block;
text-align: center;
vertical-align: middle;
}
.our-message + .list-group-item {
margin-top: 0.5em;
}
.our-message + .list-group-item .avatar-wrap {
border-radius: 50% !important;
display: block;
overflow: hidden;
text-align: center;
vertical-align: middle;
background: transparent;
}
.our-message + .our-message {
margin-top: 7px;
}
.our-message + .our-message .avatar-wrap {
border-radius: 50% !important;
display: none;
text-align: center;
vertical-align: middle;
}

