padding-left: 0.8em;
font-size: 1.25em;
line-height: 1.6em;
border-radius: 5px 5px 0 0;
background: #222;
color: #fff;
}
.materials-box .materials-slider {
margin: 0;
padding: 0;
list-style: none;
line-height: 1.4em;
padding-bottom: 0.5em;
}
.materials-box .materials-slider .slider-item {
display: block;
}
.materials-box .materials-slider .slider-item > a {
display: none;
}
.materials-box .materials-slider .slider-item > a img {
width: 100%;
display: block;
}
.materials-box .materials-slider .slider-item h3 {
margin: 0;
font-weight: 400;
font-size: 1em;
}
.materials-box .materials-slider .slider-item h3 a {
-webkit-tap-highlight-color: rgba(0, 0, 0, 0);
padding: 0.5em 1em;
display: block;
}
.materials-box .materials-slider .slider-item h3 a:active {
background: rgba(255, 255, 255, 0.1);
}
.materials-box .materials-slider:after {
content: "";
display: table;
clear: both;
}
#news .page-nav.box {
  display: block;
  margin: 0.3rem 0.5%;
  padding: 0;
  text-align: center;
  font-size: 0.9rem;
}
#news .page-nav.box li {
  line-height: 0.9rem;
  border-radius: 5px;
  border: 1px solid #222;
  background: #101010;
  display: inline-block;
  width: 48%;
  padding: 0;
  list-style-type: none;
}
#news .page-nav.box li a {
  display: block;
  width: 100%;
  margin: 0;
  padding: 0.5rem 0;
  text-transform: uppercase;
  color: #0277bd !important;
  font-weight: bold;
  text-decoration: none;
}
#news .page-nav.box li a .icon-right-small i,
#news .page-nav.box li a .icon-left-small i {
  display: none;
}
#news .page-nav.box li a .icon-right-small:before,
#news .page-nav.box li a .icon-left-small:before {
  display: none;
}
.comment-box {
background: #101010;
margin: 0.5em 0.3em;
position: relative;
padding-bottom: -10px;
border-radius: 5px;
}
.comment-box > .heading {
position: relative;
margin: 0;
border-radius: 5px 5px 0 0;
background: #222;
color: #ffffff;
}
.comment-box > .heading h2 {
margin: 0;
padding-left: 0.8em;
font-size: 1.25em;
line-height: 1.6em;
}
.comment-box > .heading a {
font-size: 0;
width: 100%;
height: 100%;
display: block;
position: absolute;
top: 0;
right: 0;
z-index: 10;
display: none;
}
.comment-box > .heading:after {
font-family: 'fontello';
content: '\45';
position: absolute;
top: 0;
right: 0;
width: 2.2em;
height: 2.2em;
text-align: center;
display: none;
}
.comment-box .comment-list {
margin: 0;
padding: 0;
list-style: none;
list-style-type: none;
position: relative;
padding-left: 0.5em;
border-left: 1px solid #222;
}
.comment-box .comment-list.level-0 {
padding-left: 0;
}
.comment-box .comment-list.level-0 > li {
padding-left: 1em;
}
.comment-box .comment-list.level-0 > li > div[id*=comment]:not([id*=comment-form]) {
margin-top: 0;
}
.comment-box .comment-list li {
position: relative;
}
.comment-box .comment-list li div[id*=comment]:not([id*=comment-form]) {
position: relative;
margin-top: 1em;
}
.comment-box .comment-list li .heading {
padding: 0.5em;
padding-left: 0;
padding-bottom: 0em;
}
.comment-box .comment-list li .heading:after {
clear: both;
content: "";
display: table;
}
.comment-box .comment-list li .heading .nickname {
float: left;
font-size: 0.875em;
color: #8a8a8a;
}
.comment-box .comment-list li .heading .karma .icon-karma-up {
display: none;
}
.comment-box .comment-list li .heading .karma .icon-hide-comment {
display: none;
}
.comment-box .comment-list li .heading .karma .num-wrap {
float: right;
color: #8a8a8a;
}
.comment-box .comment-list li .heading .karma .num-wrap:before {
content: " | ";
padding-left: 0.28571429em;
font-size: 0.875em;
}
.comment-box .comment-list li .heading .karma .num-wrap .num:before {
padding-right: 0.28571429em;
content: "\2764";
}
.comment-box .comment-list li .heading .karma[data-karma-vote*="1"] .num {
color: #0277bd;
}
.comment-box .comment-list li .heading .h-meta {
float: right;
color: #8a8a8a;
font-size: 0.875em;
}
.comment-box .comment-list li .heading .h-meta .more-meta {
display: none;
}
.comment-box .comment-list li .content {
padding: 0.5em;
padding-left: 0;
padding-top: 0.25em;
margin: 0;
}
.comment-box .comment-list li div[id*=form-reply] {
text-align: right;
}
.comment-box .comment-list li div[id*=form-reply] a {
color: transparent;
position: absolute;
top: 1.8em;
left: 0;
bottom: 0;
right: 0;
-webkit-tap-highlight-color: rgba(0, 0, 0, 0);
}
.comment-box .comment-list li div[id*=form-reply] a:active {
background: rgba(255, 255, 255, 0.1);
}

/*--- ПОИСК ---*/
.posts_list.search-results .topic_title_post {
border-bottom: 1px solid #222;
}
.posts_list.search-results .post_container .post_header {
padding: 0.5em 1em;
}
.posts_list.search-results .post_container .post_header .s_inf.nick {
color: #dedede;
}
.posts_list.search-results .post_container .post_header .s_inf.nick.online {
color: #1a9b43;
}
.posts_list.search-results .post_container .post_header .s_inf.date {
float: right;
}
.posts_list.search-results .post_container .post_header .s_inf.date > span {
font-size: 0.875em;
}
.posts_list.search-results .post_container .s_post_footer {
padding: 1em 0.5em;
}
.posts_list.search-results ~ #bottomMargin {
height: 5.5em;
}
div[data-post] {
background: #101010;
margin: 0.5em 0.2em;
-webkit-border-radius: 5px;
border-radius: 5px;
}
div[data-post] .post_header_container {
position: relative;
z-index: 2;
}
div[data-post] .post_header_container .post_header {
-webkit-border-radius: 5px 5px 0 0;
border-radius: 5px 5px 0 0;
text-align: left;
position: relative;
padding-top: 2.25em;
padding-left: 0.5em;
padding-right: 0.5em;
line-height: 2.25em;
min-height: 6.75em;
}
div[data-post] .post_header_container .post_header:before {
content: "";
position: absolute;
height: 2.25em;
width: 100%;
top: 0;
left: 0;
background: #222;
}
div[data-post] .post_header_container .post_header:after {
content: "";
position: absolute;
top: 6.75em;
left: 0.5em;
right: 0.5em;
border-top: 1px solid #222;
}
div[data-post] .post_header_container .post_header br {
display: none;
}
div[data-post] .post_header_container .post_header .post_date {
position: absolute;
top: 0;
right: 0.571em;
font-size: 0.875em;
color: #ededed;
background: #222;
}
div[data-post] .post_header_container .post_header .post_date a {
color: #ededed;
background: #222;
}
div[data-post] .post_header_container .post_header .post_nick {
position: absolute;
top: 0;
left: 1.75em;
}
div[data-post] .post_header_container .post_header .post_nick a {
font-size: 0.875em;
font-weight: bold;
color: #ededed;
background: #222;
}
div[data-post] .post_header_container .post_header .post_user_info strong {
float: right;
}
div[data-post] .post_header_container .post_header .post_user_info span + br {
display: block;
}
div[data-post] .post_header_container .post_header .post_user_info font[color] {
position: absolute;
top: 0;
left: 0;
height: 2.25em;
width: 2.25em;
color: transparent;
}
div[data-post] .post_header_container .post_header .post_user_info font[color]:after {
content: "";
position: absolute;
width: 0.75em;
height: 0.75em;
top: 0.75em;
left: 0.75em;
border-radius: 50%;
}
div[data-post] .post_header_container .post_header .post_user_info font[color][color=red]:after {
background: #cb5242;
}
div[data-post] .post_header_container .post_header .post_user_info font[color][color=green]:after {
background: #309f5e;
}
div[data-post] .post_header_container .post_header .post_user_info a {
color: #0277bd;
font-weight: bold;
text-transform: uppercase;
font-size: 0.875em;
}
div[data-post] .post_header_container .post_header .post_user_info a[href*="act=qms"] {
float: right;
}
div[data-post] .post_header_container .post_header .post_user_info a[href*="showuser"] {
float: right;
padding-right: 0.75em;
}
div[data-post] .post_header_container .post_header [id*="ajaxrep"] {
color: #0277bd;
}
div[data-post] .post_header_container .post_header [onclick*="rep_change"] {
position: relative;
width: 2em;
display: inline-block;
height: 2em;
vertical-align: top;
background: #101010;
}
div[data-post] .post_header_container .post_header [onclick*="rep_change"] img {
display: none;
}
div[data-post] .post_header_container .post_header [onclick*="rep_change"]:before {
content: '';
position: absolute;
left: 0.5em;
top: 0.5em;
right: 0.5em;
bottom: 0.5em;
-webkit-border-radius: 5px;
border-radius: 5px;
line-height: 1em;
color: #ededed;
text-align: center;
}
div[data-post] .post_header_container .post_header [onclick*="rep_change"][href*="win_minus"]:before {
content: '-';
background: #a54336;
}
div[data-post] .post_header_container .post_header [onclick*="rep_change"][href*="win_add"] {
margin-left: -0.5em;
}
div[data-post] .post_header_container .post_header [onclick*="rep_change"][href*="win_add"]:before {
content: '+';
background: #249151;
}
div[data-post] .post_header_container .post_header .post_action {
display: table;
width: 100%;
text-align: center;
}
div[data-post] .post_header_container .post_header .post_action a {
display: table-cell;
color: #0277bd;
text-transform: uppercase;
font-weight: bold;
font-size: 0.875em;
}
div[data-post] .post_header_container .post_header .post_action:before {
content: "";
position: absolute;
top: 4.5em;
left: 0.5em;
right: 0.5em;
border-top: 1px solid #222;
}
div[data-post] .post_body {
-webkit-border-radius: 0 0 5px 5px;
border-radius: 0 0 5px 5px;
}
.searchlite {
background: rgba(248, 143, 0, 0.3);
-webkit-border-radius: 5px;
border-radius: 5px;
padding: 0 0.125em;
}
#bottomMargin {
height: 4em;
background: #000;
position: relative;
z-index: 1;
}

/*--- ПРОЧЕЕ ---*/
@media screen and (max-width: 24em) {
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl {
width: 100%;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow {
margin: 0.5em;
}
}

@media screen and (min-width: 50em) {
#main .container .product-detail {
text-align: left;
background: #101010;
border-bottom: 1px solid #222;
}
#main .container .product-detail .photo {
padding-bottom: 30%;
width: 40%;
}
#main .container .product-detail .photo .label {
width: 100%;
color: #ededed;
}
#main .container .product-detail .photo img {
-webkit-border-radius: 2px 0 0 0;
border-radius: 2px 0 0 0;
}
#main .container .product-detail .description h1 {
width: 60%;
right: 0;
background: none;
color: #dedede;
text-shadow: none;
}
#main .container .product-detail .description .info-holder {
right: 0;
width: 60%;
bottom: auto;
top: 0em;
}
#main .container .product-detail .description .more-box {
bottom: auto;
top: 0em;
}
#main .container .content .content-box .table4site {
-webkit-border-radius: 2px;
border-radius: 2px;
display: table;
}
#main .container .content .content-box .table4site tbody {
display: table-row-group;
}
#main .container .content .content-box .table4site tbody th {
display: table-row;
}
#main .container .content .content-box .table4site tbody tr {
-webkit-border-radius: 0;
border-radius: 0;
display: table-row;
border: 1px solid #222;
margin: 0.5em;
}
#main .container .content .content-box .table4site tbody tr p {
margin: 0;
display: inline-block;
}
#main .container .content .content-box .table4site tbody tr td {
display: table-cell;
width: 100%;
padding: 0.2em 0.4em;
padding-left: 0.6em;
border-right: 1px solid #222;
}
#main .container .content .content-box .table4site tbody tr td:first-child {
width: auto;
background: none;
color: #dedede;
}
#main .container .content .content-box .table4site tbody tr td:last-child {
border-left: none;
}
#main .container .content .content-box .mlinks {
position: relative;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mlicon {
width: 7em;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl {
overflow: hidden;
position: absolute;
top: 0.25em;
right: 0;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow {
color: #ededed;
background: #222;
-webkit-border-radius: 5px;
border-radius: 5px;
position: relative;
line-height: 2em;
margin: 0.25em 0.5em 0.25em 0.5em;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow:first-child {
display: none;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell {
display: inline-block;
position: relative;
padding: 0 10px;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell a {
display: block;
width: 100%;
height: 100%;
position: absolute;
color: transparent;
top: 0;
left: 0;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell i {
display: block;
text-align: center;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell i:before {
line-height: 2em;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell:after {
content: "";
background: #ededed;
position: absolute;
width: 1px;
bottom: 0.35em;
right: 0;
top: 0.35em;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell:last-child {
border-right: none;
position: absolute;
top: 0;
left: 0;
width: 100%;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell:last-child:after {
display: none;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow .mlcell:nth-child(3):after {
width: 0;
}
#main .container .content .content-box .mlinks .mlhead .do-vam > div .mltbl .mlrow:last-child .mlcell {
border-bottom: none;
}
}
.search-web-view {
margin-top: 0!important;
}

