css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live.bilibili.com") {
    
/*隐藏元素*/
    #rank-list-ctnr{display:none !important;}
    .download-link.p-relative.float-left{display:none !important;}
    .user-level-icon{display:none !important;}
    .gift-msg{display:none !important;}
    .medal-icon{display:none !important;}
    .btn.danmu-color{display:none !important;}
    .live-icon.vip-color.v-middle{display:none !important;}
    #profile-ctrl{display:none !important;}
    .live-ad-full{display:none !important;}
    .live-icon.vip-icon.p-absolute.vip-gray{display:none !important;}
    .supergift{display:none !important;}
    .system-msg{display:none !important;}
    .live-title-icon{display:none !important;}
    .announcement-container{display:none !important;}
    #gift-msg-1000{display:none !important;}
    .close-treasure-box{display:none !important;}
    
    /*侧边栏*/
    .doz-z{display:none !important;}/*切换按钮睡觉*/
    .tv-eye.left{display:none !important;}/*切换按钮左眼*/
    .tv-eye.right{display:none !important;}/*切换按钮右眼*/
    .not-enough{display:none !important;}/*关注列表底部图片*/
    .main-ctnr.p-relative.w-100.h-100.ps-container.ps-theme-default.ps-active-y > .ps-scrollbar-y-rail{display:none !important;}/*隐藏大滚动条*/
    
/*修正元素*/
    .nav-link-ctnr{height:0px;}
    .nav-link-ctnr{position:relative;top:-50px;left:0px;}
    .head-info-panel .attention-btn-ctrl .right-part{position:relative;top:-25px;height:25px;}
    .head-info-panel .attention-btn-ctrl .left-part{position:relative;right:20px;}
    #player-container{width:896px;height:470px;}
    #player_object{position:relative;top:-16px;}
    .sidebar-left-open .room-left-sidebar .large-wrap{width:200px;}
    #chat-msg-list{position:relative;top:-5px;}
    .rank-list .rank-item{height:26px;}
    .rank-list-tab .tab-switcher{width:60px;}
    .clear-float.danmu-sender{height:25px;}
    .clear-float.danmu-sender{position:relative;top:5px;}
    #danmu-textbox{height:15px;width:220px;}
    #danmu-send-btn{height:25px;}
    .danmu-sender .danmu-textbox{font-size:10px;}
    .right-part.chat-ctnr.main-section.p-relative.float-right{width:290px;height:597px;}/*弹幕池调整大小*/
    #chat-list-ctnr{height:597px;}
    #chat-msg-list{height:587px;}
    #chat-msg-list{position:relative;top:0px;}
    #chat-ctrl-panel{position:relative;top:6px;}
    #chat-ctrl-panel{height:75px;border:1px solid #ddd;}
    
    /*顶栏*/
    .live-action-btn.dp-inline-block.transition-dot-2{height:55px;}/*我要直播按钮缩小*/
    .live-top-nav-ctnr.no-hidden.transition-dot-4{height:60px;}/*顶栏加宽*/
    .live-top-nav-ctnr .nav-link-item{position:relative;top:-30px;}/*直播、主站、排行元素上移*/
    .nav-link-item.area-navigator{width:55px;}/*直播按钮限宽*/
    .content-ctnr{position:relative;top:30px;}/*直播按钮文字下移*/
    .live-top-nav-panel{position:relative;top:50px;}/*直播分区下移*/
    .live-icon.main-site{position:relative;top:30px;}/*主站按钮下移*/
    .live-icon.main-site + *{position:relative;top:30px;}/*主站按钮文字下移*/
    .live-icon.rank{position:relative;top:30px;}/*排行按钮下移*/
    .live-icon.rank + *{position:relative;top:30px;}/*排行按钮文字下移*/
    
    /*侧边栏*/
    .tv-logo.m-auto{position:relative;left:10px;}/*小侧边栏电视右移*/
    .small-btn.mission{position:relative;left:10px;}/*小侧边栏成就右移*/
    .small-btn.sign{position:relative;left:10px;}/*小侧边栏签到右移*/
    .small-btn.capsule{position:relative;left:10px;}/*小侧边栏扭蛋右移*/
    .small-btn.attention{position:relative;left:10px;}/*小侧边栏关注右移*/
    .btn-img{position:relative;top:80px;right:69px;transform:scale(0.5)rotate(270deg);}/*切换按钮左移缩小*/
    .btn-img:hover{background:#4FC1E9;}/*切换按钮蓝色背景*/
    .main-ctnr{overflow:visible !important;}/*禁止大滚动条*/
    #room-my-attention .my-attention-body{height:274px;}/*关注列表拉长*/
    
    .treasure{position:relative;top:0px;left:10px;}/*宝箱下移*/
}
