css:
@-moz-document url-prefix('https://twitter.com/'),url-prefix('https://api.twitter.com'),url-prefix('https://amp.twimg.com'){

.btn{border-radius:20px!important;}


/* experimental*/
.PermalinkOverlay-with-background{background:rgba(0,0,0,0.81)none repeat scroll 0% 0%;}

.DMInbox-content{max-height:inherit!important;}

.global-nav .search-icon .Icon{color:#7991A2;}

.TweetWithPivotModule-header a{color:#3FC4AD;}

.Streams-momentsDividerText{background-color:#CCD6DD;color:#191919!important;}

.TwitterCard [data-poll-vote-majority*="1"] .PollXChoiceTextOnly-optionContainer[data-poll-index="1"] .PollXChoiceTextOnly-choice--chart,.TwitterCard [data-poll-vote-majority*="2"] .PollXChoiceTextOnly-optionContainer[data-poll-index="2"] .PollXChoiceTextOnly-choice--chart{background:#1A5058;}

.MomentCapsulePermalink .MomentCapsuleDetails-description{color:#6F6F6F;}

/*[data-nav="moments"],[data-nav="moments"]:hover{left:53.5%;top:-4px;position:fixed!important;}*/

.js-moments-tab{left:100.7%;position:absolute!important;}

.moments .text{display:none;}

.TwitterCard .Button--smallGray:not([disabled]):hover{color:#D0D0D0;}

.Trends .context-trend-item .trend-item-stats{color:#7CABA6;}
.Trends .context-trend-item .trend-item-context{color:#949494;}

.TwitterCard .PollXChoiceTextOnly-optionContainer .PollXChoiceTextOnly-choice--chart{background:#3A5049;border-top-left-radius:0;border-bottom-left-radius:0;}

.PollXChoiceTextOnly-choice--text{color:#A7A7A7!important;}

.route-logout .buttons-arrow{border:8px solid #101010;border-bottom-color:#242424;}
.ProfileAlerts .description-content,.ProfileAlerts .leftcontent{padding:20px;}
.Icon--smiley{color:#285677!important;}
.conversation-module>li:after,.conversation-module>li:before{width:2px;left:37.5px;border-radius:4px/9px;}
.conversation-module .conversation-root:after{top:64px;}
.conversation-module>li:after{top:61px!important;}
.vellip{left:38px;width:2px;}
.vellip:before,.vellip:after{width:2px;}

.ProfileMessagingActions-buttonWrapper button{border-radius:20px!important;}

.TwitterCard .CommerceCard-details{border-color:rgba(136,153,166,.5);}
.ProfileCardStats-statLabel{font-size:9px!important;letter-spacing:.02em!important;}
.ProfileCardStats-statValue{font-size:13px!important;}

/* basics */
.route-download .session-dropdown,.tweet .user-pin-tweet{display:block;}
.promoted,[data-promoted],.js-profileClusterFollow,.import-prompt,.Banner.eu-cookie-notice,.promoted-tweet,.promoted-account,.ProfileHeaderUploadDialog-cropperHelp,#empty-timeline-recommendations,.Icon--verifiedFill::before,[data-nav="ads"]{display:none!important;}
.ProfileHeaderCard-screenname.u-inlineBlock,.Grid-cell.u-size2of3.u-lg-size3of4 .u-lg-size2of3 .ProfileHeading-content.ProfileHeading-content--noFill{display:inherit!important;}
.ProfileHeaderCard-url>*{display:initial!important;}

.count-inner,.new-count,.dm-new{border-radius:2em!important;font-weight:bold;}

.dm-delete-confirm p,.dm-spam-confirm p,.dm-abuse-confirm p,p.operators,.remember-forgot *,.module{text-shadow:initial!important;}
.dm-dialog .twttr-dialog-footer,.dm-placeholder-empty p{text-shadow:inherit!important;}

h2.TwitterCard-title{text-align:left!important;}

.front-card .text-input,.front-signin .submit,.front-signup .btn,.content-searchbar .search-query,.follow-card .follow-bar,.dm-participant-container .dropdown-menu,.dm-dialog .twttr-dialog-footer,.DMComposer,.btn:focus,.module .list-link,.modal-content,.Trends,.TopNav{box-shadow:none!important;-webkit-box-shadow:none!important;}

.nav-tabs>.active>a{color:#101010!important;}
.Section p{color:cadetblue!important;}
.typeahead-items .Icon.Icon--close,.modal-table th,.keyboard-shortcut-title,.block-dialog .label-head,.permissions.deny strong{color:#C6292B!important;}
.ProfileNav-value,.activity-popup-users-hidden,.geo-not-enabled-yet p,.modal-body .geo-not-enabled-yet p,.TwitterCard{color:#7A8084!important;}
.nav>li .Icon,.nav>li>a .text{color:#7A8084;}

.photo-selector.disabled{border:1px solid #404040;border-radius:3px;}

.disabled .icon-btn.enabled,.photo-selector.disabled,.InStreamSignup .SignupCallOut,.InStreamLogin .LoginCallOut,.InStreamLogin{background-color:#353535!important;}
.detail-view-inline-text{background-color:#4B4B4B!important;}
.ProfileCard-avatarLink,.DirectMessage--emoji.DirectMessage--sent .DirectMessage-message,.DirectMessage--emoji.DirectMessage--received .DirectMessage-message,.MomentMediaItem,.dm-media{background-color:transparent!important;}
.user-select-controls input,.DMComposer .tweet-content,.ProfileAlerts .content,.ProfileSidebar--withLeftAlignment .ProfileLifelineInfo.u-cf,.hovercard .profile-social-proof,.EmojiBar-keyboardEntry,.EmojiBar-suggestions,.EmojiBar-icon{background:transparent!important;}
.geo-query-location,.ProfilePopupContainer--bellbird .profile-details .profile-dialog-blocks-you,.flex-table-btn.js-search-dropdown,.grid-tweet-actions-container,.item-box .tweet:hover,.ScrollBump.separated-module{background-color:initial!important;background-image:initial!important;}
.block-or-report-dialog .submit-section,.stream-item-activity-header{background:none!important;}
.GridTimeline-footerIcon,.expansion-container .permalink-tweet{background-color:inherit!important;}

.content-inner{border-left:1px solid #2c2c2c!important;border-right:1px solid #2c2c2c!important;border-bottom:1px solid #2c2c2c!important;}

.input-append .add-on,.tweet-form div.rich-editor:not(.DMComposer-editor){border:1px solid #363636!important;}
.user-select-controls{border:1px solid #4D4D4D!important;}
.dashboard{border:1px solid #101010!important;margin-left:-2px;}
[aria-expanded="true"],.DMComposer .u-borderUserColorLighter,.CommerceDiscovery,.wrapper-custom_timeline .custom-timeline-follow-card,.MomentMediaItem,.MomentGrid:first-child .MomentCapsuleItem,.MomentCapsulePermalink-media{border:1px solid #101010!important;}
.content-header,.content-no-header,.home-tweet-box,.stream-placeholder,.PollingCardComposer .PollingCardComposer-pollDuration{border:1px solid #2c2c2c!important;}
.ProfileListItem:first-child,.ProfilePopupContainer--bellbird .profile-details .profile-dialog-blocks-you,.list-link:first-child,.dm-participant-container,.stream-end,.modal-footer,.stream-placeholder,.DMConversation-composer{border-top:none!important;}
.module .list-link{border-top:1px solid #000;}
.block-or-report-dialog .report-type-section,.activity-content .account,.list-link:last-child,.AdaptiveFiltersBar,.inline-reply-tweetbox,.dm-to,.block-dialog .block-section,.disabled-links.modal-tweet div.simple-tweet,.header-inner{border-bottom:none!important;}
.column-header,.column-header-temp{border-bottom:1px solid #060606!important;}
.global-nav-inner{border-bottom:1px solid rgb(20,35,30)!important;}
.content-main .expansion-container>li .tweet,.front-signup h2,.front-signin h2{border-bottom-color:#242424!important;}

.list-editor .list-editor-save{margin:0!important;}
#global-actions{margin-top:1px!important;}
.js-new-items-bar-container,.ThreadedPermalinkOverlay-footer{margin-top:-3px;}
.GridTimeline{margin-top:-2.6px!important;}
#timeline:not(.content-main){margin-top:-1.6px!important;}
.global-nav [role=navigation]{margin-left:-3px;}
.nav{margin-left:0px;}
.pull-right{margin-right:15px;}
#global-new-tweet-button{margin-right:31px!important;}
.logged-out .global-nav .pull-right .nav{margin-left:50px;}
.ProfileSidebar .ProfileHeaderCard,.ProfileSidebar .ProfileUserList,.ProfileSidebar .PhotoRail,.permalink-footer .SignupCallOut,.ThreadedPermalinkOverlay-replies .replies-to,.module{margin-bottom:0!important;}

.global-nav .pull-right .dropdown-toggle{overflow:visible;}

/* Grey background with points */

.fast-signup .global-nav,.global-nav-inner{background-image:url(https://pbs.twimg.com/media/CLQ6Lc6WUAELkz2.png);background-size:100% 100%;}
.global-nav .global-nav-inner{border-bottom:1px solid rgb(16,23,23)!important;}

.enhanced-mini-profile,.modal-tweet-form-container,.ThreadedConversation-showMore,.module .active .list-link,#global-tweet-dialog .modal-tweet-form-container,html[dir="ltr"],body.nofooter,html[xmlns="http://www.w3.org/1999/xhtml"],.AdaptiveSearchPage .AppContent,[data-platform="web"],.route-download #page-outer,.fast-signup.logged-out,.route-profile #page-outer .AppContent#doc.route-connect,.route-profile #page-container.AppContent,.MomentsPage,.route-account_suspended,.AdaptiveDefaultPage #doc,.ProfilePage #doc,.Streams #timeline,.overlay-enabled{background:#242424 url(https://pbs.twimg.com/media/CKecQtCWoAA8keI.png)}

/*[data-fouc-class-names="swift-loading"]:not(.iframe),#doc.route-connect*/.enhanced-mini-profile,.route-profile #page-container.AppContent,.MomentsPage,.ProfilePage #doc,.overlay-enabled{background-color:#383838;}

.u-bgUserColorLightest,.ProfileHeaderCardEditing{background:#242424 url(https://pbs.twimg.com/media/CKecQtCWoAA8keI.png)!important}

/* Profile */
.ProfileCanopy-navBar:after{content:" "!important;position:absolute;left:1px;margin-top:-60px;padding:60px 370px 0 0;}
/*.ProfileNav-list:after{content:" "!important;position:absolute;right:-301px;padding:60px 289px 0 0;}*/
.ProfileCanopy .ProfileCanopy-card{padding-top:0;left:1220px;position:relative;z-index:1;margin-left:-20px}
.ProfileCanopy-navBar{max-width:1190px!important;margin:0 auto!important;background-color:transparent!important;border-bottom:none!important;}

@media screen and (min-width:1236px){
.ProfileNav{padding-left:302px!important;margin-left:-2px;}
.ProfileCanopy-navBar .AppContainer{margin-right:25.3%!important;padding-left:100px!important;margin-left:-400px!important;position:relative;z-index:2;}
}
.ProfileCanopy-navBar .AppContainer{position:relative;z-index:2;}

.BirthdateSelect-button.u-borderUserColorLight{border-color:#2F4F4F!important;background-color:darkslategray;text-align:center;color:#fff;}
.DashboardProfileCard-screennameLink,.ProfileHeaderCard-screennameLink{color:lightsteelblue;}
.ProfileHeaderUploadDialog-cropperSlider{width:47%;}
.ProfileNameTruncated-link,.bypassuser{font-size:18px;}

.ProfileTweet-contents{margin-left:58px!important;margin-top:-30px!important;}
.ProfileTweet-avatar{width:48px!important;height:48px!important;}
.ProfileTweet-context{margin-left:35px!important;}
.ProfileTweet-originalAuthor .u-linkComplex-target{padding-left:3px!important;}
.ProfileUserList-facepile{display:table;margin-left:25px;}
.ProfileCard .ProfileCard-screennameLink,.ProfileCard .ProfileCard-screennameLink:hover,.ProfileCard .ProfileCard-screennameLink:focus{font-size:13px!important;}
[data-element-term="following_stats"],[data-element-term="follower_stats"],[data-element-term="tweet_stats"]{width:65px;text-align:center;}
.ProtectedTimeline{width:65%!important;}
.DashboardProfileCard-statLink,.Grid-cell.u-lg-size2of3 .ProtectedTimeline{width:initial!important;}
.js-mini-profile-stats [data-element-term="following_stats"] strong{margin-left:-8px;}

.nav.right-actions>li>a,.nav.right-actions>li>button{transition:all .50s ease-in-out;}
.ProfilePage-cancelButton,.ProfilePage-saveButton{padding:8px 10px!important;}
.ProfileAvatarEditing-changeAvatarHelp{margin-top:-11%!important;}
.UserActions-editButton{padding-top:11px;margin-top:-2px;padding-bottom:11px;}

.DashboardProfileCard{background:#080808!important;}
.DashboardProfileCard-avatarImage{height:92px!important;width:92px!important;}
.DashboardProfileCard-avatarLink{margin:17px 0;display:block!important;background:transparent!important;}
.DashboardProfileCard-stats{padding:13px 0px!important;}
.DashboardProfileCard-avatarLink img,.DashboardProfileCard-avatarImage{margin:0 auto;display:block;margin-top:-16px;}
.DashboardProfileCard-userFields{position:static;text-align:Center;width:auto;margin-top:-11px;}
.DashboardProfileCard-content{margin-top:-25px;margin-left:-1px;width:292px;}
.DashboardProfileCard-statValue,.stats li a strong,.hide-text,.show-text,.DirectMessage-text .tweet-text,.ProfileNav-label{font-size:12px!important;}
.DashboardProfileCard-avatarContainer{width:auto!important;}
.hovercard.profile-card .ProfileCard-avatarImage,.ProfileCard-avatarImage{border-radius:150px;border:2px solid #818181;background-color:#585858;}
.DashboardProfileCard-avatarImage .Icon{margin:20px 0 0 24px!important;}
.ProfileAvatar{margin-left:33px;border-color:#717171;background:#717171!important;}
.ProfileAvatarEditing{border-color:#717171!important;margin-left:33px!important;}
.ProfileUserList .Avatar,.ProfileHeader .profile-picture,.profile-picture .avatar,.sent .avatar,.ProfileCard-avatarLink,.ProfileAvatarEditing-button,.ProfileAvatarEditing-overlay,.ProfileAvatarEditing-placeholder{border-color:#BBB!important}
.ProfileUserList-facepileItem:nth-child(9),.ProfileUserList-facepileItem:nth-child(10){display:none;}
.ProfileHeading-toggleItem:first-child{padding-left:35px!important;}

.ProfileListItem .u-floatRight{float:left!important;margin-right:20px;}
.ProfileHeading-content{margin-top:10px;margin-bottom:2px;}
.PromptbirdPrompt-arrow:not(.ie8),.PromptbirdPrompt--dashboardProfile{background-color:#222D33!important;border-color:#1B303C!important;border-radius:0px;}

/* Profile Preview */
.ProfilePopupContainer--bellbird .stats th{color:#8c9ca9!important;}
.ProfilePopupContainer--bellbird .go-to-profile .view-profile{width:563px!important;}
.ProfilePopupContainer--bellbird .stats th:first-child{padding-left:4.7%!important;}
.ProfilePopupContainer--bellbird .stats a strong:hover,.stream-item-activity-me .stream-item-activity-header .pretty-link b,.ProfileNav-item.is-active:hover .ProfileNav-label,.ProfileNav-item.is-active .ProfileNav-label{color:inherit!important;}
.ProfileNav-item--userActions{background-color:transparent!important;margin-right:10px;}
.ProfileNav-stat{padding-right:11px!important;padding-left:10px!important;}

/* DM */

.Caret--right.Caret--stroked:after{right:-1px;}

.DMAvatar{overflow:visible;}
.DMNotice{background:#1D5F8F!important;}

.DMConversation-composer{box-shadow:inset 0 1px 0 rgba(150,150,150,.1)!important;}

.dm-thread:hover,li .DMInboxItem:hover{background-color:#202020!important;background-image:none;}
.dm-thread.unread,.is-unread{background-color:#272727!important;}
.sent .dm-caret-inner,.sent .dm-caret-outer{border-left:4px solid #9e9e9e!important;border-right:0;}
.DirectMessage--sent .DirectMessage-message{background-color:#224B6A!important;border-color:#1E3648!important;}
.DirectMessage--sent .Caret:before,.DirectMessage .Caret:after{border-color:#224B6A;}
.DirectMessage--received .DirectMessage-message{background-color:#313D47!important;border-color:#40454A!important;}
.DirectMessage--received .Caret:before,.TwitterCard-container,.TwitterCard .CommerceCard-details,.TwitterCard .CommerceCard,.TwitterCard .Poll2ChoiceTextOnly-footer,.TwitterCard .PollXChoiceTextOnly-footer{border-color:#313D47;}
.dm-caret-inner{top:0;border-top:6px solid transparent;border-bottom:6px solid transparent;border-right:none;left:0!important;}
.dm-dialog .twttr-dialog-header,.twttr-dialog-footer{border-bottom:0px solid #050307!important;}
.dm-tweetbox{border-top:1px solid #050307!important;box-shadow:0px 0px 0px rgba(255,255,255,0.5) inset!important;}
.dm-participant-container .typeahead{margin-top:10px!important;}
.btn:focus,.btn.focus,.Button:focus{box-shadow:0 0 0 1px #fff,0 0 0 3px rgba(8,138,104,0.5)!important;font-style:italic;}
.DMActivity-back .Icon--logo{font-size:23px;}
.DMActivity-back .Icon--caretLeft{margin-right:12px;font-size:20px;vertical-align:1.4px;}
.DMTokenizedMultiselectSuggestion.is-highlighted{background-color:#747474!important;}
.DMComposer .thumbnail-container .previews .preview{margin:unset!important;}
.DMComposer .thumbnail-container{padding:13px 13px 13px 13px!important;position:static!important;}
.DMConfirmation{background-color:rgb(30,80,82);}
.TokenizedMultiselect-inputContainer{border:1px solid #474747;}
.DMTypeaheadSuggestionsContainer-suggestion.is-highlighted{background-color:#4C4C4C;}
.DMTypeaheadHeader{background-color:#525252;color:#BDBDBD;}
.dm-error{background-color:#69251D!important;}
.QuoteTweet--dmAttachment.is-sent{border:1px dotted rgb(51,43,43)!important;}
.QuoteTweet--dmAttachment.is-sent:hover{border:1px solid #3b94d9!important;}

/* Tweet/tweet box */

.modal .modal-tweet .tweet:hover{background-color:inherit!important;}

.favorited:after,.retweeted:after,.favorited.retweeted:after,.is-retweeted.is-favorited .ProfileTweet-header:before{top:0!important;}
.ThreadedConversation-showMore .show-more-link{height:53px!important;line-height:55px!important;}
.ThreadedConversation{margin:0!important;}
.tweet-counter.warn,.user_description_count.warn{color:#DD0000;}
.tweet-counter{text-shadow:0 1px 1px rgba(131,131,131,0.64);}
.timeline-tweet-box .tweet-form .thumbnail-container,.tweet-form .thumbnail-container,.PollingCardComposer{box-shadow:inset 0 1px 0 #2c2c2c;}
.QuoteTweet-text .pretty-link s,.QuoteTweet-text .pretty-link b,.DashboardProfileCard-userFields{color:#ddd!important;}
.QuoteTweet,.QuoteTweet--unavailable{background:rgb(45,45,45)!important;border:solid 1px #474747;}
.js-view-details,.js-hide-details{font-size:13px;text-transform:uppercase;}

.stream-container .tweet .details{margin-top:7px!important;}
.tweet-form .thumbnail-container .previews .preview-overlay{background:#CB0909!important;}
.rich-editor em{background:#894242!important;}
.tweet-form.has-thumbnail .thumbnail-container .preview{border:1px solid #273132!important;border-radius:0px;}

.tweet .stats{border-bottom:1px solid #2C2C2C!important;border-top:1px solid #2C2C2C!important;}
.Icon--follower.Icon--large,.Icon--colorFollower{color:#1F669B!important;opacity:.9;}
.js-disableTweetAction .Icon{opacity:.3;}
.stream-item .stats .avatar-row a:first-child,.permalink .stats .avatar-row a:first-child{border-left:1px solid #2C2C2C!important;}
/*.TwitterPhoto-media,.Gallery-content{border:0px solid #060606!important;}*/

.Gallery-content.video{min-height:0!important;}
.conversations-enabled .inline-reply-caret{border-color:transparent transparent #2f2f2f!important;}

/* Dropdown */
.me.dropdown .dropdown-menu{margin-top:12px!important;}
.me.dropdown{right:-28px;position:absolute!important;z-index:50;}
.nav.right-actions *{color:#DADADA;}
.dropdown-menu li:hover .js-nav s,.dropdown-menu li:hover .js-nav b{color:#969696!important;}
.dropdown-menu ul li{border-top:none!important;}
.dropdown-menu ul li button,.dropdown-menu ul li .fullname,.dropdown-menu ul li a{color:#909090!important;}
.non-selectable-item{color:#898989!important;}
.dropdown-menu,.popover{border:1px solid #525252!important;margin-top:7px!important;background-color:#272727!important;border:none!important;border-radius:0!important}
.dropdown-caret .caret-inner{border-bottom-color:#272727!important;}
.dropdown-divider{border-bottom:1px solid #313131!important;}

/* Lists */
.list-author{top:10px!important;left:15px!important;right:initial!important;}
.is-unsubscribed{margin-left:27%!important;}
.follow-card-footer .is-subscribed{margin-left:18%!important;}
.js-list-details .stats li:first-child{margin-left:16%;margin-right:8%;}
.list-membership-content div{text-align:center!important;margin-top:25px;}
.add-to-list-prompt.has-lists{margin-top:0!important;text-align:initial!important;}
[data-item-type="list"]{margin-top:1px!important;border:1px solid #464646;background:#171616;}

/* Follow you back */
.follow-status,.FollowStatus{font-size:10px!important;top:-1px!important;padding:3px 6px!important;margin-left:5px!important;color:#ccc!important;background-color:#4D4D4D!important;border-radius:20px;}

.follow-card .follow-bar{background-color:inherit!important;background-repeat:inherit!important;background-image:none!important;border-top:none!important;border-radius:0!important;margin-top:-10px;}

.follow-card .flex-module-header h1,.list-follow-card h1{margin-right:-12px!important;margin-bottom:15px!important;}

/* Grey bar on tweet expanded*/
.home-tweet-box{background-color:rgb(34,34,34)!important;}
.inline-reply-tweetbox,.permalink .inline-reply-tweetbox,.content-main .expansion-container .view-more-container{background-color:#202020!important;}
.conversations-enabled .inline-reply-caret .caret-inner{border-bottom-color:#202020!important;}
.trend-location-picker-item.selected{color:#C4C4C4!important;background-color:#3E3E3E!important;border-color:#7C7C7C!important;}

/* Username */
.profile-modal .badges .Icon,.js-action-profile-name .Icon,.typeahead-items .Icon{color:#099;}
.Icon--verified,.Icon--protected,.Icon--translator,.profile-modal .badges .Icon.Icon--protected{color:inherit;}
.ProfileHeaderCard-badges .Icon{margin-left:6px!important;}
.fullname{font-size:15px!important;font-weight:normal!important;}
.WhoToFollow-users .fullname{font-size:14px!important;}

.ProfileTweet-fullname,.modal .modal-tweet .stream-item-header .account-group,.DirectMessage-text .tweet-text,a.tweet-screen-name,.message .message-text,.ep-EngagementsTableName,.ep-MetricAnimation,.ep-TweetPerformance.ep-Section .ep-NoEngagements,.QuoteTweet-fullname,.MomentCapsuleItemTweet-fullname,.TwitterCard .PromotionCardFull .promo-title{color:#ccc!important;}/*priority grey*/

.fullname,.stream-item-activity-line-me strong.fullname,.RelatedUsers-users .fullname,.WhoToFollow-users .fullname,.stream-item-activity-line-notification .fullname{color:#ccc;}/*grey*/

.ProfileTweet-screenname:before{content:"\00b7"!important;}
.ProfilePopupContainer--bellbird .profile-details .fullname{display:inline!important;position:relative;top:2.22px;}
.modal-body .screen-name:before,.typeahead-user-item-info .username:before{content:"Â·"!important;margin-right:9px;}
.stream-item-header .username:before,.account-group-inner .username:before{content:"\00b7"!important;margin-right:5px;}
.verifiedContainer{margin-right:5px!important;}

/* Toast notification and close */
#message-drawer{top:56px!important;left:66.6%;}.WebToast{right:50px;}
.message{width:252px!important;}
.message .dismiss{right:20px!important;font-size:23px!important;top:16px;}
.message,.WebToast{background:#181818!important;}
.alert-messages{top:125px!important;}
.WebToast-box--altColor{background-color:#1F1F1F!important;}

.WebToast-closeButton{left:-3px;font-size:20px;}.WebToast-closeButton:hover{color:#0C90ED;}
.geo-picker .close{top:9px!important;}
.close{background-position:-100px -511px;width:10px;height:10px;}
.recap-header .dismiss{font-size:18px!important;margin-right:10px!important;}

/* Inputs */
input:not(.submit),textarea,div[contenteditable="true"],select,.ProfileHeaderCardEditing-editableField{background-color:#363636!important;color:#BBB!important;border:1px solid #505050!important;}
.search-input{border:1px solid #353333!important;background-color:#111617!important;}

/* Color for RT, FAV and FOLLOW*/
.Icon--follower,.Icon--colorFollower{color:#32A7FF!important;}

/* Buttons */
.btn:focus{border-color:#C5C5C5;}
.photo-selector:hover .btn,.icon-btn:hover{background-image:linear-gradient(rgba(67,67,67,1),rgba(60,60,60,1));}
.icon-btn.enabled{background:rgba(255,255,255,0.75)!important;}
.btn-primary[disabled="disabled"]{border:1px solid #7F7F7F!important;background-color:#0F0F0F!important;background-image:initial!important;}
.btn-primary[disabled="disabled"]:hover,.btn.disabled:hover,.disabled .icon-btn.enabled,.photo-selector.disabled,.user-select-container .cannot-select a,.user-select-container .selected.cannot-select a,.photo-selector.disabled{cursor:not-allowed!important;}
