css:
@-moz-document domain("phoenix.corvidae.org") {
  
  /* Vae's Changes */
    #ipsLayout_header header {
        background-color: #151515;
    }
    #elSearchNavContainer,
    #elMobileNav {
        background: #3F3F3F;
    }

    .ipsType_sectionTitle {
        background: #232323;
    }
    .ipsItemStatus.ipsItemStatus_large {
        background: #232323;
    }
    .ipsButtonBar {
        background: #3F3F3F;
    }
    a {
        color: #3F3F3F;
    }
    a:hover {
        color: #696969;
    }
    .ipsApp .ipsButton_important {
        background: #232323;
    }
    #elHeaderSubLinks a,
    .ipsBreadcrumb > u {
        color: #232323;
    }
    .ipsApp .ipsButton_alternate {
        background: #232323;
    }
    .ipsCommentUnreadSeperator {
        border-color: #8D8D8D;
    }
  
    /* Sar's Changes */
    
    /* SITE MODIFICATIONS - Modifications that don't change colours, but elements within the site.*/
    /* ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
   
    /* Circled avatars! Comment out if it flares up your autism. */
     .ipsUserPhoto_small img, img.ipsUserPhoto_small, .ipsUserPhoto_small, 
    .ipsUserPhoto_large img, img.ipsUserPhoto_large, .ipsUserPhoto_large, 
    .ipsUserPhoto, .ipsUserPhoto_tiny img, img.ipsUserPhoto_tiny,.ipsUserPhoto_tiny::after {
    border-radius: 50%;
    }

    /* Change Phoenix's Logo - Sorry that this is kind of a dirty way. */ 
    #elLogo img {
        display: block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
      
        /* The image you want to use*/
        background: url(http://i.imgur.com/ghpbKZI.png) no-repeat;

        /* Width of new image */        
        width: 429px;
      
        /* Height of new image */
        height: 54px;
      
        /* Always padding-left equal to width of new image */
        padding-left: 429px;
        padding-top: 15px;
    }
  
    /* Use me to fix any allignment issues with the Site Logo*/
    .ipsLayout_container {
        padding-top: 5px;
    }
    
    /* ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */

    
    /* COLOUR SCHEME*/
    /* ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- */
    
   /* Main body, most of the UI inherits this one way or another */
    body,  .ipsNavBar_primary {
        
        /* Change the sites fonts here! */
        font-family: "Open Sans", Noto Sans, Open Sans, Helvetica, Arial, sans-serif;
        color: #F2F2F2;
        
        /* Sites overall BG */
        background-color: #151515;
    }
    
    /* Titlebar for vertical widgets */
    .ipsWidget.ipsWidget_vertical .ipsWidget_title {
        background: #252525;
    }
    
    /* Primary Buttons */
    .ipsApp .ipsButton_primary {
	background: #252525;
}
    
    /* Forms/Menus - like the sign in one */
    .ipsPad, .ipsApp ul.ipsPad, .ipsApp ol.ipsPad {
    background: #151515;
}
    
    .ipsTabs {
    background: #101010;
    }
    
    .ipsMenu {
    background: #151515;
    box-shadow: 0px 7px 20px rgba(0, 0, 0, 0.2);
    border: 1px solid #303030;
    border-radius: 3px;
    z-index: 10000;
    position: absolute;
}
    
    .ipsMenu_title {
    background: #252525;
}
    
    .ipsMenu_item {
    color: #FFFFFF;
}
    
    .ipsMenu_item > a, .ipsMenu_item > span {
    color: #AAA;
}
    
    .ipsMenu:not(.ipsMenu_noStem)::after, .ipsMenu:not(.ipsMenu_noStem)::before {
    border-style: none
}
    
    
/* ------------*/
    .ipsType_large:not(.ipsType_richText) {
        font-size: 16px;
        color: #9C9C9C;
    }
    .ipsType_pageTitle {
        color: #FFF;
    }
    .ipsType_richText {
        word-wrap: break-word;
        line-height: 1.5;
    }
    
    .ipsType_sectionHead {
       font-size: 20px;
       color: #CCC;
       line-height: 24px;
       font-weight: 400;
       display: inline-block;
       margin: 0px;
    }
    .ipsWidget.ipsWidget_horizontal .ipsWidget_title, .ipsNavBar_secondary {
        background: #252525 none repeat scroll 0% 0%;
      
    }
    
    /* Navbar changes */
    /* ------------------------------------------------------------------------------------------------------------- */
    
    .ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #252525;
    color: #FFF;
    }
    
    .ipsNavBar_secondary > li.ipsNavBar_active a {
    color: #FFF;
    }
    
    ipsNavBar_active, .ipsNavBar_secondary > li > a {
    color: #CCC;
    }
    
    /* ------------------------------------------------------------------------------------------------------------- */
    
    
    .ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {
        background-color: #101010;
    }
    ipsLayout_contentWrapper {
        background-color: #151515;
    }
    #ipsLayout_mainArea,
    #ipsLayout_sidebar {
        background: #151515 none repeat scroll 0% 0%;
    }
    
    /* Inbetween background */
    .ipsAreaBackground_light {
    background: #151515 none repeat scroll 0% 0%;
    }
    
    .ipsBreadcrumb.ipsBreadcrumb_top {
    background: #151515 none repeat scroll 0% 0%;
    }
    
    #ipsLayout_contentWrapper {
    background-color: #151515;
    }
    
    .ipsAreaBackground_reset {
    background: #101010 none repeat scroll 0% 0%;
    }
    
    .ipsDataList.ipsDataList_zebra .ipsDataItem:nth-child(2n):not(.ipsDataItem_selected):not(.ipsModerated):not(.ipsDataItem_new):not(.ipsDataItem_success):not(.ipsDataItem_warning):not(.ipsDataItem_error):not(.ipsDataItem_info):not(.ipsDataItem_status) {
    background: #050505 none repeat scroll 0% 0%;
    }
    
    /* Main text colour*/
    .ipsType_richText {
        color: #BBB;
       margin-bottom: 0px;
    }
    
    /* Post Quotes
    /* ------------------------------------------------------------------------------------------------------------- */
    
    /* Quote header */
    .ipsQuote_citation {
       background: #3F3F3F none repeat scroll 0% 0%;
       margin: 0px -15px;
       padding: 5px 15px;
       color: #222;
       font-weight: bold;
       font-size: 13px;
       display: block;
    }
    
    /* Quote reference (Name of the quote poster) */
    .ipsQuote .ipsQuote_citation a {
    color: #FFF;
    text-decoration: none;
    }
  
    /* Quote box */
    .ipsQuote {
    margin: 0px;
    padding: 0px 15px;
    border-width: 2px;
    border-color: #666;
    border-style: solid;
    position: relative;
    background: #202020 none repeat scroll 0% 0%;
    clear: both;
    }
    
    /* ------------------------------------------------------------------------------------------------------------- */
    
      /* Like button font fixes */
    .ipsLike_contents {
        color : #555555;
        padding-right : 2px;
    }
    
    
    /* Quick-Reply Box */
    /* ------------------------------------------------------------------------------------------------------------- */
    /*reply background*/
    .ipsAreaBackground {
    background: #252525 none repeat scroll 0% 0%;
    }
    
    /* Text box background  */
    .cke_editable {
    cursor: text;
    background: #252525 none repeat scroll 0% 0%;
    }
    
    .ipsComposeArea_editor {
    background: #454545 none repeat scroll 0% 0%;
    }
    
    .ipsApp label:not(.ipsField_unlimited), .ipsApp input, .ipsApp button, .ipsApp select, .ipsApp textarea {
    color : #CCC;
    }
    
    

}
