css:
@-moz-document domain("instructure.com") {
/*Background*/

html, body, head, #main, #content, #application, #assignments, #not_right_side, #wrapper, .with-right-side #main, #content-wrapper, .padless-content #content, #content.padless, #breadcrumbs ul, .ig-row.ig-published, .module-sequence-footer-content, #content.container-fluid, .header-bar, .item-group-container .item-group-condensed, .ig-row.ig-published.student-view, .ig-title.module-item-title.ellipsis, #context_module_content, .menu-item-drop-column, .menu-item.hover .menu-item-title, .student_assignment.editable, #grades_summary th.title, #grades_summary td.due, #grades_summary td.assignment_score, #grades_summary td.possible, #grades_summary td.details, #add_comment_form, .comments, .comment_list, .ic-notification__content, .conversations .panel, .message-list .messages>li, .dropdown-menu.inner, .dropdown-menu.open, .tool_content_wrapper, .discussion-list, .discussion, .discussion-collections, .topic .entry-content, .toolbarView .headerBar, .topic>.discussion-entry-reply-area, .mce-tinymce .mce-container-body, #editor_tabs .ui-tabs-panel, #editor_tabs #pages_accordion .ui-accordion-content, .ui-accordion .ui-accordion-header a, #group_categories_tabs>.roster-tab, .emptyMessage, .discussionTopicIndexList .discussion-topic:nth-child(odd), .module-sequence-footer .module-sequence-footer-content, #questions.assessing, .question, .question .header, .quiz-submission, .question_editing, #quiz_show .alert, #right-side table.summary td, #right-side table.summary tbody th, .item-group-container, .alert-info, .discussion-entries, .discussion_entry, .discussion-entry-reply-area.reply-box-container.hide-if-collapsed.hide-if-replying, .discussion-entry-reply-area.hide-if-collapsed.show-if-replying, a.discussion-reply-action.discussion-reply-box, .student_assignment.hard_coded.group_total, #questions.assessment_results .question .header, .ui-tabs .ui-tabs-panel, .ui-tabs .ui-tabs-nav li.ui-tabs-active a, #grades_summary tr.group_total td, #grades_summary tr.group_total th[scope=row], .question_holder>.question .answer_comment, #identity, .discussionTopicIndexList .discussion-topic, #instructure_ajax_error_box, .table-striped tbody tr:nth-child(odd) td, .table-striped tbody tr:nth-child(odd) th, .table-striped tbody tr:nth-child(even) td, .table-striped tbody tr:nth-child(even) th, #right-side, #context-list-holder, #undated-events, #minical .fc-toolbar, #minical .fc-view-container, #calendar-app .fc-view-container, .fc-row .fc-bg, #calendar-app .fc-event, #calendar-drag-and-drop-container .fc-event, .file-upload-submission, .submission-details-comments .comments, .ui-dialog .ui-dialog-titlebar.ui-widget-header, .ui-widget-content, .compose-message-dialog .ui-dialog-buttonpane, #compose-new-message .conversation_body, .ac-token, .ac-input-box, .ac-result-container {
    background-color: #040404;
}

.stream_header:hover, .stream-details tr:hover, .ig-title.module-item-title.ellipsis:hover, a.modules.active, .message-list .messages>li:hover, .conversations .panel .btn, .message-list .messages>li.active, .menu-item .menu-item-drop li:hover, .list-view a.active, .list-view>nav>ul>li>a:hover, .list-view a.active:hover, .discussion:hover, .discussion.no-content:hover, #editor_tabs .page_list li a:hover, #grades_summary tr.editable:hover>*, #right-side .element_toggler:hover, #right-side .element_toggler:focus, #context-list .context_list_context:hover, #calendar-app .fc-state-highlight, #calendar-drag-and-drop-container .fc-state-highligh, #undated-events .event, .ColorPicker__Container, .alert-danger, .alert-error, .ac-token:hover, .ac-result.selected, .ac-result:hover, .notification_account_content, .ef-item-row:hover, .ef-item-row.ef-item-selected {
	background-color: #202020;
}

#grades_summary thead th, table.summary thead th, table.summary caption, #grades_summary tr>*, .table>thead {
	background-color: #202020;
}

.ui-tabs .ui-tabs-nav li:hover a {
	background-color: #202020;
	color: #0081bd;
}

.quiz_comment {
	background-image: none;
}

.item-group-condensed .ig-row {
	background: #040404;
}

/* #submit_quiz_form .button-container .next-question, #submit_quiz_form .button-container .previous-question, #submit_quiz_button */

/*Text Color*/

*, h2, #compose-new-message .conversation_body, .ef-plain-link, .ef-plain-link:hover {
	color: gainsboro;
}

#topbar #identity a, .user_name a, .logout a, #identity-help-container a {
	color: gainsboro!important;
}

/*dark preview text*/
.discussionTopicIndexList .discussion-summary {
	color: #B5B5B5;
}

.ig-header .name {
	text-shadow: none;
	text-decoration: underline;
}

/*Buttons*/

.btn, .Button, .ui-button {
	background: #202020;
	color: gainsboro;
}

.btn:hover, .Button:hover, .ui-button:hover, .ui-button.ui-state-hover {
	background: #444444;
	color: gainsboro;
}

#course_url, #assignment_order, #right-side .button-sidebar-wide, #group_categories_tabs div select {
	background-color: #202020;
	color: gainsboro;
}
}

@-moz-document url-prefix("https://itweb.mgccc.edu") {
body, span, html, h2, h3, p, a, img, ul, li, fieldset, form, label, legend, #cas #login, #header, #cas form {
	background-color: #040404;
}

#header {
	background: transparent;
}

/*body, html, div, span, h1, h2, h3, p, a, img, ul, li, fieldset, form, label, legend, .fl-screenNavigator-view, #header*/
}

@-moz-document url-prefix("http://www.mgccc.edu/") {
body, html, .container-wrapper, .all-container-wrapper.boxed-layout, .container, div.slider-bottom-gimmick, div.footer-wrapper .container {
	background-color: #040404;
}

.gce-list-event.gce-tooltip-event {
	background-color: #2B2B2B;
}

.blog-thumbnail-content, .gce-list {
	color: gainsboro;
}
}
