background-color: #232323 !important;
}

li,
ul {
background-color: inherit !important;
}

li a:not([class*="icon"]):not([id*="icon"]):not([onclick]),
dt a:not([class*="icon"]):not([id*="icon"]):not([onclick]) {
background-image: none !important;
